  body {
    background-color: #f9f9f9;
    font-family: Roboto, "Helvetica Neue", Helvetica, sans-serif;
  }

  /* Material Radio and Checkbox */
  input[type="checkbox"],
  input[type="radio"] {
    border: 0;
    clip: rect(0 0 0 0);
    height: 1px;
    margin: -1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    width: 1px;
  }

  input[type="checkbox"]:active+label:before,
  input[type="radio"]:active+label:before {
    transition-duration: 0s;
  }

  input[type="checkbox"]+label,
  input[type="radio"]+label {
    position: relative;
    padding: 5px 5px 5px 2em;
    vertical-align: middle;
    user-select: none;
    cursor: pointer;
  }

  input[type="checkbox"]+label:before,
  input[type="radio"]+label:before {
    box-sizing: content-box;
    content: '';
    color: #4285f4;
    position: absolute;
    top: 50%;
    left: 0;
    width: 14px;
    height: 14px;
    margin-top: -9px;
    border: 2px solid rgba(0, 0, 0, .54);
    text-align: center;
    transition: all 0.4s ease;
  }

  input[type="checkbox"]+label:after,
  input[type="radio"]+label:after {
    box-sizing: content-box;
    content: '';
    background-color: #4285f4;
    position: absolute;
    top: 50%;
    left: 4px;
    width: 10px;
    height: 10px;
    margin-top: -5px;
    transform: scale(0);
    transform-origin: 50%;
    transition: transform 200ms ease-out;
  }

  input[type="checkbox"]:disabled+label:before,
  input[type="radio"]:disabled+label:before {
    border-color: #cccccc;
  }

  input[type="checkbox"]:disabled:focus+label:before,
  input[type="radio"]:disabled:focus+label:before,
  input[type="checkbox"]:disabled:hover+label:before,
  input[type="radio"]:disabled:hover+label:before {
    background-color: inherit;
  }

  input[type="checkbox"]:disabled:checked+label:before,
  input[type="radio"]:disabled:checked+label:before {
    background-color: #cccccc;
  }

  input[type="checkbox"]+label:after {
    background-color: transparent;
    top: 50%;
    left: 4px;
    width: 8px;
    height: 3px;
    margin-top: -4px;
    border-style: solid;
    border-color: #ffffff;
    border-width: 0 0 3px 3px;
    border-image: none;
    transform: rotate(-45deg) scale(0);
  }

  input[type="checkbox"]:checked+label:after {
    content: '';
    transform: rotate(-45deg) scale(1);
    transition: transform 200ms ease-out;
  }

  input[type="radio"]:checked+label:before {
    animation: borderscale 300ms ease-in;
    /*     background-color: white; */
    border: 2px solid #4285f4;
  }

  input[type="radio"]:checked+label:after {
    transform: scale(1);
  }

  input[type="radio"]+label:before,
  input[type="radio"]+label:after {
    border-radius: 50%;
  }

  input[type="checkbox"]:checked+label:before {
    animation: borderscale 200ms ease-in;
    background: #4285f4;
    border: 2px solid #4285f4;
  }

  input[type="checkbox"]:checked+label:after {
    transform: rotate(-45deg) scale(1);
  }

