css:
@-moz-document domain("members.luscious.net") {


html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
	 cursor: url(http://cur.cursors-4u.net/cursors/cur-5/cur425.cur), progress !important;
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: #3a0a0a00;
}
a, a:link, a:visited {
    color: #ff001d;
    text-decoration: none;
}
#sticky.user_menu {
    text-transform: uppercase;
    font-weight: bold;
    background: #3a0a0a;
    border-bottom: 3px solid #3a0a0a;
    float: left;
    height: 43px;
}
.menu li.page_controls a:link, .menu li.page_controls a:visited, .menu li.page_controls a:hover {
    color: #e20606;
}
.btn {
    border-radius: 2px;
    background: #000000;
    padding: 10px 15px 10px 15px;
}
.menu li.page_controls {
    border-radius: 2px;
    background: #292929;
    padding: 3px 30px 3px 30px;
    float: left;
}
.menu li a:link, .menu li a:visited {
    color: #d80f0f;
}
.information_block li {
    display: inline-block;
    vertical-align: top;
    padding: 10px;
    background: #3a0a0a;
    margin-top: 10px;
}

/*
 === account background===
*/
.ui-widget-content {
    background: url(http://l.rgbimg.com/cache1rl6df/users/b/ba/ba1969/600/neYkcMq.jpg);
}

.about_me.markdown {
    margin: 10px 0;
    line-height: 24px;
    background: #25252500;
    padding: 1px 5px 18px 15px;
    overflow: hidden;
}
#user_profile {
    background: #3a0a0a;
    padding: 35px;
    border: 2px solid #800f0f;
    margin: 0 auto 19px auto;
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
}
/* ==Picture background== 

*/

.header-img {
    background-image: url(https://ak0.picdn.net/shutterstock/videos/8168029/thumb/12.jpg);
    background-attachment: fixed;
    background-size: cover;
    height: 800px;
    background-position: top;
    margin: 0 auto;
    position: relative;
}

.menu li .menu_dropdown li a:link, .menu li .menu_dropdown li a:visited, .menu li .menu_dropdown li a:hover {
    border: 0;
    color: white;
}
.menu li .menu_dropdown {
    display: none;
    position: absolute;
    background: #3a0a0a;
    z-index: 10001;
    width: 150px;
    overflow: hidden;
}
#sticky.user_menu li .menu_dropdown li a:link, #sticky.user_menu li .menu_dropdown li a:visited, #sticky.user_menu li .menu_dropdown li a:hover {
    border: 0;
    color: white;
}
.search_filter .menu_dropdown.community_dropdown .highlighted, #sticky.user_menu li .menu_dropdown.community_dropdown .highlighted {
    background: #3a0a0a;
}
.btn {
    border-radius: 2px;
    background: #3a0a0a;
    padding: 10px 15px 10px 15px;
}
.grid figcaption {
    position: absolute;
    top: 0;
    left: 0;
    min-height: 257px;
    height: auto;
    width: 353px;
    background: #3a0a0a;
    z-index: 101;
    display: none;
    font-size: 12px;
    padding-top: 10px;
}
.facet_list li a.positive {
    background: #ab383800;
    padding: 0 5px;
    text-align: center;
    display: inline-block;
}
.icon-bell.unread {
    color: #ff8401 !important;
}

.community_content_wrapper {
    width: 93%;
    min-height: 600px;
    max-width: 1400px;
    margin: 0 auto;
    background: #3a0a0a;
    padding: 0px 15px 25px 15px;
    border: 1px solid #bb0e0d;
    position: relative;
    top: -400px;
}
.notifications li.noti.read {
    background: #3a0a0a;
    color: #3a0a0a;
}
#sticky.user_menu li:hover {
    background: #29292903;
}
.video_cover a {

    display: inline;
}
#masthead .cm_info {
    position: absolute;
    top: 0;
    right: 48px;
    padding: 30px;
    background: rgb(58, 10, 10);
}
.l-btn:hover {
    background: #3a0a0a;
}
.l-btn {
    text-align: center;
    cursor: pointer;
    color: #c1c1c1;
    outline: 0;
    padding: 10px 25px 10px 25px;
    border: 1px solid #833333;
    background: #3a0a0a;
}
.l-vmenu .l-vmenu-item {
    background: #3a0a0a;
    border-bottom: 1px solid #d61c1c;
    cursor: pointer;
    padding: 5px 8px 5px 8px;
    transition: 400ms;
    display: block;
}
.notifications li.noti:hover {
    background: #c70c0c;
    color: #000;
}

.post-meta {
    background: #3a0a0a00;
    border-bottom-left-radius: 7px;
    border-bottom-right-radius: 7px;
    text-align: center;
    display: inline;
    justify-content: center;
}
#blogs.main .block_wrapper:nth-child(odd) .blog {
    background-color: #3a0a0a;
    color: #dedede;
}
.block {
    background: #560d0d;
    padding: 20px;
    margin: 0 auto;
}
#sticky.user_menu li .menu_dropdown {
    display: none;
    position: absolute;
    background: #3a0a0a;
    z-index: 10001;
    width: 150px;
    overflow: hidden;
}

.item_cover {
    height: 288px;
    overflow: hidden;
    box-shadow: 1px 3px 5px #ff0000;
    background: #3a0a0a;
    border-radius: 10px;
}
#view_mode li a {
    padding: 12px;
    display: block;
    border-top: 4px solid #dc1111;
    border-top-right-radius: 2px;
    border-top-left-radius: 2px;
}
#view_mode li a.activity {
    border-top: 4px solid #dc1111;
}
.ui-tabs ul.ui-tabs-nav li.ui-state-active a:visited {
    color: #ef0a0a;
}
.post-meta li a:link, .post-meta li a:visited {
    color: #ff1407;
}
.post-meta li a:hover {
    text-shadow: 2px 2px 5px #f44248;
}
#profile_content_wrapper {
    background: #3a0a0a;
    padding: 10px;
    margin: 60px auto;
    border-top: 3px solid #3a0a0a;
    margin-top: 80px;
    margin-left: -6px;
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
}
.profile_stats table td {
    padding: 5px 7px;
    background: #3333337a;
    vertical-align: middle;
    width: 180px;
}
#user_meta .interests .body.markdown, .about_me.markdown {
    margin: 10px 0;
    line-height: 24px;
    background: #25252500;
    padding: 1px 5px 18px 15px;
    overflow: hidden;
}
.l-bgcolor--secondary-color {
    background-color: #3a0a0a;
}
.block {
    background: #29292900;
    padding: 10px;
    margin: 0 auto;
}
.moderator_info li.mod_link {
    background: #3a0a0a;
}
.moderator_info li.mod_link:hover {
    background: #b11414;
}
a:hover {
    text-shadow:2px 2px 5px red;
    color: #ff97a3;
}
.video-js .vjs-progress-holder .vjs-play-progress, .video-js .vjs-progress-holder .vjs-load-progress, .video-js .vjs-progress-holder .vjs-load-progress div {
    position: absolute;
    display: block;
    height: .3em;
    margin: 0;
    padding: 0;
    width: 0;
    left: 0;
    top: 0;
    color: darkred;
}
.video-js .vjs-play-progress {
    background-color: #ef0b00;
}
.video-js .vjs-fullscreen-control {
    width: 3.8em;
    cursor: pointer;
  text-shadow: 1px 5px 5px red;
    /* flex: none; */
}
	


li.logo_icon a {
    width: 100px;
    height: 40px;
    background: #6a0d2600 url(https://cdn.discordapp.com/attachments/368814419519733763/394254890953736192/RL_Logo1.gif) no-repeat right;
    border-right: 0;
}
footer {
    clear: both;
    background-color: #3a0a0a;
    color: white;
    margin-top: 30px;
}
	a.follow_btn {
    background: #0000009c;
    padding: 10px;
    margin-top: 6px;
    display: block;
    width: 60px;
    border-radius: 2px;
    font-weight: bold;
    border-bottom: 2px solid #08385700;
    font-size: 13px;
    text-shadow: 1px 1px 10px #ff0039;
}
a.follow_btn:link, a.follow_btn:visited {
    color: #ff1a00;
}

}

@-moz-document url-prefix("https://members.luscious.net/messages") {

 html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
    margin: 0;
