    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: #3a0a0a;
}

}

@-moz-document domain("members.luscious.net") {
/*scrollbar*/

::-webkit-scrollbar {
  width: 14px;
  height: 14px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #040404;
  border: 5px ridge #fa051e;
  border-radius: 7px;
}
::-webkit-scrollbar-thumb:hover {
  background: #4a4a4a;
}
::-webkit-scrollbar-thumb:active {
  background: #000000;
}
::-webkit-scrollbar-track {
  background: #800000;
  border: 0px groove #a7a7a7;
  border-radius: 50px;
}
::-webkit-scrollbar-track:hover {
  background: #800000;
}
::-webkit-scrollbar-track:active {
  background: #f81f2f;
}
::-webkit-scrollbar-corner {
  background: transparent;
}

}

