css:
@-moz-document domain("donjon.bin.sh") {
/***************
More or less global styles
***************/
table.body td {
    background-color: #272822;
    color: #F8F8F2;
}

table.body ul a {
    color: #F8F8F2;
}

table.body h3 a {
    color: #66D9EF;
    font-size: 16px;
}
/***************
End globals
***************/

/***************
Dropdowns
***************/
table.body select {
    background-color: #3B3A32;
    color: #F8F8F2;
    padding: 5px;
    font-size: 15px;
    font-family: 'Asul', sans-serif;
}

table.body select optgroup {
    color: #66D9EF;
}

table.body select option{
    color: #F8F8F2;
}
/***************
End dropdowns
***************/

/***************
Buttons and inputs
***************/
table.body input {
    border: solid 1px #75715E;
    font-size: 20px;
    color: #F8F8F2;
    background-color: #171714;
    padding-top: 5px;
    padding-right: 17px;
    padding-bottom: 5px;
    padding-left: 17px;
}

textarea {
    color: #F8F8F2;
    background-color: #171714;
}
/***************
End buttons and inputs
***************/

/***************
Table headers
***************/
table.body tbody tr th {
    background-color: #0d0d0d;
}

table.body td.header {
    background-color: #0d0d0d;
}
/***************
End table headers
***************/

/***************
Popovers
***************/
#pop_card div, div.popup div.card {
    background-color: #272822;
    color: #F8F8F2;
}

#pop_card h2 {
    background-color: #0d0d0d;
    color: #F8F8F2;
}
/***************
End popovers
***************/

/***************
  On hover stuff
***************/

  /*
    Buttons, navigation headers, sort icons for tables
  */
  table.body input:hover,.nav-section h2:hover, table.body span.sort_icon:hover {
      cursor: pointer;
      color: #FFE792;
      opacity: .95;
      transition: all .3s ease 0s;
  }
  
  /*
    Dropdowns
  */
  table.body select:hover {
      cursor: pointer;
  }
  
  /*
    Dropdown options
  */
  .nav-section .list li:hover {
      background: #272822;
  }
  
  /*
  Links
  */
  .list ul a:hover {
      color: #FFE792;
      background: #272822;
      transition: all .3s ease 0s;
  }
  
  table.body a:hover {
      color: #FFE792;
      transition: all .3s ease 0s;
  }
  
  /*
  Tables
  */
  td#content tr:hover td {
    background-color: #3B3A32;
  }
  
  td#content form tr:hover td {
      background-color: #272822;
  }

/***************
  End on hover stuff
***************/
}
