body, #mc_embed_signup
{
	background-image: none !important;
    background-color: #1C1C1C !important;
    color: #AAA !important;
}

.main-left-title, table.dataGrid th,
.main-left-inner > table#maintable1.dataGrid2 > tbody > tr:nth-child(1):hover,
.main-left-inner > #diff_graph.jqplot-target > canvas.jqplot-series-shadowCanvas, .footer,
.main-left-inner,
.main-left-inner table.dataGrid2 thead tr th
{
    background-color: #2C2C2C !important;
}

.main-left-title,
.dataGrid2 tbody tr.ssrow:hover,
#graph_earnings_results.jqplot-target > .jqplot-axis.jqplot-yaxis,
#graph_earnings_results.jqplot-target > .jqplot-axis.jqplot-xaxis,
#graph_results_lyra2v2.jqplot-target .jqplot-axis.jqplot-yaxis,
#graph_results_lyra2v2.jqplot-target .jqplot-axis.jqplot-xaxis,
#graph_results_lyra2v2.jqplot-target .jqplot-highlighter-tooltip,
#graph_results_lyra2v2.jqplot-target .jqplot-title,
html.no-js body.page div.content-out div.content-inner table .main-left-inner > ul
{
	color: #aaa !important;
}

.jqplot-series-shadowCanvas
{
    background-color: #3c3c3c4d !important;
}

.main-left-box .main-left-inner #graph_results_phi1612.jqplot-target canvas.jqplot-series-canvas
{
    background-color: #00000078 !important;
}
table
{
    color: #AAA !important;
}

canvas.jqplot-series-shadowCanvas:nth-child(8),
canvas.jqplot-series-canvas:nth-child(11)
{
	background-color: #0000 !important;
}

.ssrow:hover
{
    background-color: #33576c !important;
}

.main-left-box > .main-left-inner > ul > li > p.main-left-box,
.main-left-box > .main-left-inner > pre.main-left-box,
.main-left-inner > ul > li > .main-left-box
{
    background-color: #323023 !important;
}

form input.main-text-input,
form input.main-text-input,
form input.main-submit-button,
form#mc-embedded-subscribe-form.validate > #mc_embed_signup_scroll > input#mce-EMAIL.email,
#mc_embed_signup_scroll > .clear > input#mc-embedded-subscribe.button,
select#algo_select.filter,
select#chip_select.filter,
input.search
{
    background-color: #3C3C3C !important;
    color: #AAA !important;
}

.main-left-box,
.main-left-inner table.dataGrid2 tbody
{
	border: 1px solid #5C5C5C !important;
}

table.dataGrid2 td
{
	border-bottom: 1px solid #5C5C5C !important;
}

tr.ssrow
{
	border-top: 3px solid #5C5C5C !important;
}

.ssrow:hover
{
    background-color: #111 !important;
}


.main-left-inner form table.dataGrid2 > tbody > tr
{
    background-color: #2B2430 !important;
}

a, a:visited
{
    color: #4B88C2 !important;
}
#maintable1.dataGrid2.tablesorter.tablesorter-default tbody > tr
{
    background-color: #233343 !important;
}

#maintable1.dataGrid2.tablesorter.tablesorter-default > tbody > tr.ssrow,
#maintable1.dataGrid2.tablesorter.tablesorter-default > tbody > tr.ssrow:hover,
tr[style^="cursor"] > td
/*#maintable1.dataGrid2.tablesorter.tablesorter-default > tbody > tr.tablesorter-childRow,*/
{
    background-color: #1C1C1C !important;
}
}
