css:
@-moz-document domain('soul-anime.us') {
    body {
        background-color: #000 !important;
        background-image: url('https://pti-world.com/wp-content/uploads/2016/02/wanted-black-grey-gradients1.jpg')!important;
        background-position: center center !important;
        background-repeat: no-repeat !important;
        background-size: 100% 100% !important;
    }
    aside iframe {
        display: none !important;
    }
    #bg_head {
        background-color: rgba(0, 0, 0, 0.4) !important;
        background-image: none !important;
    }
    #bg_nav {
        background-color: rgba(0, 0, 0, 0.6) !important;
        background-image: none !important;
    }
    main {
        background-color: inherit !important;
        background-image: none !important;
    }
    #bg_content {
        background-color: rgba(6, 6, 6, 0.755) !important;
        background-image: none !important;
        color: white !important;
    }
    aside {
        background-color: rgba(23, 22, 22, 0.755) !important;
        background-image: none !important;
    }
    .title {
        background: linear-gradient(#010101, #101010, #111) !important;
    }
    .holder {
        background: linear-gradient(#000, #121212, #131313) !important;
        background-image: none !important;
        border: 1px solid black !important;
        color: white!important;
    }
    .holder li a {
        color: rgba(255, 255, 255, 0.7) !important;
    }
    .holder li:hover {
        color: #111 !important;
        background-color: rgba(255, 0, 0, 0.8) !important;
    }
    .ep {
        color: #fff !important;
    }
    #ep .anime a {
        color: rgba(255, 0, 0, .467) !important;
    }
    #wrap-googleplay,
    #wrap-salapers {
        display: none !important;
    }
    #genre li {
        background-color: #222 !important;
        background-image: none !important;
        background: none !important;
    }
    #genre li a {
        color: rgba(255, 0, 0, 0.4) !important;
    }
    .alpha,
    .goto {
        background: linear-gradient(#010101, #101010) !important;
        background-color: inherit !important;
        border: solid 1px #cccccc !important;
    }
    #alpha li,
    #alpha li a,
    .alpha a .goto:hover {
        background-color: red !important;
        color: black !important;
    }
    select,
    option,
    input {
        background: black !important;
        color: rgba(255, 0, 0, .6) !important;
        border: 1px solid red !important;
        margin: 2px !important;
    }
    header strong {
        color: red !important;
        text-transform: uppercase !important;
    }
    header .td div {
        display: none !important;
        background-color: transparent !important;
        border: solid 1px #111 !important;
    }
    header img {
        display: none !important;
    }
    main #schema {
        background: transparent !important;
        border: red!important; color:red!important
    }
    header {
        background-image: url('http://soul-anime.us/effects/site_name.gif') !important;
        background-size: 100% 100% !important;
        background-position: center center !important;
    }
}
