css:
@-moz-document domain(2sucres.org) {
/* Header + Navbar */
  .ms-navbar
  {
    border-bottom: 4px solid #B71C1C;
    background-color: #f95252;
  }

  .ms-header.ms-header-primary
  {
    background-color: #ff0f0f;;
  }

.ms-header .ms-title img,body > div.sb-site-container > nav > div > div.navbar-header > a
{
  content: url("http://image.noelshack.com/fichiers/2017/51/7/1514109812-2sucres.png");
}

  #navbar > ul > li > a
  {
    font-family: "Helvetica Neue",sans-serif;
    font-weight: bold;
    text-transform: uppercase;
  }

  #navbar > ul >  a
  {
    font-family: "Helvetica Neue",sans-serif;
    font-weight: bold;
  }





/* Background forum */
  body .sb-site-container
  {
       background-image: url(http://www.noelshack.com/2017-51-7-1514114620-p5-wallpaper.png);
    background-size: contain;
    background-repeat: no-repeat;
    }





/*Bloc forum */
  #forum-container
  {
    background-color: transparent;
  }

  .list-topics-container.forum-view.ng-scope, .topic-title
  {
    background-color: #F5F5F5;
    padding: 1.75rem!important;
    padding-bottom: 40px!important;
  }

  .list-topics > tbody > tr
  {
    background-color: #f9e3e3;
  }

  .topic-name .topic-name-truncate
  {
    font-weight: 500;
  }






/*Sidebar */
  .forum-aside-content
  {
    background-color: #ce0404;
    color: white;
  }

  .toolbar-msg
  {
    background-color: #ce0404!important;
    color: white;
  }

  .toolbar-msg .md-label
  {
    color: white;
  }


/*Créer topic*/

  #form-send-new-message textarea, #form-send-new-message input
  {
    background-color: #f2f2f2;
  }

  #form-send-new-message select
  {
    background-color: #f2f2f2;
  }






/*Affichage posts*/
  .col-lg-8.col-md-9.col-xs-12.no-pr.no-pl
  {
    background-color: #F5F5F5;
    padding: 1rem!important;
  }

  .mt-2
  {
    margin-bottom: 1rem;
    margin-top: 0px;
  }

  #message-container
  {
    color:  #f9e3e3;
  }

  .sucre-pagi > li.active > a
  {
    background-color: transparent;
    color: #484848;
    font-size: 1.5em;
    border-bottom: 2px solid #D91E18;
    padding: 2px 5px;
  }

  .sucre-pagi > li > a
  {
    font-size: 1.5em;
    color: #484848;
    padding: 2px 5px;
  }

  .md-button
  {
    margin: -6px 6px;
  }

  #top-pagi-navig.col-xs-12
  {
    padding-bottom: 10px;
  }

  .pr-1
  {
    padding-bottom: 15px;
  }

/*Boutons oranges en bleu*/
  .md-button.md-default-theme.md-hue-1.md-accent.md-raised, .md-button.md-hue-1.md-accent.md-raised, .md-button.md-default-theme.md-hue-1.md-accent.md-fab, .md-button.md-hue-1.md-accent.md-fab, .md-button.md-default-theme.md-hue-1.md-fab, .md-button.md-hue-1.md-fab, .md-button.md-default-theme.md-accent.md-raised, .md-button.md-accent.md-raised, .md-button.md-default-theme.md-accent.md-fab, .md-button.md-accent.md-fab, md-switch.md-default-theme.md-hue-1.md-checked .md-thumb, md-switch.md-hue-1.md-checked .md-thumb,md-switch.md-default-theme.md-checked .md-thumb, md-switch.md-checked .md-thumb
  {
    background-color: #e51d1d;
    color: #fff;
  }

  .md-button.md-default-theme.md-hue-1.md-accent.md-raised:not([disabled]):hover, .md-button.md-hue-1.md-accent.md-raised:not([disabled]):hover, .md-button.md-default-theme.md-hue-1.md-accent.md-fab:not([disabled]):hover, .md- button.md-hue-1.md-accent.md-fab:not([disabled]):hover, .md-button.md-default-theme.md-hue-1.md-fab:not([disabled]):hover, .md-button.md-hue-1.md-fab:not([disabled]):hover, .md-button.md-default-theme.md-accent.md-raised:not([disabled]):hover, .md-button.md-accent.md-raised:not([disabled]):hover, .md-button.md-default-theme.md-accent.md-fab:not([disabled]):hover, .md-button.md-accent.md-fab:not([disabled]):hover
  {
    background-color: #ba1616;
    color: #fff;
  }

  md-switch.md-default-theme.md-hue-1.md-checked .md-bar, md-switch.md-hue-1.md-checked .md-bar, md-switch.md-default-theme.md-checked .md-bar, md-switch.md-checked .md-bar
  {
    background-color: rgba(229, 45, 31, 0.5);
  }




/*Barre forum*/
  .list-topics > thead > tr
  {
    background-color: #e8e8e8 !important;
    color: #3F4447;
    text-transform: uppercase;
    padding: 5px!important;
    font-size: 0.8em;
  }

  .topic-title
  {
    font-weight: normal;
    color: #777;
  }

  .topic-title > span
  {
    color: #3097D1;
  }

  .layout-row>.flex, .md-toolbar-tools>.md-button:last-child
  {
    margin-top: 0px!important;
  }

  .flex-20, .layout-row>.flex-20
  {
    margin-top: 2px;
  }

  .no-mb
  {
    margin-bottom: 10px!important;
  }







/* Footer */
  .ms-footer
  {
    border-top: 4px solid #B71C1C;
    font-size: 0.8em;
    background: #212121;
    text-align: center;
  }
}
