css:
@-moz-document domain("youtube.com")
{
  ytd-masthead,
	.menu-container.ytd-multi-page-menu-renderer,
	.ytp-popup,
	.sbsb_a,
	.captions-text span
  {
    background-color: rgba(255, 255, 255, 0.7);
    -webkit-backdrop-filter: saturate(180%) blur(20px);
    backdrop-filter: saturate(180%) blur(20px);
  }

  #container.ytd-searchbox
  {
    background-color: transparent!important;
    border: 1px solid transparent!important;
    box-shadow: none!important;
  }

  *
  {
    outline: none!important;
  }

  #search-icon-legacy.ytd-searchbox
  {
    background-color: transparent!important;
    border: none!important;
    box-shadow: none!important;
  }

  .sbdd_b
  {
    background-color: transparent!important;
  }

  ytd-multi-page-menu-renderer
  {
    background-color: transparent!important;
    box-shadow: 0 2px 16px rgba(0, 0, 0, 0.2)!important;
  }

  .menu-container.ytd-multi-page-menu-renderer
  {
    margin-right: 0!important;
    padding-right: 8px!important;
    overflow: auto!important;
  }

  ytd-simple-menu-header-renderer,
	ytd-active-account-header-renderer
  {
    background-color: rgb(249, 146, 209)!important;
  }

  .ytp-swatch-background-color,
	#progress.ytd-thumbnail-overlay-resume-playback-renderer
  {
    background-color: rgb(249, 146, 209)!important;
  }

  ytd-account-settings
  {
    background-color: transparent!important;
  }

  #sections.ytd-multi-page-menu-renderer>*.ytd-multi-page-menu-renderer:not(:last-child)
  {
    border-bottom: 1px solid transparent!important;
  }

  #primary-links.ytd-c4-tabbed-header-renderer,
	#secondary-links.ytd-c4-tabbed-header-renderer
  {
    background-color: rgba(136, 136, 136, 0.7);
    -webkit-backdrop-filter: saturate(180%) blur(8px);
    backdrop-filter: saturate(180%) blur(8px);
  }

  .ytp-popup .ytp-menuitem-label
  {
    color: #333!important;
    text-shadow: none!important;
  }

  .ytp-settings-button.ytp-hd-quality-badge:after,
	.ytp-settings-button.ytp-4k-quality-badge:after,
	.ytp-settings-button.ytp-5k-quality-badge:after,
	.ytp-settings-button.ytp-8k-quality-badge:after
  {
    background-color: rgba(255, 67, 182, 0.7)!important;
    -webkit-backdrop-filter: saturate(180%) blur(20px);
    backdrop-filter: saturate(180%) blur(20px);
  }

  html[dark] ytd-masthead,
	html[dark] .ytp-popup,
	html[dark] .menu-container.ytd-multi-page-menu-renderer,
	html[dark] .sbsb_a
  {
    background-color: rgba(0, 0, 0, 0.7)!important;
  }

  html[dark] .sbdd_b
  {
    border: 1px solid black!important;
  }

  .sbsb_c
  {
    transition: background-color .3s ease;
  }

  html[dark] .sbsb_c,
	html[dark] .ytp-popup .ytp-menuitem-label
  {
    color: rgba(255, 255, 255, 0.5)!important;
    text-shadow: none;
  }

  html[dark] .sbsb_d
  {
    background-color: rgba(0, 0, 0, 0.2);
  }
}
