@-moz-document domain("seyler.eksisozluk.com") {
::-webkit-scrollbar,
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track-piece {
	background: #1C1C1C !important;    
}

::-webkit-scrollbar {
	width: 6px !important;
	height: 6px !important;
}

::-webkit-scrollbar-thumb {
	border-radius: 0px !important;
    box-shadow: inset 0 0 0px #5A5B5C;
	border: 0px solid #5A5B5C !important;
}

::-webkit-scrollbar-thumb:horizontal {
    background: linear-gradient(to right, #5A5B5C, #5A5B5C, #5A5B5C, #5A5B5C);
}
::-webkit-scrollbar-thumb:vertical {
    background: linear-gradient(#5A5B5C, #5A5B5C, #5A5B5C, #5A5B5C);
}

