css:
@-moz-document domain("3dnews.ru")
{
#wrapper {
    background: #6f6f6f;}

#full-view #right-sidebar {
    background: #6f6f6f;}

.white {
    background: #6f6f6f;}

#currencyTicker span s {
    color: #ccc;}

#full-view #left-sidebar {
    background: #6f6f6f;}

#top-articles {
    background: rgb(111, 111, 111);}

#search-form #search-text {
    background: #6f6f6f;}

body, html {
    background: #000;}

.sidebar-chunk .content-block {
    border: 1px solid #5f5d5d;}

._2lqg {
    background: #f6f7f9;
    border: 1px solid #6f6f6f;}

._2lqg>div {
    background-color: #6f6f6f;}

._h7n {
    background: #6f6f6f;}

.entry-body img, .entry-body object, .entry-body iframe {
    max-width: 100%;
    box-shadow: 0 0 13px 5px black;}

* {
    font-weight: normal;
    color: black;}

#content-column .toc .content-block-data .header .date {
    text-shadow: 0 0 1px black;}

* a:link {
    text-decoration: underline;
    color: #00178e;}

sidebar-chunk.menu-item.dark {
    background: #255096;}

.sidebar-chunk.menu-item.light {
    display: block;
    background: #6f6f6f;
    color: #ffffff;
    text-shadow: #000 0 1px 0;}

.content-block-header>.left {
    color: #000;}

.sidebar-chunk.menu-item.light {
    display: block;
    background: #6f6f6f;
    color: #000000;
    text-shadow: #435b98 0 1px 0;}



a.sidebar-chunk.menu-item.light:hover, a.sidebar-chunk.menu-item.light.current {
    background: url(https://3dnews.ru/assets/images/single-dot.png) 9px center no-repeat #585858 !important;
    text-decoration: none;}

#feed-to-top {
    background: #6f6f6f;
    color: #fff;
    box-shadow: 0 0 20px 4px black;}

.entry-body .caption {
    text-shadow: black 1px 1px 0;}

.article-entry h1 {
    font-size: 18px;
    font-weight: bold;
    color: black;
    text-shadow: 0 0 1px #6292c7;}


.nae {
    background-color: #6f6f6f;}

#voteContainer {
    background: #6f6f6f;}

.logoc {
    background: #1c1c1c;}

.content-block-data ul li {
    background: url(http://sysadmins.ru/templates/subSilver/images/folder_announce.gif) left center no-repeat;
    background-size: 9px;}

.commentlinkblock {
    border-radius: 10px 10px 0px 0px;}

._h7n {
    background: #36a;}

._57i_ {
    color: #dadada;}

._2lqg {
    background: #272727;
    border: 1px solid #272727;}

#section-header td {
    background-color: #1d1d1d;}

.bcfirst {
    text-shadow: 0 0 2px #ff0101;}

div.sidebar-chunk.margin-bottom.socwidgets{
    display: none;}

a:visited {
    text-decoration: underline;
    color: #414141;}

#content-column .content-block-data .header .date {
    display: inline-block;
    background: #6f6f6f;
    width: 120px;
    margin: 0;
    padding: 7px 0 6px 0;
    text-align: center;
    font-size: 12px;
    color: #000;
    text-shadow: none;}

#full-view #content-column {
    padding: 6px;
    vertical-align: top;
    font-size: 12pt;}

#feed-to-top {border-radius: 33px;}
a:hover {
    color: #fff !important;
    font-weight: bold;
    text-shadow: 0 0 4px black !important;}

}
