css:
@-moz-document domain("www.bundlehaber.com") {
::-webkit-scrollbar,
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track-piece {
	background: #1C1C1C !important;    
}

::-webkit-scrollbar {
	width: 6px !important;
	height: 6px !important;
}

::-webkit-scrollbar-thumb {
	border-radius: 0px !important;
    box-shadow: inset 0 0 0px #5A5B5C;
	border: 0px solid #5A5B5C !important;
}

::-webkit-scrollbar-thumb:horizontal {
    background: linear-gradient(to right, #5A5B5C, #5A5B5C, #5A5B5C, #5A5B5C);
}
::-webkit-scrollbar-thumb:vertical {
    background: linear-gradient(#5A5B5C, #5A5B5C, #5A5B5C, #5A5B5C);
}

.desktop {
    background-color: #111111;
}
.site {
    background-color: #111111;
}
.cokpaylasiliyor ul li {
    background-color: #111111;
}
.cokokunuyor ul li {
    background-color: #111111;
}
.cokokunuyor h2,
.cokpaylasiliyor h2 {
    color: #EFF0F1;
}
.cokokunuyor .head,
.cokpaylasiliyor .head {
    border-bottom: 1px solid #232323;
}
.nav-tabs {
    border-bottom: 1px solid #232323;
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:focus,
.nav-tabs>li.active>a:hover {
    background-color: #00ADEE;
}
.ismail {
    background: #fff;
}
.populernews .content .right h3 {
    color: #EFF0F1;
}
.populernews .content .heading h4 {
    color: #CCCCCC;
}
.populernews .content .right p {
    color: #CCCCCC;
}
.header ul.social svg {
    color: #777;
}
.logo {
    background-image: url(https://image.prntscr.com/image/JBDu5xsyS4quA1IBOsr3XA.png);
}
.nav-tabs>li>a {
    color: #EFF0F1;
}
.tab-content ul li .content h3 {
    color: #EFF0F1;
}
.tab-content ul li .content h4 {
    color: #CCCCCC;
}
.dunneokundu h2 {
    color: #EFF0F1;
}
.dunneokundu .content .heading h4 {
    color: #CCCCCC;
}
.dunneokundu .content .right h3 {
    color: #EFF0F1;
}
.dunneokundu .content .right p {
    color: #EFF0F1;
}
.footer {
    background-color: #181818;
}
.footer h3 {
    color: #00ADEE;
}
.detailpage .container h2 {
    color: #EFF0F1;
}
.detailpage .container .bundleXPathBody,
.detailpage .container .bundleRssBody,
.detailpage .container .bundleXPathBody h1,
.detailpage .container .bundleRssBody h1,
.detailpage .container .bundleXPathBody h2,
.detailpage .container .bundleRssBody h2,
.detailpage .container .bundleXPathBody h3,
.detailpage .container .bundleRssBody h3,
.detailpage .container .bundleXPathBody h4,
.detailpage .container .bundleRssBody h4,
.detailpage .container .bundleXPathBody h5,
.detailpage .container .bundleRssBody h5,
.detailpage .container .bundleXPathBody h6,
.detailpage .container .bundleRssBody h6,
.detailpage .container .bundleXPathBody p,
.detailpage .container .bundleRssBody p,
.detailpage .container .bundleXPathBody span,
.detailpage .container .bundleRssBody span,
.detailpage .container .bundleH3,
.detailpage .container .bundleH3 {
    color: #999 !important;
}
.app-suggest {
    background-color: #111111;
    visibility: hidden;
}
.app-suggest-btn {
    background-color: #00ADEE;
}
.detailpage .container .detail h4 {
    color: #ccc;
}
.EmbeddedTweet--mediaForward .EmbeddedTweet-tweet {
    padding-top: 14.4px;
    border: 1px solid #232323;
}
.detailpage .container a.readonweb {
    color: #EFF0F1;
    border: 1px solid #232323;
}
.cls-1 {
    fill: #181818;
}
.cls-2 {
    fill: #00ADEE;
}
.widget {
    border: 19px solid #181818;
}
.widget img {
    visibility: hidden;
}
.widget {
    background-image: url(https://image.prntscr.com/image/RA4pkmKIShKO0dbMx-RPJg.jpg);
}
}
