logs_list_all .mkh2{color:#FD3 !important;transition:linear 400ms 3s}span[class="author"] a,footer a,.article_info .tags a,.content > p > a,.article_info > a,#menu_more_button{color:#80CFFF !important;transition:linear 400ms 3s}li p a,.article-footer-media-rt a{transition:linear 400ms 3s}a:hover,p a:hover,li p a:hover,span[class="author"] a:hover,footer a:hover,.article_info .tags a:hover,.content > p > a:hover,.article_info > a:hover,.links_two a:hover,*[class^="mkh"] a:hover{text-shadow:0 0 10px rgba(255,255,255,.8);transition:all linear 400ms 0s}.menu li a:hover{background-color:#999;transition:none}.menu li.active a{background-color:#aaa}div.article-footer-media > a{padding:1px 4px 1px 4px !important;color:#ec3;border:1px outset #ccc;border-radius:8px;background-color:#444;background:linear-gradient(to top,#444,#888);box-shadow:0 0 3px #444;transition:all linear 400ms 1s}div.article-footer-media > a:hover{padding:1px 4px 1px 4px !important;color:#fe3;border:1px solid rgba(153,153,153,.6);border-radius:8px;background-color:#888;background:linear-gradient(to top,#aaa,#555);box-shadow:0 0 3px #cd3;transition:all linear 400ms 0s;text-shadow:none}.mkblock,.date{color:#FFBF00 !important}.header .social li.vr{display:none !important}.ring .sixteenplus{background-color:rgba(136,51,51,.8);position:fixed !important;z-index:500;right:0;top:0;color:#CC3;font-weight:700;text-shadow:1px 1px 1px #AAA}.logo > a:nth-child(1)> img:nth-child(1){opacity:.33}.tabs li{border-top:1px solid #e5e5e5;border-right:1px solid #e5e5e5;border-bottom:1px solid #030303;background-color:#444;background:linear-gradient(to top,#444,#888)}.tabs li.selected,.tabs li:hover{border-bottom:1px solid black;border-top:2px solid #e5e5e5;background-color:#B3B3B3;background:linear-gradient(to top,#b3b3b3,#444)}.active{background-image: none !important}.mkform_comments .comments_user_avatar img, .author[src$="no_avatar.gif"]{filter: invert(100%) brightness(400%)}.mkform_comments .rules {color: #ff3333}.vjs-text-track-display{background-color: transparent}div#messages-window.messages_window > div.message_spiner {background-color: rgba(40,40,40,.3) !important}
