css:
@-moz-document url(https://new.pikabu.ru/) {body { background-color: #202020; }
.story__main, .story__footer, .section_padding_left { background-color: #2d2d2d; }
.story__read-more { background: linear-gradient(to bottom,transparent 15%,#222 100%); text-align: center;  padding: 20px; border-top: 1px solid #353535;}
.feed-panel { background-color: #2d2d2d; color: #d4d4d4; border: 1px solid #565656; }
.story__main { border: 1px solid #565656; }
.story__footer { border-top: 1px solid #565656; background-color: #272727; }
.collapse-button { border: 1px solid #565656 }
.profile-info__label { color: #6c9b45 }
.profile-info__hint { color: #bebebe }
.user__exit { color: #363636 }
.story__title-link { color: #6c9b45 }
.story__content, .story__description { text-align: justify;  color: #d2d2d2; }
.link, .user__nick, .user a { color: #6c9b45!important }
.story__scroll-next-btn:before { background: linear-gradient(to bottom,transparent 15%,#2d2d2d 100%); }
.story__rating-count { color: #a0a0a0; }
.story__read-more:before, .story .b-story__show-all:before { background-image: none; }
section, .section, .static-page__container>h1, .static-page__container>a, .static-page__container>div, .static-page__more { background-color: #212121; border: 1px solid #505050; color: #c1c1c1; }
.page-story__rating-pluses { background-color: #374e24;  border-right: 1px solid #aaa;}
.page-story__rating:before {  background-color: #6e271d;}
.section-group>section:first-of-type, .section-group>section[style*="display: none"]:first-of-type+section, .section-group>section[style*="display: none"]:first-of-type+section[style*="display: none"]+section
.story-editor__blocks .section-group { background-color: #212121; border: 1px solid #505050;}
.page-story__comments h4 { color: #757575!important; }
.comment__children { border-left: solid 1px #323232; }

.comment__user[data-own-story=true] .user__nick { color: #fff!important; }
.story-editor__blocks { background-color: #212121; border: 1px solid #505050;}
.section-group>section, .comment__children, .page-story__comments { background-color: #212121!important; color: #c1c1c1; }
.stories-feed__message { background-color: #464646; color: #c1c1c1; border: 1px solid #d2d2d2; }
.footer { background-color: #151515; color: #636363; border-top: 1px solid #949494; }
.popup_theme_gray .popup__container, .calendar-head, .calendar-head__buttons { background-color: #151515; color: #636363; }
.comments-navigator__refresh { background-color: #212121; }
.comment__children::before { background-color: #212121; position: absolute;  left: -2px; }
.comment-reply_border {  border: 1px solid #464646;}
.sidebar-block { background-color: #1d1d1d; color: #636363; }
.sidebar-block__header { background-color: #191919; color: #707070; border-bottom: 1px solid #505050; }
.sidebar-block__content_hr { border-top: 1px solid #505050; color: #707070; }
.sidebar-block__header h4 { color: #707070; }
.sidebar-block_border { border: 1px solid #505050; }
.community-info-block__rules { background-color: #1d1d1d; color: #636363; border-bottom: 1px solid #505050; border-top: 1px solid #505050; }
.input, .select { background-color: #191919; color: #fbfbfb; border: 1px solid #454545; }
input, textarea, .input-buffer, .select select { color: #636363; }
.popup__container { background-color: #151515; color: #636363; border: 1px solid #949494; }
.menu { color: #d4d4d4 }
.notification { border: 1px solid #505050; }
.section-hr { color: #d4d4d4; margin: 35px 0; }
.comment-reply__controls {background-color: #191919; border: none; }
.stories-search-item {background-color: #151515; border: 1px solid #949494;}
.story-save__name {color: #aaa;}
blockquote  { background-color: #282828!important; color: #a0a0a0; }}
