css:
@-moz-document domain("cointracker.io") {
  body {
    background-color: #0e1214;
}
#header,
#footer,
.badge-light,
.bg-light {
    background-color: #0e1214 !important;
    color: #f3f3f3;
}
.header-title {
    color: #ffcc00 !important;
}
.text-success {
    font-weight: bold;
    color: #99ff00 !important;
}
.badge-success {
    color: #99ff00;
    font-weight: bold;
    background-color: #003300 !important;
    border-color: #99ff00;
}
.text-danger {
    font-weight: normal;
    color: #ff3300 !important;
}
.alert-warning,
.badge-danger {
    color: #ff3300;
    font-weight: normal;
    background-color: #330000!important;
    border-color: #ff3300
}
.nav-tabs .nav-item.show .nav-link,
.nav-tabs .nav-link.active {
    background-color: #37474F;
    color: #fff;
    border-color: transparent
}
.nav-tabs {
    border-bottom: 1px solid #20292e;
}
a,
span.twitter-typeahead .tt-suggestion,
.nav-link,
.btn-link,
.header-link {
    color: #ffcc00!important;
}
.btn,
.btn-link,
.btn-primary,
.btn-light {
    color: #ffcc00;
    background-color: transparent;
    border-color: #ffcc00
}
a:hover,
.btn:focus,
.btn:hover,
.btn-primary:focus,
.btn-primary:hover,
.btn-light:focus,
.btn-light:hover,
.btn-link:focus,
.btn-link:hover {
    background-color: transparent;
    color: #fff !important;
    border-color: #fff
}
hr {
    display: block;
    height: 1px;
    border: 0;
    border-top: 1px solid #20292e;
    margin: 1em 0;
    padding: 0;
}
.dropdown-item:focus,
.dropdown-item:hover,
.nav-tabs .nav-link:focus,
.nav-tabs .nav-link:hover {
    background-color: #20292e;
    border-color: transparent;
}
.list-group-item {
    background-color: transparent;
}
.text-muted {
    color: #f0f0f0 !important
}
.card-footer {
    text-shadow: 0px 2px #0e1214;
}
.card,
.bg-white {
    background-color: #2e3b42 !important;
}
.table thead th {
    border-bottom: 1px dashed #20292e;
}
.table td,
.table th {
    border-top: 1px dashed #20292e;
}
element,
#main {
    color: #d5d5d5;
    background-color: #37474F !important;
}
span.twitter-typeahead .tt-menu,
.dropdown-menu {
    color: #F0F0F0;
    background-color: #2e3b42;
}
span.twitter-typeahead .active.tt-suggestion,
span.twitter-typeahead .tt-suggestion.tt-cursor,
span.twitter-typeahead .active.tt-suggestion:focus,
span.twitter-typeahead .tt-suggestion.tt-cursor:focus,
span.twitter-typeahead .active.tt-suggestion:hover,
span.twitter-typeahead .tt-suggestion.tt-cursor:hover {
    background-color: #20292e;
    color: #fff;
}
.d-sm-block {
    text-shadow: 0px 2px #0e1214;
}
h5.text-danger,
h5.text-success {
    font-size: 1.75rem!important;
}
}
