css:
@-moz-document domain("rutracker.org"), domain("rutracker.net"), domain("new-team.org"), domain("nnm-club.me") {
/*====================== bg ===========================*/
td, #page_header, #page_container, body, .news_title, .sp-body, textarea, .q, td.cat.pad_2, #body_container, .menu-a, tr.hl-tr:hover td, #contentHeadLeft, #contentHeadRight, #contentHeadCenter, #contentBodyLeft, #contentBodyRight, #contentBodyCenter, #contentMain, #contentFootLeft, #contentFootRight, #contentFootCenter, #p-search, button, .tg, .tCenter.small.nowrap, .row1, .row1 td, .row2, .row2 td, .row3, .row3 td, .row4, .row4 td, .row5, .row5 td
{
	background: #1f1f1f url(data:image/png;base64,) !important;
}

optgroup, option, input, input#searchGoButton.searchButton, select, select, #search-text
{
	background-color: #111 !important;
}

optgroup option:hover
{
	color: #111 !important;
	background-color: wheat !important;
}

span, p
{
	color: #ccc;
}

img.menu-alt1, img.log-out-icon, a.menu-root img, #nav-panel, #nav-up, #nav-settings, #nav-down, span.nav-icon, .rank_img img
{
	filter: invert(100%);
}

.site-nav, .topmenu, .news_date, .sb2-block, optgroup, .sp-wrap, table.forumline, .menu-a a:hover
{
	background-color: #1f1f1f !important;
	background-image: none;
}

.cat_title, .bordered th, .forumline th, .cat, td.cat, td.catTitle, td.catHead, td.catBottom, .thHead, option:hover, input[type="submit"], div.t-tags span, .spaceRow, #cse-search-btn-top, .btn-primary-grad, html input[type="button"]
{
	background-color: #1f1f1f !important;
	background-image: none;
}

input[type="submit"]:hover, .btn-primary-grad:hover, .btn-primary-grad:focus, html input[type="button"]:hover
{
	background-color: rgba(90, 90, 99, 0.25) !important;
}

input[type="submit"]:active
{
	background-color: rgba(35, 35, 45, 0.9) !important;
}

div.t-tags span:hover
{
	background: rgba(90, 90, 99, 0.25) !important;
}

.f, .fieldsets div > p, .fieldsets div, a.med, .a-like.med, a.small.tr-dl, p.tCenter a.gen, a.gen, a.med, a.small, a.gensmall, .gen, .med, .gensmall, .small
{
	background: transparent;
}

.folded, .folded2
{
	background-color: #1f1f1f !important;
}

.row2, .row2 td
{
	background-color: rgba(35, 35, 45, 0.5) !important;
}

/*====================== color ===========================*/
#page_container, body, .gen, .med, .small, .gensmall, .topmenu, .news_title, .site-nav, .news_date, .row1, .row1 td, .sb2-block, legend, optgroup option, span.brackets-pair, .sp-head span, .sp-body, span.p-color, select, textarea, input, .new .dot-sf, .q, .c-head, .q-head, .q-head span, div.t-tags span, .poster_info em
{
	color: #ccc !important;
}

#latest_news h3, .cat_title a, .forumline th, a.tLink, .maintitle, .pagetitle, .catTitle, optgroup, a:hover .brackets-pair, #sidebar1 h3, #idx-sidebar2 h3, td.topicSep, .poster_info p
{
	color: #f90 !important;
}

.news_date, .sb2-block, .forums td.row1, .cat_title, table.bordered, .bordered th, .bordered td, table.borderless .bordered th, table.borderless .bordered td, fieldset, .forumline th, .forumline td, table.forumline, table.topic, .topic .td1, .topic .td2, .topic .td3, .post_head, .post_btn_2, .c-body, .q, .sp-wrap, .sp-head, .sp-body, hr, #fs-main, select, textarea, input, .menu-a
{
	color: #ccc !important;
}

a, #latest_news a, .a-like, .nick-author, .nick-author a, .nick, .nick a, a.ref-1, a.ref-1:visited, a.internal-promo-text-link-1, a.internal-promo-text-link-1:visited
{
	color: #ac8 !important;
}

a:hover, a:active, a:focus, #latest_news a:hover, .site-nav a:hover, .site-nav a:active, .a-like:hover, a span
{
	color: #7a1 !important;
}

.dlComplete, .seed, .seedmed, .seedsmall
{
	color: #7CB342;
}

.dlDown, .leech, .leechmed, .leechsmall
{
	color: #ef5350 !important;
}

[style='color: brown;']
{
	color: #e57373 !important;
}

/*====================== Borders ===========================*/
input, select, .menu-a
{
	border-radius: 2px;
}

.topmenu
{
	border-radius: 6px;
}

.news_date, .sb2-block, .forums td.row1, .cat_title, table.bordered, .bordered th, .bordered td, table.borderless .bordered th, table.borderless .bordered td, fieldset, .forumline th, .forumline td, table.forumline, table.topic, .topic .td1, .topic .td2, .topic .td3, .post_head, .post_btn_2, .c-body, .q, .sp-wrap, .sp-head, .sp-body, hr, #fs-main, select, textarea, input, .menu-a
{
	border-color: #555 !important;
}

div.t-tags span:hover, div.t-tags span
{
	border-color: #555 !important;
}

option
{
	border-color: #202020 !important;
}

.bw_TRL
{
	border-width: 0px !important;
}

textarea, input
{
	border: solid 1px;
}

/*====================== hide ===========================*/
/*
#logo
{
    display: none;
}
*/
#bn-idx-marathonbet, .bn-idx, #bn-bot-wrap, #bn-idx-3, .bn-idx, #idx-sidebar2 iframe, #adriver-240x120, table.w100 iframe, td.bn-topic
{
	display: none;
}

.topmenu
{
	border: none;
}

/*====================== sizes ===========================*/
.topmenu #search-text, .topmenu #search-text-guest, form#login-form-quick [type='text'], form#login-form-quick [type='password']
{
	padding: 0px 12px;
}

.topmenu #search-menu
{
	padding: 0px 5px;
	margin-right: 2px;
}

.topmenu #search-submit .topmenu #cse-search-btn-top
{
	width: 70px !important;
}

.topmenu input, .topmenu select
{
	height: 30px;
}

.cat_title
{
	padding: 8px 8px 7px;
}

a.topictitle:hover, a.torTopic:hover, a.tLink:hover
{
	text-decoration: none !important;
}

select, textarea, input
{
	outline: none !important;
}

div.t-tags span
{
	text-shadow: none;
}

.news_date
{
	border-radius: 7px 0px 0px 0px;
}

.site-nav
{
	padding: 5px 0px 0px 130px;
}

.topmenu
{
	margin: 12px 10px 0;
	padding: 8px 16px;
}

#page_footer a img, .nav-natz a img
{
	opacity: 0.15;
}

#page_footer a:hover img, .nav-natz a:hover img
{
	opacity: 1;
}

#main-nav
{
	position: fixed;
	z-index: 999;
	top: 0px;
	width: 100%;
	box-shadow: none;
}

#page_header
{
	margin-top: 50px;
}

div.signature
{
	display: none;
}

#logged-in-username
{
	color: #35b6e1 !important;
}

iframe#bn-mg-bottom
{
	display: none;
}

.dot-sf
{
	color: #69b !important;
}
}

@-moz-document domain("rutracker.org") {
#bn-top-right, .inlined.middot-separated.floatR
{
	display: none;
}
}

@-moz-document domain("new-team.org") {
.pad_2, .pad_2 td, .collpase td, .collpase th, .space1 td, .space1 th, td.pad_2, td, #page_header, #page_container, body, .news_title, .sp-body, textarea, .q, td.cat.pad_2, #body_container, .menu-a, div.sp-wrap, div[style="max-width: 600px;margin: auto;border: 1px solid #d3d3d3;border-top: 0;background: #fff;margin-top: -1px;"], .q-head, .sb-standart, .filter-component, .container-filters
{
	background: #1f1f1f url(data:image/png;base64,) !important;
}

div, td, #page_header, #page_container, body, .news_title, .sp-body, textarea, .q, td.cat.pad_2, #body_container, .menu-a
{
	border-color: #555 !important;
}

div.sp-wrap, .forums th, .subforums, .forum_desc
{
	color: #ccc !important;
}

.pad_2, .pad_2 td, .collpase td, .collpase th, .space1 td, .space1 th, td.pad_2
{
	background-image: none !important;
	background-color: #1f1f1f !important;
}

td.topicSep
{
	background-image: none !important;
	background-color: #602e1b !important;
}

.js-important-links-control
{
	filter: invert(100%);
}

.posted_since, .borderless.bCenter.pad_8 b, #pep b, .last_edited
{
	color: #4b7;
}
}

@-moz-document domain("nnm-club.me") {
.postdetails, .genmed
{
	color: #e7fda3 !important;
}

div.copyright
{
	color: #ccc !important;
}
}
