css:
@-moz-document domain("tesreloaded.com") {
#wrapper {
    opacity: 1 !important;
}
/* transparency */

.list .board.item:hover
{
    background-color: transparent;
}

.stats .content a {
    color: #62599c;
}
/* latest posts */

.list a {
    color: #7aa6ac !important;
}

.list a:visited {
    color: #577074 !important;
}

/* thread titles */

.post.even {
    background-color: #3c3c3c !important;
}
/* post backgrounds even */

.board:nth-child(even) {
    background-color: #2c2c2c !important;
}
/* board thread title backgrounds even */

.thread:nth-child(even) {
    background-color: #2c2c2c !important;
}
/* title backgrounds even */




[class^="js-thread__title"]
{
   font-size: 165% !important;
}
/*thread titles*/
    
[class^="js-board__title"]
{
   font-size: 165% !important;
}
/*board titles*/


.title.bar {
    background-color: #5d2424 !important;
}



.board:img {
    background-color: #1f1f1f;
    box-shadow: inset 0px 0px 20px 13px #1f1f1f !important;
}

.board:nth-child(even) img {
    background-color: #2c2c2c;
    box-shadow: inset 0px 0px 20px 13px #2c2c2c !important;
}

.board:nth-child(odd) img {
    background-color: #1f1f1f;
    box-shadow: inset 0px 0px 20px 13px #1f1f1f !important;
}

img[title="New Posts"] {
	background-color: #18d4d2 !important;
    box-shadow: inset 0px 0px 20px 13px #1f1f1f !important;
}



/* to make new posts stand out */

/*
.list a {
    font-size: 150% !important;
}*/


/*.latest.last {
    font-size: 80% !important;
}*/


.posts .post .message a {
    color: #b44c6c !important;
}

.posts .post .message a:visited {
    color: #845865 !important;
}

.item.thread.new a
{
    color: #e13c3c !important;
}

.new-icon
{
    display: none !important;
}

.message {
    font-family: 'PT Sans', sans-serif !important;
	font-size: 21px !important;
	font-style: normal !important;
	font-variant: normal !important;
	font-weight: 400 !important;
	line-height: 30px !important;
}

.mini-profile {

font-size: 1.1em !important;

}

/* users */
.content a.user-link.group-0 {
    font-size: calc(10px + 0.4em) !important;
    color: #2b874d !important;
    word-wrap: break-word;
}

/* admin */
.content a.user-link.group-1 {
    font-size: 2.0em !important;
    color: #efbb24 !important;
    word-wrap: break-word;
}

.content a.user-link.group-2 {
    font-size: 1.7em !important;
    color: #143ef1 !important;
    word-wrap: break-word;
}

/* moderators */
.content a.user-link.group-3 {
    font-size: 1.7em !important;
    color: #d22121 !important;
    word-wrap: break-word;
}

.content a.user-link.group-4 {
    font-size: 1.7em !important;
    color: #29a6a7 !important;
    word-wrap: break-word;
}

.content a.user-link.group-5 {
    font-size: 1.7em !important;
    color: #39a6e7 !important;
    word-wrap: break-word !important;
}
}
