css:
@-moz-document url-prefix("https://play.google.com/music/listen")
{

body,
.material,
.material .cluster-text-protection,
.material .cluster-text-protection::before {
    background: #282828;
}
body {
    background: #282828!important;
}
.material-card {
    background: #282828;
}
.cluster .header .cluster-title {
    color: #fbf1c7;
}
body #main div .lane-content .fade-out:after {
    background: #282828;
}
body #main div .lane-content .material-shadow-z1 .fade-out:after {
    background: #282828;
}
.material-card .title {
    color: #fbf1c7;
}
.material-card .sub-title {
    color: #fbf1c7;
}
.material-card .details paper-icon-button.menu-anchor {
    color: #fbf1c7;
}
.cluster .header .subtitle {
    color: #fbf1c7;
}
h2.title {
    color: #ebdbb2;
}
#loading-overlay {
    background-color: #1d2021;
}
.gpm-detail-page-header-0 div.gpm-detail-page-header h2,
.gpm-detail-page-header-0 div.gpm-detail-page-header > [slot="buttons"] {
    color: #ebdbb2!important;
}
.gpm-detail-page-header-0 div.gpm-detail-page-header a,
.gpm-detail-page-header-0 div.gpm-detail-page-header span {
    color: #bdae93!important;
}
/* App Bar */

#material-app-bar {
    background: #3c3836;
    /* ff5722*/
    
    border: none !important;
}
.paper-tabs-0 #selectionBar.paper-tabs {
    background: #ebdbb2;
}
#tabsContainer .tab-content.style-scope.paper-tab {
    color: #ebdbb2;
}
body.qp #material-app-bar #material-one-left .music-logo {
    background-image: url('https://cdn.rawgit.com/macfeaster/3f2d97676bc1901cdd041dd0212cb390/raw/8cf75fb0ceb3f385c70c9567016edd11974ea9a1/play_music_white.svg');
}
#material-app-bar #countSummary,
#material-app-bar #action-bar-container {
    color: #ebdbb2;
}
#left-nav-open-button:after,
#playlist-drawer-button:after {
    filter: invert(1) !important;
    opacity: 1 !important;
}
#playlist-drawer-button.paper-icon-button-0 iron-icon.paper-icon-button {
    color: #ebdbb2;
}
#left-nav-open-button.paper-icon-button-0 iron-icon.paper-icon-button {
    color: #ebdbb2;
}
body.qp #content-container.has-hero-image #material-app-bar #material-one-left .music-logo-link {
    display: block;
}
body.qp #content-container.has-hero-image.transparent #material-app-bar #material-one-left .music-logo-link {
    display: none;
}
.sj-search-box-1 {
    background-color: #282828;
}
/* Search Box */

.sj-search-box-0 {
    background: #282828;
}
.sj-search-box-0 #searchIcon.sj-search-box {
    color: white !important;
}
.sj-search-box-0 #input.sj-search-box::-webkit-input-placeholder {
    color: #ebdbb2;
}
.sj-search-box-0 #input.sj-search-box {
    color: white !important;
}
.sj-search-box-0[opened],
.sj-search-box-1[opened],
.sj-search-box-0[has-query],
.sj-search-box-1[has-query] {
    background: #282828;
    color: white;
}
.sj-search-box-0 #clearButton.sj-search-box {
    color: #ebdbb2;
}
.sj-search-box-0[opened]:not([num-suggestions="0"]) #input.sj-search-box,
.sj-search-box-1[opened]:not([num-suggestions="0"]) #input.sj-search-box {
    border-bottom: 1px solid #3c3836;
}
sj-search-suggestion:hover,
sj-search-suggestion.query-selected {
    background: #3c3836;
}
sj-search-suggestion #queryText.sj-search-suggestion .sub-match.sj-search-suggestion {
    color: #ebdbb2;
}
.sj-search-box-1 #input.sj-search-box {
    color: #ebdbb2;
}
.sj-search-suggestion-0:hover,
.sj-search-suggestion-0.query-selected {
    background-color: #3c3836;
}
.sj-entity-suggestion-0 #title.sj-entity-suggestion {
    color: #fbf1c7;
}
.sj-search-box-1 sj-entity-suggestion.sj-search-box:last-of-type {
    border-bottom: 7px solid #282828;
    box-shadow: none;
}
.sj-search-suggestion-0 #queryText.sj-search-suggestion .sub-match.sj-search-suggestion {
    color: #fbf1c7;
}
.sj-entity-suggestion-0:hover,
.sj-entity-suggestion-0.query-selected {
    background: #3c3836;
}
/* Home */

.column .material-card .title,
.recommended-header {
    color: #fbf1c7 !important;
}
.column .material-card .sub-title {
    color: #fbf1c7 !important;
}
.material-card .reason .reason-text {
    color: #fbf1c7 !important;
}
.sj-home-0 #gradient.sj-home {
    background: #282828 !important;
}
sj-home #gradient.sj-home {
    background: #282828 !important;
}
sj-home h2 span.home-palette-id-1.tooltip {
    color: #fbf1c7!important;
}
.sj-home-0 #backgroundColor.sj-home {
    background: #282828!important;
}
sj-home #backgroundColor.sj-home {
    background: #282828!important;
}
gpm-card-grid .sj-card-0[card-aspect-ratio="wide"] {
    background: #3c3836!important;
}
.sj-card-0[card-aspect-ratio="wide"] #details.sj-card > [slot="title"] {
    color: #ebdbb2!important;
}
.sj-card-0 #details.sj-card > [slot="title"] {
    color: #fbf1c7 !important;
}
.sj-card-0 #details.sj-card > [slot="subtitle"] {
    color: #ebdbb2 !important;
}
.sj-card-0 #details.sj-card > [slot="description"],
.gpm-headline-header-0 > [slot="subtitle"] {
    color: #928374 !important;
}
sj-home[selected="6"] .home-palette-id-2 {
    color: #928374 !important;
}
.gpm-headline-header-0 h2.gpm-headline-header > a {
    color: #ebdbb2 !important;
}
.sj-page-indicator-0 .sj-page-tab.sj-page-indicator {
    color: #ebdbb2;
}
.gpm-card-grid-1 sj-card .card-title {
    color: white !important;
}
.gpm-card-grid-1 sj-card .card-description {
    color: #fbf1c7 !important;
}
.gpm-now-card-grid-0 .buttons.gpm-now-card-grid paper-icon-button.gpm-now-card-grid {
    background-color: #fbf1c7;
}
sj-home[selected="0"] .home-palette-id-2,
sj-home[selected="1"] .home-palette-id-2,
sj-home[selected="2"] .home-palette-id-2,
sj-home[selected="3"] .home-palette-id-2,
sj-home[selected="4"] .home-palette-id-2,
sj-home[selected="5"] .home-palette-id-2,
sj-home[selected="6"] .home-palette-id-2,
sj-home[selected="7"] .home-palette-id-2,
sj-home[selected="8"] .home-palette-id-2,
sj-home[selected="9"] .home-palette-id-2,
sj-home[selected="10"] .home-palette-id-2,
.home-palette-id-2 {
    color: #928374 !important;
}
/* Recent Activity, Top Songs */

.recent-view #gridTitle {
    color: #fbf1c7 !important;
}
.recent-view .details .card-title {
    color: #fbf1c7 !important;
}
.recent-view .details .card-subtitle {
    color: #fbf1c7 !important;
}
.gpm-card-grid-3 > [slot="title"] {
    color: #fbf1c7 !important;
}
.top-tracks {
    background: #3c3836;
}
.top-tracks .top-tracks-info .top-tracks-header {
    color: #fbf1c7; 
}
/* Playlists, albums etc */

#music-content.material {
    background: transparent;
}
.material-playlist-container,
.material-album-container,
.songlist-container,
.material-detail-view .artist-details {
    background: #282828!important;
}
.material .song-row td,
.material .upload-progress-row td {
    background: #282828;
    color: #ebdbb2; 
}
.material .song-row.selected-song-row td,
.material .song-row.currently-playing td {
    background: #3c3836!important;
}
.song-row .title-right-items {
    color: #ebdbb2; 
}
.material .song-table [data-col="title"],
.material .song-table.mini [data-col="song-details"] .song-title {
    color: #ebdbb2; 
}
tr.song-row a {
    color: #ebdbb2; 
}
.song-table th {
    color: #ebdbb2; 
}
.song-row:hover > td {
    background-color: #282828 !important;
}
.material-container-details .info .title .title-text {
    color: #ebdbb2; 
}
.sub-details a {
    color: #ebdbb2; 
}
.material-detail-view .material-container-details .info .container-stats-container .container-stats,
.material-detail-view .material-container-details .info .container-stats-container .container-stats span {
    color: #ebdbb2; 
}
.material-container-details .info .sub-details paper-icon-button {
    color: #fbf1c7; 
}
gpm-action-buttons {
    border-top: 1px solid #282828;
    color: #ebdbb2; 
}
.song-table th iron-icon {
    color: #ebdbb2; 
}
.rating-container.thumbs li {
    -webkit-filter: brightness(500%);
}
.fade-out.gray:after {
    background: linear-gradient(to right, rgba(17, 17, 17, 0), rgba(17, 17, 17, 1));
}
.song-indicator[data-playback-status="playing"] {
    background-image: url('http://i68.tinypic.com/2qmq3v4.gif')!important;
}
.song-indicator[data-playback-status="paused"] {
    background-image: url('http://i67.tinypic.com/2je42zl.png')!important;
    background-size: 100%!important;
    background-position: -1px -3px!important;
}
.song-indicator[data-playback-status="loading"] {
    background-image: url('http://i65.tinypic.com/2lvijht.gif')!important;
    background-size: 60%!important;
}
.material-detail-view .material-container-details .info .title {
    color: white;
}
.playlist-view .editable:hover {
    background: rgba(255, 255, 255, .1);
}
h2.section-header {
    color: #fbf1c7; 
}
.gpm-detail-page-header-0[description-overflows] #descriptionWrapper.gpm-detail-page-header:hover {
    background: #3c3836;
}
/* Artist Pages */

.material-detail-view .artist-details {
    color: #fbf1c7; 
}
.material-detail-view .artist-details .bio-wrapper .bio {
    color: #ebdbb2; 
}
.material-detail-view .material-container-details .read-more-button:hover {
    background: #3c3836;
}
body.qp .material .cluster-text-protection::before,
body.qp .material .cluster-text-protection {
    background: #1d2021;
}
/* Player */

#player {
    background: #282828;
}
#currently-playing-title {
    color: #fbf1c7; 
}
#player-artist,
.player-album,
.player-dash {
    color: rgba(255, 255, 255, .7);
}
#player .now-playing-actions paper-icon-button {
    color: #ebdbb2; 
}
#player-bar-forward iron-icon,
#player-bar-rewind iron-icon,
#material-volume-indicator,
#material-volume-low,
#material-volume-high {
    fill: #ebdbb2; 
}
.paper-progress-0 #progressContainer.paper-progress,
.paper-progress-0 .indeterminate.paper-progress::after {
    color: #ebdbb2; }
#player paper-icon-button[data-id="repeat"],
#player paper-icon-button[data-id="shuffle"],
#queue {
    color: #ebdbb2; 
}
.paper-icon-button-0 #ink.paper-icon-button {
    color: #fbf1c7; 
}
#player:hover #material-player-progress:not([disabled]) #progressContainer {
    background: #ebdbb2;
}
/* Left Drawer */

#nav-container .nav-toolbar,
.paper-drawer-panel-0 iron-selector.paper-drawer-panel > #drawer.paper-drawer-panel {
    background: #1d2021;
}
#nav-container {
    border: none !important;
}
.nav-item-container {
    color: #ebdbb2;
}
#nav .nav-section.material,
#nav.material {
    background: #1d2021;
}
#nav_collections .nav-item-container:focus,
.nav-item-container:focus,
.nav-item-container:hover {
    background: #282828;
}
.new-playlist-drawer .nav-section-divider {
    border-bottom: 1px solid #3c3836;
}
.nav-item-container.selected {
    background: #3c3836;
}
.new-playlist-drawer #nav-container .nav-toolbar .menu-logo {
    content: url('https://cdn.rawgit.com/macfeaster/3f2d97676bc1901cdd041dd0212cb390/raw/8cf75fb0ceb3f385c70c9567016edd11974ea9a1/play_music_white.svg');
}
.paper-drawer-panel-1 iron-selector.paper-drawer-panel > #drawer.paper-drawer-panel,
.paper-toolbar-2 {
    background-color: #1d2021;
}
/* Right Drawer */

sj-right-drawer #drawer.sj-right-drawer {
    background: #1d2021;
}
#playlist-drawer paper-header-panel[at-top] paper-toolbar:not([style-scope]):not(.style-scope) {
    border-bottom: 1px solid #3c3836;
}
.paper-toolbar-1,
.paper-toolbar-2 {
    background: #1d2021;
    color: #ebdbb2;
}
#playlist-drawer #playlist-drawer-header {
    color: #ebdbb2;
}
#playlist-drawer a {
    color: #ebdbb2;
}
#playlist-drawer .autoplaylist-section,
#playlist-drawer #recent-playlists-container {
    border-bottom: 1px solid #3c3836;
}
#playlist-drawer .playlist-drawer-item .playlist-wrapper:hover,
#playlist-drawer .playlist-drawer-item .playlist-wrapper:focus,
#playlist-drawer .playlist-drawer-item sj-play-button:hover~.playlist-wrapper,
#playlist-drawer .playlist-drawer-item iron-icon:hover~.playlist-wrapper,
#playlist-drawer .playlist-drawer-item.playlist-drop-target:not(.subscribed) .playlist-wrapper {
    background: #282828;
}
#playlist-drawer:not([style-scope]):not(.style-scope) paper-header-panel[at-top]:not([style-scope]):not(.style-scope) paper-toolbar:not([style-scope]):not(.style-scope) {
    border-bottom: 1px solid #3c3836;
}
/* Queue */

#queue-overlay::after {
    border: 8px solid #282828;
}
.sj-mini-queue-0 #queueContainer.sj-mini-queue {
    background: #282828;
}
/* Modals */

.iron-overlay-backdrop-0 {
    background: #1d2021;
}
.paper-dialog-0 {
    background: #3c3836;
    color: #fbf1c7;
}
.paper-input-container-0 .input-content.paper-input-container input,
.paper-input-container-0 .input-content.paper-input-container textarea,
.paper-input-container-0 .input-content.paper-input-container iron-autogrow-textarea,
.paper-input-container-0 .input-content.paper-input-container .paper-input-input {
    color: #fbf1c7;
}
.share-buttons .share-button .button-label {
    color: #fbf1c7;
}
.visualizercard {
    background: #282828;
}
.visualizercard .label {
    color: #fbf1c7;
}
.material-share-options #sharing-option-label {
    color: #fbf1c7;
}
/* Listen Now etc */

.situations-filter {
    background: #282828;
    color: #fbf1c7;
}
.filter-description {
    color: #ebdbb2 !important;
}
.material-list li:not(:last-child) .li-content {
    border-bottom: 1px solid #3c3836;
}
.material-list a {
    color: #fbf1c7;
}
.material-list li a:hover,
.material-list li a:focus {
    background: #282828;
}
.radio-selection.material {
    background: transparent;
}
.material-detail-view .station-container-content-wrapper .material-container-details {
    background: #282828;
}
.sub-details {
    color: #fbf1c7;
}
.material-detail-view .material-container-details .info .description {
    color: #fbf1c7;
}
/* Top Tracks, New Releases etc */

.material-detail-view .top-tracks {
    background: #282828;
    color: #fbf1c7;
}
.more-songs-container {
    border-bottom: 1px solid #3c3836;
}
/* Context Menus */

.playlist-menu,
.goog-menuitem,
.goog-menuitem-content,
.now-playing-menu,
.goog-menu-vertical {
    color: #ebdbb2!important;
    background: #282828 !important;
}
.material .goog-menu .goog-menuseparator {
    background: #424242 !important;
}
.material .goog-menuheader {
    color: rgba(255, 255, 255, .5) !important;
}
.goog-menuitem:hover {
    background: #3c3836 !important;
}
.goog-menuitem:hover > .goog-menuitem-content {
    color: #fbf1c7 !important;
    background: #3c3836 !important;
}
.goog-menuitem-highlight .goog-menuitem-content .goog-submenu-arrow,
.goog-menuitem-highlight .goog-menuitem-content {
    color: #ebdbb2 !important;
}
/* Scroll Bars */

::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-track {
    border-radius: 10px;
    background: #ebdbb2; 
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    background: #ebdbb2!important;
}
/* Account Switcher etc */

#gbwa .gb_g .gb_ca,
.gb_ja,
#gbwa .gb_ga {
    background-color: #504945!important;
}
#gbwa .gb_g {
    border: none!important;
}
#gbwa .gb_Z .gb_O {
    color: #fbf1c7!important;
}
#gbwa .gb_Z .gb_O:hover {
    color: #1d2021!important;
}
#gbwa .gb_ha a {
    color: #fbf1c7!important;
}
#gbwa .gb_ka {
    background-color: #504945!important;
}
#gbwa .gb_ka:hover,
#gbwa .gb_ka:hover > #gbwa .gb_ha a {
    color: #fbf1c7!important;
    background-color: #282828!important;
}
#gbwa .gb_ha a {
    color: #fbf1c7!important;
}
#gbwa .gb_la {
    background-color: #282828!important;
}
#gbwa .gb_la:hover {
    color: #fbf1c7!important;
    background-color: #282828!important;
}
.gb_la {
    border-color: #ebdbb2!important;
}
#gbwa .gb_wa {
    background: #3c3836!important;
}
#gbwa .gb_wa .gb_ya {
    color: #fbf1c7!important;
}
.gb_fa .gb_ea .gb_b {
    filter: invert(1);
    opacity: 1;
}
#gbwa>div:first-child>a:after,
#gbwa > div.gb_Fc > a {
    filter: invert(1) !important;
    -webkit-filter: invert(1) !important;
    opacity: 1 !important;
}
.gb_za .gb_ia {
    background: #504945;
}
.gb_b.gb_cc {
    filter: invert(1);
    opacity: 1;
}
/* Various fixes, like the f*cking Holo context menu on the Now Playing bar */

.now-playing-menu.goog-menu,
.goog-menu {
    border: none !important;
    border-radius: 2px;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37)!important;
}
}
