css:
@-moz-document domain("rutracker.org") {
html, #newtab-window
  {
    background-image: linear-gradient(rgb(0,0,0), rgb(0,0,0)) !important;
    background-attachment: fixed !important;
    min-height: 100vh !important;
  }
/*--------------------- Основа ---------------------*/
  *:not(.coloris)
  {
    color: skyblue !important;
    box-shadow: none !important;
    background-color: transparent !important;
    border-color: rgba(135,206,235,0.1) !important;

  }
  [id="page_content"], option
  {
    background-image: linear-gradient(rgb(0,0,0), rgb(0,0,0)) !important;
  }
/*-------------------------------------------*/  
  
  [class="cat border bw_TB"], [id="nav-panel"], [id="nav-opt-menu"], [id="show-only"], [class="catTitle"], [class="catBottom med"], .menu-sub table
  {
    background-image: linear-gradient(rgb(0,0,0,.8), rgb(0,0,0,.8)) !important;
  }
  [class="menu-a bold nowrap"], .menu-split, .cat, td.cat, td.catTitle, td.catHead, td.catBottom, table.user_details th
  {
    background-image: linear-gradient(rgb(0,0,0,.7), rgb(0,0,0,.7)) !important;
  }    
  [class="cat_title"], th, .topmenu, [class="tablesorter-headerRow"], [class="row3"], .forumline th
  {
    background-image: linear-gradient(rgb(135,206,235,.5), rgb(0,0,0,.9)) !important;
  }
  .cat_title a 
  {
  	text-decoration: none;
	  color: #000000 !important;
  }
  tr.hl-tr:hover td
  {
	  background-color: transparent !important;
  }
  input
  {
    background-image: linear-gradient(rgb(0,0,0), rgb(0,0,0)) !important; 
  }
  [class="tRight w50"], [class="tCenter pad_2"], [class="w50"]
  {
   background-image: linear-gradient(rgb(0,0,0,.0), rgb(0,0,0,.0)) !important;  
  }
  .top-alert
  {
    box-shadow: 0px 0px 4px 1px rgb(135, 206, 235, .17) !important;
  }
  table.user_details th
  {
    border: solid 1px rgb(135,206,235,0.1) !important;
  }
/*----------------------- Иконки -----------------------*/
  img.icon2, img.topic_icon, [src*="https://static.t-ru.org/templates/v1/images/post.gif"], [src*="https://static.t-ru.org/templates/v1/images/menu_arrow_1.svg"], [src*="https://static.t-ru.org/templates/v1/images/attach_big.gif"], [src*="https://static.t-ru.org/templates/v1/images/reply.gif"]
  {
    filter: saturate(0)invert(1);
  }
  .tor-approved
  {
    color: green !important;
  }
  .tor-dup
  {
    color: blue !important;
  }
  .tablesorter thead .tablesorter-headerUnSorted 
  {
	  background-image:url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSIxOCIgaGVpZ2h0PSIxMiIgdmlld0JveD0iMCAwIDI0IDE2Ij48cGF0aCBkPSJNMTUgOCAxIDggOCAwek0xNSA5IDEgOSA4IDE2eiIgZmlsbD0iIzIyMiIvPjwvc3ZnPg==)  !important;
    filter: saturate(0)invert(0);
  }
/*--------------- Свечение текста ------------------------*/  
  a:hover, a:hover *, a:visited:hover, a:visited:hover *, span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .link:hover, .link:hover *, h2:hover, h5:hover, h6:hover, h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover, h6 a:hover, option:hover, [class="sp-head folded unfolded"]:hover *, [id="thx-btn"]:hover, [class="sp-head folded"]:hover *, [id="tor-filelist-btn"]:hover, [type="submit"]:hover, [onclick="BB.set_h_cat();"]:hover, [id="tpl-submit-btn"]:hover, input:hover, #logged-in-username:hover, label:hover
  {
    color: rgb(255,255,255) !important;
    text-shadow: 0 0 7px rgba(255,255,255,0.5) !important;
    text-decoration: none !important;
  }
  #sidebar1 h3, #idx-sidebar2 h3
  {
    color: #BF9000 !important;
  }
  span.seedmed b, .seed, span.seed b
  {
    color: rgb(0, 170, 0) !important;
  }
  span.leechmed b, .leech, span.leech b
  {
    color: rgb(140, 0, 0) !important;
  }
  span.posted_since
  {
    color: rgb(128,128,128) !important;
  }
}
