css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("clipstreams.net") {

/* ◆全体◆ */
html {
    background: #111111;
}
body {
    background: #272727 !important;
    color: #d6d6d6;
    text-shadow: #0054ff 0px 0px 1px;
}
p {
    color: #C0C0C0 !important;
    margin: 0 !important;
    line-height: 1.2 !important;
}
a {
    color: #d6d6d6 !important;
    text-decoration: none !important;
}
a:link {
    color: #d1e2ff !important;
    text-decoration: underline;
    -webkit-transition: all 500ms cubic-bezier(0.1, -0.55, 0.265, 1.55);
    -moz-transition: all 500ms cubic-bezier(0.1, -0.55, 0.265, 1.55);
    ;
}
a:visited {
    color: #76a3cc !important;
}
a:hover {
    color: #d1e2ff !important;
    text-shadow: #ffca00 0px 0px 10px;
}
.signup_container {
    background: #111111 !important;
}


/* ◆TOPページ◆ */
.container {
    width: 1200px !important;
    margin-left: 50px;
}
.content_con {
    width: 1310px !important;
}
.tabs {
    width: 540px !important;
    height: 41px !important;
    border: 1px ridge #cccccc !important;
}
.main_vids4 {
    margin-top: 2px;
    border: 1px dotted #999999 !important;
    display: -moz-box !important;
}
.view_thumb4 {
    background: #272727;
}
.view_thumb4 a img {
    border: 1px solid #00289b;
}
.pagination {
    color: #d6d6d6;
}
.pagination a,
.pagination .selected {
    background: #333333;
    border: 1px solid #555555;
}
.selected2,
.selected2_word {
    background: #333333;
}
#AlLMenu ul {
    background: #111111;
}
#AlLMenu:hover ul li a:hover {
    background: #222222;
    text-shadow: #0c53ee 0px 0px 10px !important;
}
.MenuLinks ul li a {
    text-shadow: none;
}

/* header footer */
.navi,
.footer {
    background: #111111;
}
.footer ul li a,
.dm_footer ul li.admin_area a {
    background: #333333;
    border: 1px solid #555555;
    text-shadow: none;
}
.s_bg {
    border: 1px solid #bbb;
}
.searchbar {
    color: #fff;
    background: #000;
    height: 16px;
    border: 1px solid #999;
    margin-top: -1px;
    font-size: 16px;
    font-weight: bold;
}
.moveL {
    margin-top: -2px;
    margin-left: 0px;
    font-weight: bold;
    text-shadow: none;
}
#selectedSearch {
    color: #b5bff0;
    background: #000;
    height: 29px;
    border: 1px solid #999 !important;
    margin: 1px -2px -4px 0px;
}
#selectedText{
    position: relative;
    top: -2px;
    left: 2px;
}
.searchbutton {
    margin-top: 0px;
    margin-left: 0px;
}


/* ◆動画一覧ページ◆ */
.vid_page_conainer {
    width: 1400px !important;
}
.view_thumb2 {
    background: #272727;
}
.view_thumb2 a img {
    border: 1px solid #00289b;
}
.category_list {
    display: none;
}
.video_list2 {
    width: 190px;
}


/* ◆検索ページ◆ */
#content,
#content > div > div:nth-child(4) > div:nth-child(3) {
    width: 1400px !important;
    margin-left: -12px !important;
}
.simple_container {
    background: #222222;
    color: #d6d6d6;
}
.more_button,
.more_button_up {
    background: #222222;
    color: #d6d6d6;
    font-size: 16px !important;
    padding: 6px;
    border-bottom: 1px #353535 solid;
    border-top: 1px #353535 solid;
    height: 20px;
}
.more_button_up a,
.more_button a {
    font-size: 16px;
}


/* ◆動画ページ◆ */
.player_container,
#s0 {
    margin-left: 218px !important;
}
.watch_left {
    margin-left: 220px !important;
}
.watch_right {
    margin-left: 0px !important;
}

.rating_container #rating_result_container {
    font-size: 11px;
    font-weight: bold;
    color: #d6d6d6;
}
.video_details {
    background: #333333;
    margin-top: 10px;
}
.video_details .vd_details {
    color: #d6d6d6;
    text-shadow: #3369d6 0px 0px 1px;
}
.s_WVDec,
.WVDec {
    color: #d6d6d6;
    text-shadow: none;
}
.ShowMore {
    height: 20px;
    background: #111111;
    border-top: none;
}
.dasdawq {
    background: #111111;
    color: #d6d6d6;
    text-shadow: 0px 0px 1px #3369d6;
    padding: 0px;
    border-bottom: none;
    pointer-events: none;
}
.comment,
.add_comment_box {
    background: #333333;
    border: 0.5px solid #555555;
}


/* ◆ユーザー一覧ページ◆ */

.user_block {
    width: 116px;
    margin: 5px;
    margin-bottom: 20px
}


/* ◆マイアカウントページ◆ */
.my_account_left {
    margin-left: 215px !important;
}
.account_box {
    margin-right: 205px !important;
}

/* アカウント */
.account_option_box {
    background: #3d3d3d;
}
.manage_contacts_tbl_head {
    background: #111111;
}
#profile_desc {
    width: 500px;
    height: 200px;
}

/* ムービー */
.account_table {
    margin-top: 10px;
    background: #111111;
}
.account_vid_list {
    background-color: #222222 !important;
}
.account_vid_list:nth-child(2n+1) {
    background-color: #353535 !important;
}

/* チャンネル・友達・メッセージ */
.pm_box {
    background-color: #3d3d3d;
}
.pm_box .messages {
    color: #d6d6d6;
}
.manage_contacts_tbl {
    background-color: #222222 !important;
}
.manage_contacts_tbl:nth-child(2n+1) {
    background-color: #353535 !important;
}
.grp_manage_links li,
.cb_fb_style_button {
    background: #000000 !important;
}

/* 受信トレイ・送信済・メッセージ作成 */
.messages_container {
    width: 798px !important;
    max-height: 300px !important;
}
.account_box > .account_form > form > fieldset:nth-child(3) > div:nth-child(4) > .account_field textarea {
    width: 490px !important;
    height: 220px;
    max-height: 400px !important;
}
#pm_content{
    width: 490px !important;
    height: 160px;
    max-height: 400px !important;
}

/*受信・送信メッセージのフォントサイズ*/
.private_message_container > div:nth-child(4){
    font-size: 12pt;
}


/* 退会 */
li.li:nth-child(7),
div.account_option_box:nth-child(8) {
    display: none;
}


/* ◆マイチャンネルページ◆ */
#container > div:nth-child(3) {
    margin-left: 200px;
}
.channelBox,
.channelBar {
    background: rgba(0, 0, 0, 0.5);
}
.channelLegend {
    background: #111111;
    color: #d6d6d6;
    border-top: none;
}
.viewChannelProfileThumb {
    background-color: #333333 !important;
}


/* ◆アップロードページ◆ */
.upload_left {
    width: 680px;
    margin-left: 200px;
}
.upload_right {
    margin-right: 200px;
}
.upload_opt,
.upload_left,
.upload_info,
.upload_form_div {
    background: #222222 !important;
}
.upload_right .instructions {
    background: #222222 !important;
}
.block {
    font-size: 11px;
}
fieldset:nth-child(1) > div:nth-child(10),
fieldset:nth-child(1) > div:nth-child(11),
fieldset:nth-child(1) > div:nth-child(12),
fieldset:nth-child(1) > div:nth-child(13),
fieldset:nth-child(1) > div:nth-child(14),
fieldset:nth-child(1) > div:nth-child(15),
fieldset:nth-child(1) > div:nth-child(16),
fieldset:nth-child(1) > div:nth-child(17) {
    display: none !important;
}
.upload_info > fieldset:nth-child(1) > div:nth-child(18) > .upload_form input[type=textfield],
.upload_form textarea {
    width: 500px !important;
    height: 220px;
    max-height: 600px !important;
}
/* ムービー編集ページ */
.account_form input,
.account_field {
    width: 490px;
}
.account_field_block:hover {
    background-color: #444444;
}


/* ◆広告◆ */
.ads,
.add_box_728,
.ads300x600,
.add_con,
.side_con,
.watch_vids_cont,
.vert_add_box2 {
    background: #272727;
    display: none;
}


/* ◆問い合わせページ◆ */
.contact_form label {
    color: #d6d6d6;
}

}
