{
	background: #1f1f1f/**/
	url(data:image/png;base64,)/**/
	!important;
}

textarea, .c-editor, input, #pageContent.entry-one-col .series-title > .table-cell, .text-frame, .article-partner, pre, header.header .bottom, .topic-body, .topic-map, .latest-topic-list, .topic-list, .category-list, .comment-wrapper, td, blockquote, div.cooked, .quote, .title, #phContent_divMetaBody .imho__container, .imho__container, button, .v37vgnw, .l6jiiqx, .hpoorqx, .bubble
