{
	color: #bbcc33 !important;
}

button, .actionbar, .button
{
	background-color: #000 !important;
}

tbody tr:nth-child(2n+1) td, tbody tr:nth-child(2n+1)
{
	background-color: #333 !important;
	color: #ccc !important;
}

.btn, .button
{
	color: #222 !important
}

.color-annotation
{
	color: yellow !important;
}

#popular-tools, #new-tools
{
	display: none !important;
}
}
