css:
@-moz-document url-prefix("https://www.criticker.com") {
body,
.gr__criticker_com,
.i_bannerad
{
	
	background-color: #2C312C !important;
	padding: 0px 0px !important;
	border-top: 0px
								
}

div#left_container{
	
	background-color: #373E37 !important;
	
}

div#up_user_bio,
#up_filmlist_container,
#up_infoupdatelink,
#up_pswdupdatelink,
#up_member,
#up_info,
#up_quip,
div#left_container,
div#i_overall_container{
	
	background-color: #606F60 !important;
	border: 0px !important;
}

.up_filmlist{ 
	border-bottom:  1px #1C2329 solid !important;
}


div#up_filmtable_choices{
	background-color: #606F60 !important;

}

.up_filmlist.shaded, 
.sidebar_header, 
.sidebar,
div#up_messages_contents,
.rc_otherrec_header,
.psiscore_container{
	
	background-color: #345A5A  !important;
	
}

.sidebar nav, 
.sidebar section{
	
	background-color: #345A5A !important;
	margin: 0px 0px 10px 0px !important;
    border: #0f142d !important;
	padding: 0 !important;
}

div#up_messages,
#tcigen_contents{
	
	background-color: #345A5A !important;
	
}

div#i_body{
	
	border: 10px !important;
	
}

div#up_filmlist_contents{
	
	border-bottom-width: 0px !important;
	padding: 0 !important;
}

div#tci_gen{
	
	background-color: #606F60 !important;
	padding: 0 !important;
}

div#tcigen_history td{
	
	background-color: #606F60 !important;
											
}

.i_searchbox.active{
	
	color: #65ea14 !important;
	
}

.sr_result:hover{
	
	background-color: #345A5A !important;
	
}

div#fi_tabbody_user, 
div#fi_tabbody_active,
div#fi_targettable td{
	
    background-color: #345a5a !important;
	
}

.pti.darkgreen {
	
    color: #50B623 !important;
}

.reviewtext.active {
    
    color: #65ea14 !important;
}

.fi_quip_green {
    color: #4CC616 !important;
}
}

@-moz-document url-prefix("https://www.criticker.com/explain/explain/explain/?p=4") {
div#te_explain_table td{
	
	background-color: #345a5a !important;
	
}
}

@-moz-document url-prefix("https://www.criticker.com/film"), url-prefix("https://www.criticker.com/tv") {
div#fi_targettable_div, 
#fi_targettable_smalldiv{
	
    background-color: #6d6e89 !important;
	
}

.reviewtext{
	
	color: #EEE8AA !important;
	
}
}
