css:
@-moz-document domain("swiftbot.fr") {
#container {
    background: #212121;
    border: 1px solid #111;
}
blockquote {
    background: #212121;
}
.smallheader {
    background: #212121;
    border-top: 1px solid #111;
    border-bottom: 1px solid #111;
}
.mainfooter {
    background: #212121;
    border-top: 1px solid #111;
}
.trow1,
.trow2 {
    background: #212121;
    border-top: 1px solid #111;
    border-bottom: 1px solid #111;
}
textarea {
    color: #ccc;
    background: #212121;
    border: 1px solid #111;
}
table {
    color: #999;
}
.tborder {
    border: 3px solid #171717;
}
.myreactions-container {
    border-top: 1px solid #111;
}
.post.classic .post_author {
    border: 1px solid #111;
}
.pbcname,
.pbcavatar {
    border: 1px solid #111;
}
.post_controls {
    background: #111;
    border-bottom: 1px solid #111;
}
.post .post_author {
    background: #212121;
}
.author_information,
.pbctext,
.pbcname,
.smallheader {
    background: #212121;
}
.forums > .tborder > tbody> tr >td {
    background: black !important;
}
.forums > .inferno_table > tbody> tr > td {
    background: #212121 !important;
}
.istatwrap > ul > li {
    background: #111;
}
.trow3 {
    background: #212121;
    border-radius: 0px;
}
.tborder > tbody> tr >td:not(.thead):not(.trow2):not(.trow1):not(#posts_container) {
    background: black !important;
}
.tborder tbody tr:last-child td:last-child {
    border-bottom-right-radius: 0px;
}
.tborder tbody tr:last-child td:first-child {
    border-bottom-left-radius: 0px;
}
.fbitstats {
    background: #111;
    color: #999;
    box-shadow: inset 0px 0px 12px 0px #111;
}
.inferno_text {
    background: #111;
    border-color: black;
    color: #999;
}
.inferno_content > p {
    background: #111 !important;
    color: #999;
}
.newsticker {
    background: #111;
}
.newsTicker .news-header {
    background: #111;
}
}
