css:
@-moz-document domain(awmc.pl) {

#shoutbox .text {
    width: 100%;
    color: #FFFFFF;
    display: table-cell;
    margin: 0;
    padding: 6px;
    text-align: justify;
    -webkit-hyphens: auto;
    -moz-hyphens: auto;
    -ms-hyphens: auto;
    hyphens: auto;
}

#shoutbox {
    margin-bottom: 10px;
    background: rgba(76, 76, 76, 0.9);
    border: 1px solid;
    border-color: #000;
    color: #FFFFFF;
    -moz-border-radius-topleft: 6px;
    -moz-border-radius-topright: 6px;
    -webkit-border-top-left-radius: 6px;
    -webkit-border-top-right-radius: 6px;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}


input {
    -webkit-appearance: textfield;
    background-color: gray;
    -webkit-rtl-ordering: logical;
    user-select: text;
    cursor: auto;
    padding: 1px;
    border-width: 2px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}

.trow2 {
    background: rgb(115, 115, 115);
}

.trow1 {
    background: rgba(121, 119, 119, 0.8);
}

a:visited {
    color: #3bf73b;
    text-decoration: none;
}
a:link {
    color: #3bf73b;
    text-decoration: none;
}

#posts_container {
    padding: 0;
    background: rgba(101, 101, 101, 0.9);

}

.post .post_author {
    border-bottom: 1px solid #ccc;
    border-top: 2px solid #ccc;
    background: #464646;
    padding: 5px;
    overflow: hidden;
}

.post_controls {
    clear: both;
    background: #464646;
    border-bottom: 1px solid #ccc;
    padding: 5px;
    overflow: hidden;
}

.post .post_head {
    font-size: 11px;
    padding-bottom: 4px;
    border-bottom: 1px dotted #ddd;
    margin-bottom: 4px;
}

.post .post_head span.post_date {
    color: #FFF;
}

.post .post_author div.author_statistics {
    float: right;
    font-size: 11px;
    padding: 3px 10px 3px 5px;
    color: #FFF;
    line-height: 1.3;
}

table {
    color: #FFF;
    font-size: 13px;
}

.codeblock code {
    overflow: auto;
    height: auto;
    max-height: 200px;
    display: block;
    font-family: Monaco, Consolas, Courier, monospace;
    font-size: 13px;
    color: white;
}

.clear {
    clear: both;
    background: #464646;
}

.codeblock {
    background: #464646;
    border: 1px solid BLACK;
    padding: 10px;
}

.tfoot {
    border-top: 1px solid #fff;
    padding: 6px;
    background: #464646;
    color: #666;
}

.tborder {
    width: 100%;
    margin: auto auto;
    background: NONE !important;
    padding: 1px;
    border: 1px;
}

.trow_sep {
    background: #464646;
    color: #FFF;
    border-bottom: 1px solid #c5c5c5;
    padding: 6px;
    font-size: 12px;
    font-weight: bold;
}

.post .post_head span.edited_post {
    font-size: 10px;
    color: #bbb;
}

#container {
    color: #b5b5b5;
    text-align: left;
    line-height: 1.4;
    margin: 0;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
    font-size: 13px;
    min-width: 990px;
}

.pagination .pagination_current {
    background: none;
    color: #b5b5b5;
    border: none;
    font-weight: bold;
}

.pagination a {
    background: #464646;
    border: 1px solid #ccc;
}

.post_body {
    font-size: 14px;
    padding: 12px 0;
    color: #FFF !important;
}
}
