css:
@-moz-document domain("vk.com")
{

/* v2.63 */
/*[[style]]*/




  #page_header_cont .back
  {
    background: var(--colour);
    border-bottom: 0px;
  }

  body[onresize]
  {
    background: #FFF var(--bground) right fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
  }

  #side_bar
  {
    filter: var(--huerot) !important;
  }

  div#side_bar_inner > nav > ol
  {
    border-radius: 2px;
  }

  .side_bar_inner
  {
    margin-top: 46px;
  }

  #page_layout
  {
    background: #f9f9f9;
  }

  #ads_left
  {
    display: none !important;
  }
/* Шапка */
  a.top_home_link.fl_l
  {
    width: 164px;
  }

  #page_header, #page_header_cont, #page_header_cont .back, .top_nav_btn, .top_nav_link
  {
    height: 46px !important;
  }

  .top_nav_btn .top_nav_btn_icon
  {
    margin: 13px auto;
  }

  .top_audio_player .top_audio_player_btn
  {
    padding: 18px 4px 14px;
  }

  .top_audio_player .top_audio_player_play
  {
    padding: 16px 4px !important;
  }

  .top_audio_player .top_audio_player_title_wrap
  {
    margin: 15px 0;
  }

  .top_nav_link
  {
    line-height: 46px;
  }

  #top_notify_wrap
  {
    top: 46px !important;
    left: 0px;
  }

  .top_audio_player .top_audio_player_title, .top_profile_name
  {
    color: #FFFFFF;
    font-weight: 400;
  }

  .top_nav_link.active, .top_nav_link:hover , .top_audio_player:hover , .top_nav_btn:hover, .audio_row:not(.audio_row_current):hover, .top_nav_btn.active
  {
    background-color: rgba(255,255,255,0.5);
    transition: 300ms;
  }

  .top_nav_link , .top_audio_player , .top_nav_btn, .audio_row:not(.audio_row_current)
  {
    transition: 300ms;
  }

  .top_audio_player .top_audio_player_btn>div
  {
    opacity: 1 !important;
  }

  .top_home_link .top_home_logo
  {
    background-size: contain;
    height: 30px;
    width: 160px;
    margin: 8px -15px 0;
  }

  .top_profile_img
  {
    margin: 9px 0;
  }

  #top_profile_menu:after, #top_profile_menu:before
  {
    right: 14px;
  }

  #top_profile_menu:before
  {
    border-width: 6px;
    margin: 0 -6px;
    border-bottom-color: rgba(0,0,0,0);
  }

  .slider .slider_handler, .nim-dialog.nim-dialog_unread-out .nim-dialog--unread, .nim-dialog.nim-dialog_unread-out.nim-dialog_muted .nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_unread-out .nim-dialog--unread , .nim-dialog.nim-dialog_unread.nim-dialog_prep-injected .nim-dialog--unread, .top_notify_count, .feedback_photo_icon
  {
    border-radius: 50% !important;
  }

  .nim-dialog.nim-dialog_unread .nim-dialog--unread
  {
    border-radius: 18px !important;
  }

  .head_nav_btns
  {
    padding-left: 0px;
  }

  input.text.ts_input
  {
    background: rgba(0, 0, 0, 0) url(https://img-fotki.yandex.ru/get/31286/194475847.1/0_107c47_abd05460_orig.png) no-repeat;
    width: 46px!important;
    padding-left: 19px!important;
    border-left: 4px solid transparent;
    background-position: 11px 15px !important;
    border-radius: 0px;
    margin: 0px 0;
    height: 46px;
    transition: 300ms;
  }

  input.text.ts_input:hover
  {
    background: rgba(255,255,255,0.5)  url(https://img-fotki.yandex.ru/get/31286/194475847.1/0_107c47_abd05460_orig.png) no-repeat;
    width: 46px!important;
    padding-left: 19px!important;
    border-left: 4px solid transparent;
    background-position: 11px 15px !important;
    border-radius: 0px;
    margin: 0px 0;
    height: 46px;
    transition: 300ms;
  }

  input.text.ts_input:focus
  {
    color: #000;
    width: 248px!important;
    background: rgba(255,255,255,1) url(https://img-fotki.yandex.ru/get/31286/194475847.1/0_107c47_abd05460_orig.png) no-repeat;
    padding-left: 6px!important;
    border-left: 4px solid transparent;
    background-position: 4px -20px !important;
    transition: 300ms;
  }

  .ts_wrap .input_back_content
  {
    font-size: 0px;
  }

  .ts_cont_wrap
  {
    position: absolute;
    top: 46px;
    left: 179px;
  }

  .head_nav_item_player
  {
    padding-left: 4px;
  }

  top_audio_player .top_audio_player_btn>div
  {
    opacity: 1;
  }

  .top_profile_arrow
  {
    opacity: 1;
  }

  .ts_cont_wrap
  {
    position: absolute;
    top: 46px;
    left: 180px;
    background: #fff;
    width: 248px;
    z-index: 119 !IMPORTANT;
    border-radius: 0px;
    box-shadow: 0 0 23px 12px rgba(0,0,0,0.1);
    overflow: hidden;
    display: none;
  }
/* Новые иконки */

  .top_nav_btn#top_notify_btn .top_nav_btn_icon, .top_nav_btn#top_notify_btn.active .top_nav_btn_icon
  {
    background: url(https://img-fotki.yandex.ru/get/222565/500851312.0/0_143a6d_44b40d22_orig.png) -10px -10px no-repeat !important;
  }

  .top_nav_btn#top_audio .top_nav_btn_icon
  {
    background: url(https://img-fotki.yandex.ru/get/897810/500851312.0/0_147de1_b9875c6c_orig.png) -15px -15px no-repeat !important;
  }

  .top_nav_btn#top_notify_btn.has_notify .top_nav_btn_icon
  {
    background: url(https://img-fotki.yandex.ru/get/222565/500851312.0/0_143a6d_44b40d22_orig.png) -50px -10px no-repeat !important;
  }

  .top_nav_btn:hover
  {
    background-color: rgba(255,255,255,.3);
  }

  .top_nav_btn.active
  {
    background-color: rgba(255,255,255,.2);
  }

  .top_nav_btn .top_nav_btn_icon
  {
    background: url(https://img-fotki.yandex.ru/get/55231/194475847.2/0_1155a1_dfedabbc_orig.png) no-repeat;
  }

  div#top_notify_count
  {
    border: 1px solid rgba(255,255,255,.5);
    background: rgba(0,0,0,.6);
    color: #FFF;
    font-size: 13px;
    height: 13px;
    line-height: 12px;
    left: 22px;
    top: 20px;
  }

  .top_nav_btn#top_notify_btn.active .top_nav_btn_icon
  {
    background-position: -5px -23px;
  }

  .top_nav_btn#top_audio.active .top_nav_btn_icon
  {
    background-position: -65px -53px;
  }

  .top_audio_player .top_audio_player_btn>div
  {
    background-image: url(http://i77.fastpic.ru/big/2016/0606/37/3bb93b8e5af8173a97d085cc67251637.png) !important;
  }

  .top_audio_player .top_audio_player_prev>div
  {
    background-position: -7px -7px !important;
  }

  .top_audio_player .top_audio_player_next>div
  {
    background-position: -80px -7px !important;
  }

  .top_audio_player.top_audio_player_playing .top_audio_player_play>div
  {
    background-position: -56px -5px !important;
  }

  .top_audio_player .top_audio_player_play>div
  {
    background-position: -33px -5px !important;
  }

  .top_audio_player
  {
    height: 46px;
  }



/* Наверх */
  .stl_active.over_fast #stl_bg
  {
    background-color: rgba(0, 0, 0, 0);
  }

  #stl_text
  {
    color: #FFFFFF;
    background: url(https://img-fotki.yandex.ru/get/67777/194475847.0/0_100972_6a402802_orig.png) no-repeat;
  }

/* ОБНОВЛЕНИЕ 2.2 (ЦВЕТ ВСЕГО И ВСЯ) */

/* Меню и блоки  */
  #page_layout
  {
    background: transparent !important;
  }

  #side_bar ol
  {
    background: #FFF;
    padding: 10px 10px 10px 17px !important;
    list-style: none;
    margin: -4px 0 10px -15px;
  }

  #side_bar ol, .page_block, .im-page.im-page_classic .im-page-history-w, .im-page.im-page_classic .im-page--chat-body
  {
    box-shadow: 0px 0px 11px 0px rgba(227,228,232,1);
  }

  #page_header_cont .back
  {
    box-shadow: 0px 0px 11px 0px rgba(0,0,0,0.5);
  }

  .left_menu_nav
  {
    color: #ffffff !important;
    text-shadow: 0px 0px 10px rgba(0, 0, 0, 0.59);
  }

  .left_menu_more:after, .left_menu_more:hover:after
  {
    opacity: 1;
    -webkit-filter: brightness(2);
    -moz-filter: brightness(2);
    -o-filter: brightness(2);
  }

  .im-page.im-page_classic .im-page--chat-input
  {
    border-bottom: 0;
    width: 550px;
    background: #fff;
    z-index: 110;
    margin-left: 0px;
    border-left: 0;
    border-right: 0;
  }
/* Цвет */

  a
  {
    color: var(--colour);
  }
/* Сообщения */
  .ui_progress .ui_progress_bar
  {
    background-image: none;
    background: var(--colour);
  }

  #page_header_cont .back
  {
    background: var(--colour);
    border-bottom: 0px;
  }

  nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--photo, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--photo
  {
    border-color: var(--colour);
  }

  .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--photo .online.mobile:after, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--photo .online.mobile:after
  {
    background-color: var(--colour);
  }

  .nim-dialog.nim-dialog_muted.nim-dialog_selected, .nim-dialog.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected
  {
    background: var(--colour);
  }

  .nim-dialog.nim-dialog.nim-dialog_selected.nim-dialog_hovered, .nim-dialog.nim-dialog.nim-dialog_selected:hover, .nim-dialog.nim-dialog_hovered+.nim-dialog.nim-dialog_selected, .nim-dialog:hover+.nim-dialog.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected.nim-dialog_hovered, .nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected:hover, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog.nim-dialog_selected
  {
    border-top: solid 1px var(--colour);
  }

  .nim-dialog.nim-dialog_muted.nim-dialog_selected .nim-dialog--photo .online.mobile:after, .nim-dialog.nim-dialog_selected .nim-dialog--photo .online.mobile:after
  {
    background-color: var(--colour);
  }

  .im-mess-stack .im-mess-stack--content .im-mess-stack--pname a
  {
    color: var(--colour);
  }

  .nim-dialog.nim-dialog_unread .nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_prep-injected .nim-dialog--unread
  {
    font-weight: 400;
    background: var(--colour) !important;
  }

  .nim-dialog.nim-dialog_muted.nim-dialog_selected .nim-dialog--photo, .nim-dialog.nim-dialog_selected .nim-dialog--photo
  {
    border-color: var(--colour);
  }

  .nim-dialog.nim-dialog_muted.nim-dialog_selected .nim-dialog--unread, .nim-dialog.nim-dialog_selected .nim-dialog--unread
  {
    color: var(--colour);
  }
/* Диалоговые окна */
  .box_title_wrap
  {
    background-color: var(--colour);
  }

/* Элементы интерфейса */
  .button_blue button, .button_gray button, .button_light_gray button, .flat_button, .button_blue button, .button_gray button, .button_light_gray button, .flat_button
  {
    background-color: var(--colour);
  }

  .flat_button:hover
  {
    background-color: var(--colour) !important;
    -webkit-filter: brightness(110%);
    -moz-filter: brightness(110%);
    -o-filter: brightness(110%);
    -ms-filter: brightness(110%);
    filter: brightness(110%);
  }

  .ui_tab_sel, .ui_tab_sel:hover, .ui_tabs .ui_tab_sel, .ui_tabs .ui_tab_sel:hover, .ui_tabs_box .ui_tab_sel, .ui_tabs_box .ui_tab_sel:hover
  {
    border-bottom: 2px solid var(--colour);
  }

  .ui_rmenu_item_sel, .ui_rmenu_item_sel:hover
  {
    border-left: 2px solid var(--colour);
  }

  .page_counter .count, .bp_author, .wddi_text
  {
    color: var(--colour);
  }

  .summary_tab_sel .summary_tab2, .summary_tab_sel .summary_tab2:hover
  {
    background: var(--colour);
  }

  .post_author .author, .wall_module .author, .wall_module .copy_author
  {
    color: var(--colour) !important;
  }

  .top_notify_count
  {
    border: 2px solid var(--colour);
    color: var(--colour);
    background-color: #FFF;
  }

  .audio_play
  {
    background: var(--colour) url(http://vk.com/images/icons/audio_playpause.png) no-repeat 8px 7px !important;
  }

  .audio_page_player .audio_page_player_play .icon
  {
    background-color: var(--colour);
  }

  .page_name, .module_header .header_top, h4, .wall_module a.page_media_link_title, .wall_module span.page_media_link_title, .nim-dialog .nim-dialog--preview-attach, .post_video_title
  {
    color: var(--colour) !important;
  }

  .profile_msg_split .profile_btn_cut_left
  {
    border-right: 1px solid var(--colour);
  }

  .profile_msg_split .profile_btn_cut_right
  {
    border-right: 1px solid var(--colour);
  }

  .upload_progress
  {
    background: var(--colour);
    border: 1px solid var(--colour);
  }

  #side_bar .left_label
  {
    color: #2a5885 !important;
  }

  .nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected.nim-dialog_classic.nim-dialog_unread, .nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected.nim-dialog_hovered, .nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected:hover, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered.nim-dialog_selected+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.nim-dialog, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected:hover+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.nim-dialog.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic.nim-dialog_selected+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog.nim-dialog_selected
  {
    border-top: 1px solid transparent;
  }

  .button_gray button.hover, .button_gray button:hover, .flat_button.secondary.hover, .flat_button.secondary:hover
  {
    background-color: #dfe6ed !important;
  }

  .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--preview.nim-dialog--preview-attach, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--preview.nim-dialog--preview-attach
  {
    color: #fff !important;
  }

  .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--date, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--name, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--name-w, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--text-preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--who, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--date, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--name, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--name-w, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--text-preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--who
  {
    color: #fff !important;
  }
}
