css:
/*
    Marshall, Carter & Dark
    [2014 Wikidot Theme]
    Created for the SCP Foundation GoI Contest of 2014 (MC&D team and Group of Interest) by Randomini. Thanks to Reach, Aelanna and Crayne for the template.
    Edited for the SCP財団 by SCP-JP Technical Team

*/

@-moz-document domain("ko.scp-wiki.net"),domain("sandbox.scp-wiki.kr"),domain("www.scp-wiki.net"),domain("scp-wiki-de.wikidot.com"),domain("ja.scp-wiki.net"),domain("scp-th.wikidot.com"),domain("lafundacionscp.wikidot.com"),domain("scp-wiki.net.pl"),domain("fondationscp.wikidot.com"),domain("scp-wiki-cn.wikidot.com"),domain("scpfoundation.ru"),domain("scp-int.wikidot.com"),domain("05command.wikidot.com")
{

h1, #page-title {
    color: #320042;
}

h1 {
    font-style: italic;
    font-weight: lighter;
}

#page-title {
    font-weight: lighter;
    border-color: #320042;
}

a {
    color: #6C4279;
}

a.newpage {
    color: #4312A5;
}

div#container-wrap {
    background: url('http://ja.scp-wiki.net/local--files/component:MCD-theme/body_bg_MCD2.png') top left repeat-x;
}

/* HEADER */
#header {
    background: url('http://ja.scp-wiki.net/local--files/component:MCD-theme/MCDLogo2-small.png') 40px 6px no-repeat;
    background-size: 270px 135px;
}

#search-top-box-input {
    border: solid 1px #3E2646;
    color: #6E3251;
    background-color: #603B6C;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #282053;
}

#search-top-box-form input[type=submit] {
    border: solid 1px #AF4FCF;
    background-color: #282053;
    background: linear-gradient(to bottom, #371F3D,#282053,#8D6BA9);
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background-color: #87a96b;
    background: linear-gradient(to bottom, #7E608A,#946BA9,#8C3CA6);
}

#login-status {
    color: #C5A3C5;
}

#login-status a {
    color: #E696E6;
}
#login-status ul a {
    color: #C5A3C5;
}

#account-topbutton {
    color: #C5A3C5;
}

#header h1 a {
    color: #190022;
    font-size: 0%;
}

#header h2 span {
    color: #190022;
    font-size: 0%;
}

#top-bar ul li ul {
    border: solid 1px #3d2b1f;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #FFE3FD;
    color: #6c006c;
}

#top-bar ul li ul a, #top-bar a:hover {
    color: #8db600;
}

/* SIDE MENU */
#side-bar .side-block {
    border: 1px solid #8B198B;
}

#side-bar .heading {
    color: #1E0087;
    border-bottom: solid 1px #1E0087;
}

#side-bar div.menu-item.inactive a {
    color: #380A38;
}
#side-bar div.menu-item .sub-text {
    color: #3D1F31;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #220096;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #220096;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #6C4279;
}

/* YUI-TABS */
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background:#320082 url('http://ja.scp-wiki.net/common--theme/shiny/images/yuitabs.png') repeat-x left -1400px; /* selected tab background */
    filter: hue-rotate(258deg);
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background:#FCDBFF url('http://ja.scp-wiki.net/common--theme/shiny/images/yuitabs.png') repeat-x left -1300px;
    filter: hue-rotate(290deg);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #400040;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #6c006c;
}

/* FOOTER */
#footer {
    background: #3d2b1f;
    color: #6C006C;
}

/* SOME NICE BOXES */
div.sexy-box {
    background: #eee;
    border: 1px solid #ccc;
}

div.sexy-box div.image-container img {
    border: 1px solid #9f8170;
}

/* Custom page content classes */
.content-panel {
    border: solid 1px #5d8aa8;
    background-color: #bcd4e6;
}

.content-panel .panel-body {
    background: #f0ffff;
}

.content-panel .panel-footer {
    color: #A30053;
}

.content-panel .content-toc {
    border: solid 1px #5d8aa8;
}

/* Page Rating Module Customizations */
.page-rate-widget-box .rate-points {
  background-color: #6c006c !important;
  border: solid 1px #6c006c;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #f0f8ff;
  border-top: solid 1px #6c006c;
  border-bottom: solid 1px #6c006c;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  color: #6c006c;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #6c006c;
  color: #f0ffff;
}

.page-rate-widget-box .cancel {
  background-color: #6c006c;
  border: solid 1px #6c006c;
}

.page-rate-widget-box .cancel a {
  color: #f0ffff;
}

.page-rate-widget-box .cancel a:hover {
  background: #cdeadc;
  color: #f0ffff;
}

.heritage-rating-module {
  background-color: #6c006c;
  border: solid 1px #ccc066;
}

.scp-image-block {
  border: solid 1px #6c006c;
}

.scp-image-block .scp-image-caption {
  background-color: #f0f8ff;
  border-top: solid 1px #6c006c;
}

/* Forum Customizations */
.thread-container .post .head {
    background-color: #f0f8ff;
    background: linear-gradient(to right, #f0f8ff, #eeecec);
}

/* webkit scrollbar */
::-webkit-scrollbar
{
    border: solid 1px rgba(108, 0, 108, 0.1);
}
 
::-webkit-scrollbar-track
{
    background: rgba(0, 0, 0, 0.1);
}
 
::-webkit-scrollbar-thumb
{
    background: rgba(108, 0, 108, 0.3);
}

/* Responsive Web Design */

/* basic Query for mobile devices */
 
@media (max-width: 767px) {

/* Minimalized Search Bar */
#search-top-box form:before {
    background-color: #282053;
    background: linear-gradient(to bottom, #371F3D, #282053, #8D6BA9);
}

/* Off-canvas Side Bar */
div#side-bar {
    background-color: rgba(108, 0, 108, .9);
}

div#side-bar:before {
    border: 0.2em solid #C5A3C5 !important;
    color: #C5A3C5 !important;
}
}
 
/* Mobile Media Query */
@media (max-width: 479px) { 
#header {
    background-size: 150px 75px;
    background-position: 1em;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) { 
#header {
    background-size: 180px 90px;
    background-position: 1em;
}
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) { 
#header {
    background-size: 210px 105px;
    background-position: 1em;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#header {
    background-size: 240px 120px;
    background-position: 1em;
}
}

/* for the Interwiki */
.scpnet-interwiki-frame {
    -webkit-filter: hue-rotate(281deg);
    -moz-filter: hue-rotate(281deg);
    -ms-filter: hue-rotate(281deg);
    filter: hue-rotate(281deg);
}


.scpnet-interwiki-wrapper {
    width: 17em;
    margin-left: -4.5px;
    margin-top: -5px;
}

.scpnet-interwiki-frame {
    height: 300px;
    width: 17em;
    border: none;
}

@media (min-width: 768px) {
    .scpnet-interwiki-frame {
        height: 300px;
        width: 18em;
    }
    .scpnet-interwiki-wrapper {
        width: 18em;
    }
}
}
