css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#identity-box, #identity-box hbox {
-moz-border-radius: 11px 0 0 11px !important;
background: transparent !important;
-moz-box-shadow: none !important;
}

.searchbar-engine-button {
background: transparent !important;
-moz-box-shadow: none !important;
-moz-border-radius: 10px 0 0 11px !important;
margin-left: -1px !important;
}

#searchbar .autocomplete-textbox-container {-moz-border-radius: 11px !important;}
.searchbar-engine-button .box-inherit.button-box {margin: 0 1px 0 4px !important;}
.searchbar-engine-button .searchbar-dropmarker-image {display: none !important;}
#searchbar .box-inherit.button-box {background: transparent !important;}

#urlbar .autocomplete-textbox-container {-moz-border-radius: 11px 0 0 11px !important;}
#urlbar .autocomplete-history-dropmarker {-moz-border-radius: 0 11px 11px 0 !important;}
#urlbar, .searchbar-textbox {
-moz-appearance: none !important;
-moz-border-radius: 11px !important;
-moz-border-top-colors: threedshadow #666666 !important;
-moz-border-left-colors: threedshadow #888888 !important;
-moz-border-bottom-colors: threedshadow #9C9C9C !important;
-moz-border-right-colors:  threedshadow #777777 !important;
}

#urlbar .autocomplete-textbox-container, 
#searchbar .autocomplete-textbox-container, 
#urlbar .autocomplete-history-dropmarker {background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAKCAYAAAB10jRKAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAALEgAACxIB0t1+/AAAAAd0SU1FB9gMAxQsIYuLBlEAAAAoSURBVAjXJcShEQAgDASwXB+DZFMWRzBBEUQEduEWTrCCGYyA7lb4PYh3BX6nfEhXAAAAAElFTkSuQmCC) !important;
background-repeat: repeat-x !important;
}

.searchbar-engine-image {opacity: .8 !important;}
#urlbar .autocomplete-history-dropmarker {background-color: transparent !important;}
#urlbar .progress-bar {opacity: .6 !important;}
#page-proxy-stack image {margin: 0 -2px 0 2px !important;}

#identity-icon-label {color: black !important;}
#identity-box:not(:hover) #identity-icon-label {font-size: 0 !important;}
#identity-icon-label {text-shadow: 0px 0px 3px white, 1px 1px 3px white, -1px -1px 3px white !important;}

#urlbar[level="high"] {background-color: #99FF99!important; }
#urlbar[level="low"] {background-color: #FFFFCC !important; }
#urlbar[level="broken"] {background-color: #FFCCCC!important; }
