css:
@-moz-document domain("tvmaze.com") {
body {
	background: #333333 !important;
	color: #FFFFFF;
	padding: 0;
	margin: 0;
	font-family: "Helvetica Neue", "Helvetica", Helvetica, Arial, sans-serif;
	font-weight: normal;
	font-style: normal;
	line-height: 1;
	position: relative;
	cursor: default;
}
.breadcrumbs {
	display: block;
	padding: 0.85714rem 1.42857rem 0.85714rem;
	overflow: hidden;
	margin-left: 0;
	list-style: none;
	/* border-style: solid; */
	
	border-width: 0px;
	background-color: #585858;
	border-color: #d4d4d4;
	border-radius: 3px;
}
.breadcrumbs a {
	color: #9C9C9C;
}
.breadcrumbs>*.current {
	cursor: default;
	color: #9C9C9C;
}
#forum #forum-section .description {
	color: #9C9C9C;
}
#forum #thread .comment.primary {
	background: #505050;
	padding: 10px;
	margin-bottom: 20px;
}
blockquote, blockquote p {
	line-height: 1.6;
	color: #9C9C9C;
}
.calendar .dayofmonth {
	font-size: 0.9em;
	padding: 5px;
	border-bottom: 1px dotted #3C948B;
	display: block;
	margin-bottom: 5px;
	background-color: #005B61;
}
h1, h2, h3, h4, h5, h6 {
	font-family: "Helvetica Neue", "Helvetica", Helvetica, Arial, sans-serif;
	font-weight: normal;
	font-style: normal;
	color: rgb(255, 255, 255);
	text-rendering: optimizeLegibility;
	margin-top: 0.8rem;
	margin-bottom: 1rem;
	line-height: 1;
}
.panel {
	border-style: solid;
	border-width: 1px;
	border-color: #dcdcdc;
	margin-bottom: 1.42857rem;
	padding: 1.42857rem;
	background: #4A4A4A;
}
.article-no-img {
	text-align: center;
	background-color: #4A4A4A;
	width: 100%;
}
a {
	color: #6BD6CB;
	text-decoration: none;
	line-height: inherit;
}
.schedule .shows div {
	height: 50px;
	line-height: 50px;
	background: #505050;
	border-bottom: 1px solid #b8bdc5;
	border-left: 1px solid #808997;
	border-top: 1px solid #e3e3e3;
	border-right: 1px solid #e3e3e3;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}
.schedule div.network {
	height: 50px;
	line-height: 17px;
	background: #333333;
	border-bottom: 1px solid #808997;
	border-left: 1px solid #808997;
	border-top: 1px solid #efefe7;
	border-right: 1px solid #efefe7;
}
table {
	background: #737373;
	margin-bottom: 1.42857rem;
	border: solid 1px #dddddd;
}
table tr.even, table tr.alt, table tr:nth-of-type(even) {
	background: #505050;
}
table tr th, table tr td {
	padding: 0.64286rem 0.71429rem;
	font-size: 1rem;
	color: #FFFFFF;
	text-align: left;
}
#airing-today th {
	background-color: #3C948B;
	color: #ffffff;
	font-weight: bold;
}
.panel h1, .panel h2, .panel h3, .panel h4, .panel h5, .panel h6, .panel p, .panel li, .panel dl {
	color: #FFFFFF;
}
#episode-list tr:nth-child(odd) {
	background: #5F5E5E;
}
[data-magellan-expedition], [data-magellan-expedition-clone] {
	background: #5F5E5E;
	z-index: 50;
	min-width: 100%;
	padding: 0px;
}
.sub-nav dt a, .sub-nav dd a, .sub-nav li a {
	text-decoration: none;
	color: #FFFFFF;
	padding: 0.5rem 0.71429rem;
}
table thead {
	background: #3C948B;
}
#episode-list-short td:nth-child(2) {
	color: #FFFFFF;
}
#user_stats #following article span {
	background-color: #6D6D6D;
	display: block;
	padding: 10px;
	font-family: 'Open Sans', sans-serif;
	font-weight: 300;
	text-align: center;
	font-size: 17px;
	border-radius: 10px;
}
#user_stats #watched_list li {
	font-family: 'Open Sans', sans-serif;
	font-weight: 300;
	background: #6D6D6D;
	border-bottom: 1px solid #e3e3e3;
	padding: 10px;
	margin-bottom: 5px;
}
#points article span {
	background-color: #6D6D6D;
	display: block;
	padding: 10px;
	font-family: 'Open Sans', sans-serif;
	font-weight: 300;
	text-align: center;
	font-size: 17px;
	border-radius: 10px;
}
#achievement_list li {
	font-family: 'Open Sans', sans-serif;
	font-weight: 300;
	background: #6D6D6D;
	border-bottom: 1px solid #e3e3e3;
	padding: 10px;
	margin-bottom: 5px;
}
#achievements .achievement_list li {
	font-family: Open Sans, sans-serif;
	font-weight: 300;
	background: #6D6D6D;
	border-bottom: 1px solid #e3e3e3;
	padding: 10px;
	margin-bottom: 5px;
}
.redactor-editor, .redactor-box {
	background: #6D6D6D;
}
.redactor-toolbar {
	position: relative;
	top: 0;
	left: 0;
	margin: 0!important;
	padding: 0!important;
	list-style: none!important;
	font-size: 14px!important;
	line-height: 1!important;
	background: #6D6D6D;
	border: none;
	box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}
.redactor-toolbar li a {
	display: block;
	color: #FFF;
	text-align: center;
	padding: 9px 10px;
	outline: none;
	border: none;
	text-decoration: none;
	cursor: pointer;
	zoom: 1;
	-webkit-box-sizing: content-box;
	-moz-box-sizing: content-box;
	box-sizing: content-box;
}
input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], textarea {
	-webkit-appearance: none;
	background-color: #6D6D6D;
	font-family: inherit;
	border: 1px solid #cccccc;
	box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.1);
	color: rgba(0, 0, 0, 0.75);
	display: block;
	font-size: 1rem;
	margin: 0 0 1.14286rem 0;
	padding: 0.57143rem;
	height: 2.64286rem;
	width: 100%;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	transition: box-shadow 0.45s, border-color 0.45s ease-in-out;
}
#credits .char-names, #appearances .char-names {
	padding: 10px;
	background-color: #22524D;
	margin: 10px 0;
	line-height: 1.4em;
}
#credits li:nth-child(odd), #appearances li:nth-child(odd) {
	background-color: #737373;
}
#credits li:nth-child(even), #appearances li:nth-child(even) {
	background-color: #505050;
}
label {
	font-size: 1rem;
	color: #FFFFFF;
	cursor: pointer;
	display: block;
	font-weight: normal;
	line-height: 1.5;
	margin-bottom: 0;
}
#forum #guidelines ul {
	color: #FFFFFF;
}
#redactor-modal {
	position: relative;
	margin: auto;
	margin-bottom: 20px;
	padding: 0;
	background: #232323;
	color: #FFF;
	font-size: 14px!important;
	font-family: Arial, Helvetica, Verdana, Tahoma, sans-serif;
	box-shadow: 0 1px 70px rgba(0, 0, 0, 0.5);
}
.day.today, .days.today {
	background: #4E4D4D;
}
#calendar-wrap .watched {
	background: #3E4C35;
}
.calendar .entry:hover {
	background: #424242;
}
.redactor-editor blockquote {
	margin-left: 1.6em!important;
	padding: 0;
	text-align: left;
	color: #DCDBDB;
	font-style: italic;
}
#search .starring {
	background-color: #797979;
	padding: 10px;
	border: #22847A solid 1px;
}
.progress {
	background-color: #737373;
	height: 1.78571rem;
	border: 1px solid #ffffff;
	padding: 0.14286rem;
	margin-bottom: 0.71429rem;
}
#tvm_features header h2, #tvm_features section h2 {
	color: #6BD6CB;
}
#follow-list ul li:nth-child(odd), #follow-list ol li:nth-child(odd) {
	background-color: #585858;
	border-bottom: 1px dotted #808997;
}
#profile-settings .nav a, #user-profile .nav a {
	padding: 4px 10px 2px;
	font-size: 1.14286rem;
	display: inline-block;
	background-color: #585858;
}
.zebra-stripes li:nth-child(odd) {
	background-color: #585858;
	border-bottom: 1px dotted #808997;
}
.alert.red {
	background-color: #585858;
	border-color: #fde6e3;
	color: #808997;
}
.calendar .entry .airtime {
	font-weight: normal;
	font-size: 11px;
	line-height: 1.2em;
	color: #38847c;
}
.episodeguide article {
	background: #585858 none repeat scroll 0 0;
	border-bottom: 1px solid #e3e3e3;
	font-family: Open Sans, sans-serif;
	font-weight: 300;
	margin-bottom: 5px;
	padding: 10px;
}
.episodeguide .info {
	background-color: #333333;
	padding: 0px 10px;
	margin-bottom: 10px;
	font-family: Open Sans, sans-serif;
	font-weight: 300;
	font-size: 1rem;
	background: #333333 none repeat scroll 0 0;
}
.watched-eps {
	color: #FFFFFF;
	font-size: 1.19em;
}
#user_stats #activity > #time_watched article {
	text-align: center;
	font-family: 'Open Sans', sans-serif;
	font-weight: 300;
	font-size: 30px;
	line-height: 0.7;
	color: #6BD6CB;
}
#user_stats #activity ul li span {
	color: #6BD6CB;
}
#user_stats #episodes_watched span, #user_stats #hours_watched span {
	color: #6BD6CB;
}
#user_stats #most_watched_networks li {
	color: #959595;
	text-decoration: none;
	display: block;
	-webkit-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	-moz-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	-o-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	-ms-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
}
#user_stats #most_watched_shows li, #user_stats #most_watched_networks li {
	color: #959595;
	text-decoration: none;
	display: block;
	-webkit-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	-moz-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	-o-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	-ms-transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
	transition: padding-left 0.3s ease, font-size 0.3s ease, background-color 0.3s ease;
}
#premium #premium #subscriptions .package .info, #premium #subscriptions .package .info {
	margin-top: 25px;
	background-color: #344240;
}
#calendar .day .show .row {
	background-color: #5f5e5e;
}
#calendar .day .show footer.row {
	background-color: #464646;
}
#calendar-wrap .acquired {
	background: #50492f;
}
#countdown .day .show .row {
	background-color: #585858;
}
.week {
	background-color: #333333;
}
#calendar-wrap .acquired a {
	color: #6BD6CB;
}
#calendar-header, .top-bar, .top-bar ul {
	background-color: #3C948B;
}
table tr.even, table tr.alt, table tr:nth-of-type(2n), .dropdown-pane, #user-tools #tools, #calendar .day .show > div, #countdown .day .show > div, #general-info-panel, table tbody, #user-page #user-info .callout, .card {
	background-color: #505050;
}
.schedule .shows div.followed {
	background-color: #50492f;
}
#user-page #user-info .callout {
	border-radius: 15px;
}
.table > tbody:nth-child(2n) > tr:nth-child(2n), table tbody tr:nth-child(2n) {
	background-color: #858585;
}
}
