css:
@-moz-document domain("www.worldcoinindex.com") {
/* By Tommixoft */

body {
    font-size: 14px;
    font-family: Ubuntu, sans-serif;
    color: #c7c7c7;
    background-color: #404040;
}

.bitcoinName span {
    color: #cecece;
}

table.tablesorter thead tr th, table.tablesorter tfoot tr th {
    background-image: linear-gradient(to bottom,#5a5a5a,#1d1d1d);
    background-size: auto;
}

table.tablesorter .tablesorter-headerRow {
    border: none; 
}

.bitcoin-container {
    box-sizing: border-box;
    width: 180px;
    height: 60px;
    margin: 5px 0;
    border: none;
    background: #404040;
    float: right;
}

.demo-container {
    box-sizing: border-box;
    border: none;
    background: #404040;
}

.coinmarketname {
    color: #fff;
}

section .main_table .main_table_section tfoot {
    background: #353535;
}

section .main_table .main_table_section thead {
    background: #353535;
    color: #b3b4b5;
}

a.market_list_link {
    color: #c5c5c5;
    font-weight: bold;
}

section .main_table .main_table_section tbody tr {
    border-bottom: 1px solid #353535;
}

.green_td {
    color: #24c327;
}

.blue_td {
    color: #66a0ff;
}

div.tab {
    overflow: hidden;
    background-color: #404040;
}

div.tab button.active {
    background-color: #404040;
    border-top: 5px solid #57585a;
    font-family: Ubuntu_B;
    color: #f8f9fa;
}

div.tab button {
    background-color: #4e4e4e;
    float: left;
    border: none;
    outline: none;
    cursor: pointer;
    padding: 14px 16px;
    font-family: Ubuntu_N;
    color: #b3b4b5;
    margin-right: 10px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}

.group_buttons .open-edit-remove-portfolio {
    padding: 10px 15px;
    border: none;
    background: #8c1818;
    color: #f7f8f9;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
}
