@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thestar.com.my") {
  #masthead {
    height: 60px !important;
  }
  #masthead h1 {
    margin: 0 !important;
  }
  #header {
    border-bottom: none !important;
  }
  #multimedia_spotlight,
  #features_specials,
  #ad_leaderboard,
  #ad_midpage,
  #footer_ads,
  #ad_midpage_expandable,
  #ad_rectangular,
  #ad_skyscraper,
  #google_afc { 
    display: none !important;
  }
  #news_opinions,
  #news_stories,
  #opinion,
  #opinion > div {
    width: 100% !important;
    padding-right: 1px !important;
  }
  #opinion div.row {
    clear: none !important;
    float: left !important;
  }
  #opinion div.row + div.row + div.row {
    margin-left: -220px;
  }
  #news_stories .row {
    padding: 10px 0 !important;
  }
  #news_stories .row > div {
    width: 48% !important;
  }
  #main_body {
    background: white !important;
  }
  #news_opinions > ul.navigation,
  #story_links_ads > ul.navigation {
    display: none;
  }
  #most_emailed ul:before {
    content: "Most Emailed:";
    font-size: large;
  }
  #most_viewed ul:before {
    content: "Most Viewed:";
    font-size: large;
  }
  #most_emailed,
  #most_viewed,
  #latest_jobs_list {
    border: 1px solid #CDDEE6 !important;
    margin-top: 10px !important;
    display: block !important;
  }
}
