@-moz-document domain("f5oclock.com") {
/* dark mode */
    
    .dark {
        background-color: rgb(40, 40, 40);
    }
    .dark .tldr .f5oclock,
    .dark .tldr .hot,
    .dark .tldr .trending {
        color: #000;
    }
    .dark .tldr {
        color: rgb(180, 180, 180);
    }
    .dark .article-card {
        background-color: rgb(60, 60, 60);
        color: rgb(180, 180, 180);
    }
    .dark .article-card .f5oclock {
        color: rgb(200, 120, 50);
    }
    .dark .article-card .hot {
        color: rgb(200, 130, 70);
    }
    .dark .article-card .trending {
        color: rgb(200, 140, 100);
    }
    .dark .trending {
        background-color: rgb(80, 80, 80) !important;
        color: rgb(180, 180, 180);
    }
    .dark .hot {
        background-color: rgb(110, 110, 110) !important;
    }
    .dark .f5oclock {
        background-color: rgb(140, 140, 140) !important;
    }
    .dark .btn {
        color: rgb(180, 180, 180);
        background-color: transparent;
    }
    .dark tr.f5oclock .btn {
        color: #000;
        background-color: rgb(200, 120, 50);
    }
    .dark tr.hot .btn {
        color: #000;
        background-color: rgb(200, 130, 70);
    }
    .dark tr.trending .btn {
        color: #000;
        background-color: rgb(200, 140, 100);
    }
    .dark tr.f5oclock,
    .dark tr.f5oclock a {
        color: #000;
    }
    .dark tr.hot,
    .dark tr.hot a {
        color: #000;
    }
    .dark tr,
    .dark tr a {
        color: rgb(180, 180, 180);
    }
    .dark .card-header {
        background-color: transparent;
    }
}
