css:
/*
    Theme: Dark Spacken.net
    Autor: DrCokain
    Version: 1.2
*/
@-moz-document url-prefix("https://admin.spacken.net/") {
.login-page, .register-page {
	background: #111!important;
}

.login-box-body, .register-box-body {
	background: linear-gradient(#148800, #214B00)!important;
	color: white!important;
}
a {
	color: white!important;
}

::selection {
  background: #808080!important; /* WebKit/Blink Browsers */
}

::-moz-selection {
  background: #808080!important; /* Gecko Browsers */
}

body {
	color: #ffffff !important;
}

.skin-green .wrapper, .skin-green .main-sidebar, .skin-green .left-side {
	background-color: #101919!important;
}

.skin-green .sidebar-menu > li > .treeview-menu {
	background: #303939!important;
}

.content-wrapper{
    background-color: #202929 !important;
}
.content-header > h1 {
	color: #ffffff !important;
}

.content-header > .breadcrumb > li > a {
	color: #ffffff !important;
}


.breadcrumb > .active {
	color: #14a200 !important;
}

.box-body {
	background-color: #1A1A1A !important;
}

tr:hover td {background:#555}

.table-striped > tbody > tr:nth-of-type(2n+1) {
	background-color: #111 !important;
}

div.dataTables_length select {
	background-color: #111 !important;
}

div.dataTables_filter input {
	background-color: #111 !important;
}

.pagination > li > a {
	background: #111 !important;
	color: white !important;
}

.box-header {
	color: white !important;
}

.box-header {
	color: white!important;
	background-color: #111!important;
}

.form-group {
	background-color: #111!important;
	margin-bottom: 0px!important;
}

.form-control {
	background-color: #111!important;
	color: white!important;
}

.box-footer {
	background-color: #111!important;
}

.dropdown-menu > li > a:hover {
	color: #000!important;
    background: #555!important;
}

.open > .dropdown-menu {
	background-color: #222!important;
}

#ftpHistorySelect {
	background-color: #111!important;
}

.trBg0 {
	background-color: #333!important;
}

.trBg1 {
	background-color: #222!important;
}

#footerDiv {
	background-color: #111!important;
}

#hostInfoDiv {
	background-color: #222!important;
}

#hostInfoDiv span {
	color: white!important;
}

.navbar-nav > .user-menu > .dropdown-menu > .user-footer {
	background-color: #222!important;
}

#contextMenu {
    -moz-box-shadow: 2px 2px 3px #010101!important;
    -webkit-box-shadow: 2px 2px 3px #010101!important;
    box-shadow: 2px 2px 3px #010101!important;
    background: #111!important;
}
}
