css:
@-moz-document domain("zerohedge.com") {
body {
    background: rgba(0, 0, 0, 0.8);
}
h1 {
    color: #bbb;
}
#header {
    background: #444444;
    color: #bbb;
}
.node--sticky {
    background: rgba(100, 100, 100, 0.3) !important;
}
.node--type-contributor-article {
    background: rgba(30, 30, 30, 0) !important;
}
.view-content {
    background: rgba(30, 30, 30, 0.8) !important;
}
.node {
    background: rgba(30, 30, 30, 0.8);
    color: #999;
}
.node span,
.field--name-comment-body p,
.up-current-score,
.down-current-score  {
    color: #bbb;
}
.sidebar-left-wrapper .block,
.sidebar-right-wrapper .block-wrapper {
    background: rgba(30, 30, 30, 0.6) !important;
    color: #fff;
}
}
