css:
@-moz-document domain(forum.donanimhaber.com) { 
body {
  background-color: #1b2028;
  color: #eeeeee;
  font-family: 'Verdana', Helvetica, 'Sans-Serif' !important;
  font-size: 13px !important;
}

a:link {
  color: #fff !important;
}

a:visited {
  color: #ff7f50;
}
.dhfull .dhorta{
  width: 100%;
  max-width: 1200px;
}


.kl-tablo .kl-basliklari,
header .DH-Logo,
.dhsearchbar {
  background-color: #222831;
}

header .DH-Logo {
  border-bottom: 1px solid #393e46;
}

.menu-new,
.topmenu-new,
.topmenu-new li div,
.topmenu-new li div p a,
.menu-new li:hover > div,
.menu-new li:hover > div p a,
.notification .list,
.notification .list .bildirim {
  background-color: #393e46;
}

.notification .list {
  border: 1px solid #222831;
  color: #FFF;
}

a.bildirim * {
  color: #EEE !important;
}

.notification .list .title {
  background-color: #222831;
  border-bottom: 1px solid #222831;
}

.notification .list .bildirim {
  border-bottom: 1px solid #222831;
  color: #FFF;
}

.notification .list .bildirim:hover {
  background-color: #323944;
}

.notification .list::after {
  border-color: transparent transparent #393e46 transparent;
}

.konuozel-acilir ul li a {
  color: #333 !important;
}

.topmenu-new {
  border-bottom: 0;
  padding: 5px 5px 0 5px;
}

.topmenu-new li a {
  color: #FFF;
  margin-top: 2px;
}

.topmenu-new li a:hover,
.menu-new li a:hover {
  background: #5f6670;
}

.yukarisabitle {
  border-bottom: 1px solid #222831 !important;
}

.yukarisabitle,
.topmenu-new li div,
.menu-new li:hover > div,
.notification .list {
  box-shadow: 0 0 0;
}

.topmenu-new li div {
  border: 1px solid #393e46;
}

.menu-new li a {
  color: #eeeeee;
}

.search .box,
.loginout .cikisbutonu,
.menu-new .search .box {
  background-color: #222831;
  color: #FFF;
  border-color: transparent;
}

.menu-new{
border-bottom-left-radius: 3px;
border-bottom-right-radius: 3px;
}

.search .button,
.menu-new .search .button {
  background-color: #ff7f50;
  color: #FFF;
  border-color: transparent;
}

.notification .counter {
  padding: 2px 5px 3px 7px;
}

.notification .counter.yok {
  background-color: #ff7f50 !important;
}

.user .info a {
  color: #fff;
  background: #222831;
}

.user .info a:hover {
  background: #5f6670;
}

.kl-tablo .kl-basliklari {
  border-bottom: 0;
}

.dhsearchbar,
.kl-basligi {
  display: none;
}

.kl-icerik .kl-icerik-satir {
  background-color: #393e46;
  box-sizing: border-box;
  border-bottom: 1px solid #222831;
}

.kl-icerik .kl-icerik-satir:hover {
  background-color: #323944;
}

.kl-icerik .kl-icerik-satir:hover a {
  color: #ff7f50 !important;
}

.kl-tablo {
  border: 0;
  border-radius: 3px;
}

.kl-img,
.kl-konu,
.kl-yazar,
.kl-cevap,
.kl-okunma {
  border-right: 1px solid #222831;
  line-height: 25px;
}

.kl-konu a{
  font-size: 13px !important;;
}

.forumdizin a {
  color: #FFF;
}

.kl-btn {
  background: #393e46;
  border: 0;
}

.sayfasiralama a {
  background-color: #ff7f50;
  color: #FFF;
}

.sayfasiralama .aktif {
  background-color: #393e46;
}

.sayfanumaralandirma {
  background-color: #222831;
  color: #EEE;
}

.sayfanumaralandirma a {
  color: #8da1c1;
}

.konusayilari .cevapsayisi {
  color: #EEE;
}

.cevapsayisi img {
  background-color: #495360;
  line-height: 25px;
  padding: 2px;
  border-radius: 3px;
}

.cevapsayisi b {
  color: #EEE;
}

.sayfafull .sagtaraf {
  display: none;
}

.sayfafull .soltaraf {
  width: 100%;
}

.ki-giris {
  color: #EEE;
  border-right: 1px solid #222831;
  line-height: 26px;
}

.ki-mesaj {
  color: #EEE;
  line-height: 26px;
}

.ikide1renk .renklendir:nth-child(2n+1) .kl-icerik-satir,
.like-unlike {
  background-color: #393e46;
}

.ikide1renk .renklendir:nth-child(2n) .kl-icerik-satir {
  background-color: #40464f;
}

.ki-cevapsahibi {
  width: 140px;
  border-right: 1px solid #222831;
  padding: 0;
  margin-left: -1px;
}

.ki-kullaniciadi {
  font-size: 14px;
  padding: 0px 10px 5px 10px;
  box-sizing: border-box;
}

.ki-unvan,
.ki-unvanimg {
  padding: 0px 10px 5px 10px;
  border-bottom: 1px solid #222831;
  box-sizing: border-box;
}

.ki-unvan {
  border-bottom: 0;
}

.ki-avatar img {
  background: #ff7f50;
  box-sizing: border-box;
  border-radius: 50%;
  margin: 5px 10px;
}

.ki-mesajsayisi,
.ki-notgonder {
  padding: 0px 10px 5px 10px;
}

.ki-cevaphakkinda {
  padding: 5px 0;
  border-bottom: 1px solid #4d5563;
}

.ki-cevaptarihi .konuacan {
  border: 1px solid #ff7f50;
  color: #ff7f50;
}

.kl-icerik .ikide1renk .kl-icerik-satir:hover a {
  color: inherit !important;
}

.sagayasla .populer-button {
  color: #FFF !important;
  background: #ff7f50;
  border-color: #ff7f50;
  padding: 2px 10px;
}

.like-unlike h1 {
  font-size: 16px;
  padding-bottom: 6px;
  border-bottom-color: #505763;
}

.like-unlike .yorum_cizgi {
  background: #505763;
}

.yorum-hakkinda {
  background-color: #ff7f50;
  height: 25px;
  color: #FFF;
  padding: 0 5px 0 10px;
  padding-top: 5px;
  margin-right: 15px;
  border-radius: 17px;
}

.More-Button {
  background-color: #ff7f50;
  border: 0;
  padding: 5px 0;
  text-transform: uppercase;
  font-weight: bold;
}

.More-Button:hover {
  background: #018a91;
  box-shadow: 0 0 0;
}

.ikide1renk .altkisim-sag a,
.ikide1renk .altkisim-sag {
  color: #333 !important;
}

.ikide1renk:hover .altkisim-sag a {
  color: #333 !important;
}

.quote {
  background-color: #222831;
  border: 0;
  border-radius: 5px;
  padding: 10px 15px;
  color: #FFF;
}

.quote i {
  color: #222831;
  position: relative;
}

.quote i:after {
  content: 'Alıntı:';
  color: #FFF;
  position: absolute;
  left: 0;
}

.dhduyuru .duyurufull {
  background-color: #393e46;
  border: 0;
  color: #FFF !important;
  padding: 5px 15px 8px 15px;
  border-radius: 3px;
  box-sizing: border-box;
  height: 35px;
}

.duyurufull .forumduyuru {
  color: #FFF;
}

.dhorta .dhduyuru {
  border: 0;
}

.soltaraf .populerkonu {
  border-color: #393e46;
  border-radius: 3px;
}

.populerkonu .enust {
  color: #FFF;
  background-color: #222831;
  border-bottom: 1px solid #393e46;
}

.enust h1 {
  color: #FFF;
}

.populerkonu .populerbasliklar {
  border-bottom: 1px solid #222831;
  background-color: #393e46;
}

.populerbasliklar span {
  color: #FFF;
}

.populerkonu .populersatir {
  border-bottom: 1px solid #222831;
  background-color: #393e46;
}

.populersatir .populerbaslik {
  color: #EEE;
}

.dahafazlagoster {
  background-color: #222831;
}

.anaforum {
  background-color: #222831;
}

.toplammesaj .mesaj-2,
.toplammesaj .mesajayarla,
.toplammesaj .baslik-1,
.toplammesaj .baslik-ayarla,
.toplammesaj .baslik-2,
.populerkonu.forum .populerbasliklar .onlinekullanici {
  color: #EEE;
}

.eniyiforumlar {
  background-color: #222831;
}

.dahafazlagoster {
  border-color: #393e46 !important;
}

.sayfafull .soltaraf .hizli-forum-arama .bilgi-satir {
  color: #EEE;
}

.sayfafull .soltaraf .hizli-forum-arama input[type="text"] {
  background-image: linear-gradient(to top, rgba(0, 0, 0, 1) 0%, rgba(0, 0, 0, 1) 100%);
  border: 0;
  border-radius: 3px;
  color: #FFF;
}

.soltaraf .forumicerigi {
  border: 1px solid #393e46;
  border-radius: 3px;
}

.soltaraf .forumicerigibaslik {
  background-color: #222831;
  border-bottom: 1px solid #393e46;
}

.forumicerigibaslik .forumicerigibaslik-1,
.forumicerigibaslik .forumicerigibaslik-2,
.forumicerigibaslik .forumicerigibaslik-3,
.forumicerigibaslik .forumicerigibaslik-4 {
  color: #EEE;
  border-color: #393e46;
  line-height: 20px;
}

.forumicerigi .anakonu {
  background-color: #222831;
  border-color: #222831;
}

.forumicerigi .forumkonusu {
  background-color: #393e46;
  border-bottom: 1px solid #222831;
}

.forumkonusu .forumkonusudosya,
.forumkonusu .forumkonusuicerigi,
.forumkonusu .forumkonusayisi,
.forumkonusu .forumkonususonmesaj {
  border-color: #222831;
  padding: 10px 5px;
}

.sayfafull .kullanicidetay,
.sayfafull .forumistatistik {
  background-color: #222831;
  border-color: #393e46;
  border-radius: 3px;
}

.sayfafull .kullanicidetay h1,
.sayfafull .forumistatistik h1 {
  background-color: #393e46;
  color: #eee;
  border: 0;
}

.forumkonusuicerigi hr {
  color: #5b626d;
  border-top: 0;
}

.forumicerigi .anakonu a {
  color: #EEE;
}

.forumicerigi a:hover {
  color: #ff7f50 !important;
}

.forumicerigi .forumkonusu:hover {
  background-color: #323944;
}

.sagayasla .populer-button {
  color: #444;
}

.onlinekullanici {
  color: #EEE;
}

.tercihlerim,
.beta-tester {
  padding: 0 10px;
}

.logo-new {
  background-color: #222831;
  border-bottom: 0;
}

.profile-new {
  background-color: #222831;
}

.profile-new .left-column .userinfo,
.profile-new .left-column .box.addition,
.profile-new .left-column .box,
.profile-new .left-column .friends,
.profile-new .right-feed .tabs a,
.profile-new .right-feed .boxes,
.profile-new .right-feed .box,
.profile-new .right-feed .box .list .line.imza,
.profile-new .right-feed .choices {
  background-color: #393e46;
}

.profile-new .right-feed .tabs a {
  border-color: #393e46
}

.profile-new .right-feed .tabs a.active {
  background-color: #2e333a;
  border-color: #2e333a;
}

.profile-new .right-feed .boxes .list .line img {
  background-color: #EEE;
  padding: 5px;
  border-radius: 3px;
}
.ikide1renk li{
  margin-bottom: 10px;
}
.ikide1renk .renklendir:nth-child(2n) .kl-icerik-satir, .ikide1renk .renklendir:nth-child(2n+1) .kl-icerik-satir, .like-unlike{
  border-radius: 3px;
}
.beta-tester{
  padding:0 10px 10px 10px !important;;
}
}
