@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("derstandard.at") {
  
  div#documentCanvas, body { background-color: #FDDFC9 !important; }

  div.skyline, div.RubrikenSub
  {
    display: none;
  }

  div.text
  {
    font-size: 9px !important;
  }

  h3 { font-size: 14px !important; }
  h4 { font-size: 10px !important; }

  li#nav_investor ul,
  li#nav_panorama ul,
  li#nav_internationalpolitik ul,
  li#nav_innenpolitik ul,
  li#nav_webstandard ul,
  li#nav_panorama ul,
  li#nav_etat ul,
  li#nav_kultur ul,
  li#nav_wissenschaft ul,
  li#nav_gesundheit ul,
  li#nav_subildung ul,
  li#nav_diestandard ul,
  li#nav_meinung ul
  {
    background-color: #999999 !important;
    color: white !important;
  }

  li#nav_investor ul a,
  li#nav_panorama ul a,
  li#nav_internationalpolitik ul a,
  li#nav_innenpolitik ul a,
  li#nav_webstandard ul a,
  li#nav_panorama ul a,
  li#nav_etat ul a,
  li#nav_kultur ul a,
  li#nav_wissenschaft ul a,
  li#nav_gesundheit ul a,
  li#nav_subildung ul a,
  li#nav_diestandard ul a,
  li#nav_meinung ul a
  {
    color: white !important;
  }

  table.posting08 table.header08 td
  {
    font-size: 9px !important;
    height: inherit !important;
  }

  ul#libottom { width: 99% !important; }
  div#bottomnav
  {
    background-color: #3f3f3f !important;
  }
  div#MutationFuszzeile
  {
    display: none;
  }


}
