css:
@-moz-document url-prefix("https://asianet.no/forum") {
.postbody .content{
    font-size: 20px;
    line-height: normal;
    font-weight: 300;
}
#notification_list_button .badge {
    background-color: #AA0000;
}
.nav-main a:hover {
    color: #bec4c9;
}
.nav-main .dropdown-contents a {
    color: #000;
}
.viewtopic_wrapper,
.forabg,
.forumbg {
    margin-left: -30px;
    margin-right: -30px;
}
.viewtopic_wrapper {
    padding: 15px;
}
ul.forums,
ul.topics {
    padding: 10px 0 5px;
}
#inner-wrap {
    margin-top: 0 !important;
    padding: 15px 30px;
}
.body-layout-Boxed .wrap {
    padding: 0!important;
}
.nav-main .quick-links .dropdown .pointer,
.nav-main .quick-links .dropdown .pointer-inner {
    display: none;
}
.nav-main .quick-links .dropdown .dropdown-contents {
    overflow: hidden;
    overflow-y: auto;
    border: 1px solid transparent;
    border-radius: 0;
    padding: 7px;
    position: fixed;
    max-height: none;
    top: 0;
    left: 0!important;
    bottom: 0;
    width: 200px;
}
.nav-main .quick-links .dropdown .dropdown-contents li {
    border-bottom: 1px solid #dcdcdc;
    padding: 5px 0;
}
.nav-main .quick-links .dropdown .dropdown-contents li.separator {
    display: none;
}
ul#nav-breadcrumbs {
    margin-bottom: 10px;
}
.advanced-search-link,
.action-bar {
    display: inline-block;
}
.advanced-search-link {
    display: inline-block;
    padding-right: 50px;
}
.action-bar .search-box,
.action-bar .pagination {
    float: none;
    display: inline-block;
    line-height: 35px;
    height: 35px;
    vertical-align: middle;
    padding-right: 50px;
}
.action-bar.bottom,
.action-bar.actions-jump {
    margin-bottom: 0;
    display: block;
}
@media (min-width: 1240px) {
    .nav-main .quick-links .dropdown {
        display: block!important;
    }
    .wrap {
        position: relative;
    }
    .nav-main .quick-links .dropdown-trigger {
        display: none;
    }
    .body-layout-Boxed .wrap {
        width: 1000px;
        margin: 0;
        padding-left: 217px!important;
    }
    #inner-wrap {
        background: #FFFFFF;
        overflow: hidden;
        position: relative;
        margin-top: 0 !important;
        z-index: 3;
    }
}
@media (min-width: 1440px) {
    .body-layout-Boxed .wrap {
        width: 1200px;
    }
}
@media (min-width: 1640px) {
    .body-layout-Boxed .wrap {
        width: 1400px;
    }
}
@media (max-width: 600px) {
    .headerbar, .parallax-mirror {
        display: none;
    }
    #inner-wrap {
        padding: 15px;
    }
    .viewtopic_wrapper,
    .forabg,
    .forumbg {
        margin-left: -15px;
        margin-right: -15px;
    }
    h2 {
        margin-bottom: 10px;
    }
    .navbar {
        padding: 5px 15px;
    }
    .topiclist .responsive-show.left-box {
        float: none;
    }
}
@media (max-width: 700px) {
    .advanced-search-link, .action-bar.bar-top {
        display: none;
    }
}
@media (max-width: 770px) {
    ul.topiclist dd {
        display: none;
    }
    .topiclist .pagination {
        float: none;
        text-align: left;
        padding-top: 10px;
    }
    dl.row-item dt .pagination li a {
        display: inline-block;
    }
    .list-inner.left-box {
        float: none;
    }
}
@media (max-width: 770px) and (min-width: 701px) {
    ul.topiclist dt .list-inner {
        margin-right: 0;
    }
}
}
