css:
@-moz-document domain("bitcointalk.org") {
body,
a.nav:hover,
.nav,
.nav:link,
.nav:visited,
#preview_body,
.quote {
    font-family: Century Gothic !important;
    background-color: #212121 !important;
    color: #BBB !important;
    font-size: 14px;
}
.windowbg,
.windowbg2,
.windowbg3 {
    font-family: Century Gothic !important;
    color: #BBB !important;
    font-size: 14px;
    border: 1px solid #131313 !important;
}
.windowbg{
    background-color: #282828 !important;
}
.windowbg2{
    background-color: #212121 !important;
}
.windowbg3{
    background-color: #353535 !important;
}
a:link{
    color: #C5C5C0 !important;
}
a:hover{
    color: #F5F5F0 !important;
    text-decoration: none;
}
a:visited {
    color: #B0E5F1 !important;
}


td,
th,
tr,
.titlebg,
.titlebg a:link,
.titlebg a:visited,
.titlebg2 a:link,
.titlebg2 a:visited,
.maintab_back a:link,
.maintab_back a:visited,
.maintab_active_back a:link,
.maintab_active_back a:visited,
div#bodyarea .maintab_active_back a:link,
div#bodyarea .maintab_active_back a:visited,
div#bodyarea .mirrortab_active_back a:link,
div#bodyarea .mirrortab_active_back a:visited,
.catbg3,
tr.catbg3 td,
.catbg3 a:link,
.catbg3 a:visited {
    color: #CCC !important;
    font-family: Century Gothic !important;
    border:1px!important;
}
.titlebg a:hover,
div#bodyarea .maintab_back a:link,
div#bodyarea .maintab_back a:visited,
div#bodyarea .mirrortab_back a:link,
div#bodyarea .mirrortab_back a:visited,
.catbg a:hover,
.catbg2 a:hover,
.catbg3 a:hover {
    color: #C9F5F1 !important;
    font-family: Century Gothic !important;
}
.catbg,
tr.catbg td,
.catbg2,
tr.catbg2 td,
.catbg3,
tr.catbg3 td,
.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg2,
tr.titlebg2 th,
tr.titlebg2 td,
div#bodyarea .maintab_active_back,
div#bodyarea .mirrortab_active_back {
    color: #aaa !important;
    background-color: #131313 !important;
    background: none;
    font-family: Century Gothic !important;
    
}
input,
textarea,
select {
    background-color: #3b3938;
    color: #aa0;
    font-size: 14px;
    border: none;
    font-family: Century Gothic !important;
}
.post {
    font-size: 14px;
}
.smalltext {
    font-size: x-small;
    font-family: Century Gothic !important;
}
.trustscore {
    color: white !important;
}
.quote {
    color: #999 !important;
    background-color: #111 !important;
    border: 1px solid #252525;
}
.tborder {
    padding: 0px;
    border: 1px;
    background-color: transparent;
}

table {
    display: table;
    border-collapse: collapse;
    border-spacing: 10px;
}

.code {
    color: #bbbba0;
    background-color: #131313;
    border: 0px solid #000000;

}
}
