css:
@-moz-document domain("www.sordum.org") {
::-webkit-scrollbar,
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track-piece {
    background: #1C1C1C !important;
}
::-webkit-scrollbar {
    width: 6px !important;
    height: 6px !important;
}
::-webkit-scrollbar-thumb {
    border-radius: 0px !important;
    box-shadow: inset 0 0 0px #d74937;
    border: 0px solid #d74937 !important;
}
::-webkit-scrollbar-thumb:horizontal {
    background: linear-gradient(to right, #d74937, #d74937, #d74937, #d74937);
}
::-webkit-scrollbar-thumb:vertical {
    background: linear-gradient(#d74937, #d74937, #d74937, #d74937);
}
body,
button,
input,
select,
textarea {
    background-color: #181818;
    color: #EFF0F1;
}
.site-branding:after {
    background-color: #181818;
}
.home-page {
    background-color: #111111;
}
.post.excerpt {
    border-bottom: 1px solid #232323;
}
.article .title {
    color: #CCCCCC !important;
}
.copyrights {
    background: #181818;
    border-top: 1px solid #232323;
}
.main-container {
    background-color: #111111;
}
.carousel a {
    color: #CCCCCC;
}
.frontTitle {
    background-color: #232323;
    color: #CCC;
}
.top {
    background: #232323;
    border-top: 1px solid #232323;
}
.sidebar.c-4-12 a {
    color: #CCCCCC;
}
.title a {
    color: #CCCCCC;
}
.site-branding {
    background: #232323;
}
.primary-navigation a {
    color: #CCCCCC;
    border-color: #232323;
}
.primary-navigation a:hover {
    color: #CCCCCC;
    background: #181818;
}
.menu .current-menu-item a {
    color: #d74937;
    background: #181818;
    border-color: #232323;
}
#navigation ul li:first-child a {
    border-color: #232323;
}
.primary-navigation .menu .current-menu-item > a:after {
    background: #d74937;
}
#s {
    border-color: #232323;
    background-color: #181818;
}
.entry-content p strong span {
    color: #d74937 !important;
}
#commentform {
    background: #181818;
    border-color: #232323;
}
input#author,
input#email,
input#url {
    background: #181818;
    border-color: #232323;
}
#commentform textarea {
    background: #181818;
    border-color: #232323;
}
input#seq_seq {
    background: #181818;
    border-color: #232323;
}
.commentmetadata {
    background: #181818;
    border-color: #232323;
}
.comment-metadata {
    background: #111111;
    border-color: #232323;
}
.comment-author:after {
    background: #111111;
    border-color: #232323;
    border-style: none;
}
.total-comments {
    color: #CCCCCC;
}
.related-posts .meta a {
    color: #CCCCCC !important;
}
.related-posts span {
    color: #CCCCCC !important;
}
.related-posts h3 {
    color: #CCCCCC !important;
}
}
