@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blip.fm") {
 #header,#slot320x250{display:none}
 #frame,#page{width:100% !important; max-width: 1000px !important}
 .col1{width: 73% !important}
 .col2{width: 26% !important}
 #display .bd {width: auto !important}
 #feedme input, #feedme textarea {max-width: 72%}
 .author img.profilePic{height: 75px !important; width: 75px !important;}
 #topstatsContainer {left: 0 !important; position: relative !important; float: right !important; width: auto !important}
}
