css:
@-moz-document domain("pin-cong.com") {
/*** 导航栏 ***/
.nav_bg {
    background-color: #4575b4;
    background-image: none;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
    width: 100%;
    padding: 0px 0px;
}
#navbar > div > a {
    color: #fff;
    font-size: 14px;
    font-weight: bold;
    outline: none;
}
.nav_bar {
    max-width: 9999px;
    width: 80%;
    padding-left: 15px;
}
.nav-item:hover {
    background-color: #4575b4!important;
    border-radius: 0!important;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
}
.nav_person_area:hover {
    background-color: #4575b4!important;
    border-radius: 0!important;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
}
.navbar-user-thumb img {
    width: 25px;
    border-radius: 100%;
    margin-right: 5px;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
}
.select2-container--classic .select2-selection--single {
    border-radius: 0;
    border: none;
}
.select2-container--classic.select2-container--open .select2-selection--single {
    border: none;
}
.select2-container--default.select2-container--focus .select2-selection--multiple {
    border: none !important;
}
.select2-container--classic .select2-selection--single .select2-selection__arrow {
    border-radius: 0;
    border-left: none;
    background-color: transparent;
    background-image: none;
}
.select2-dropdown {
    border-radius: 0;
    border: none !important;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
}
#top-nav-profile-dropdown {
    background-color: #4575b4;
    border-radius: 0;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
}
.top-nav-dropdown li:hover {
    background-color: #4575b4;
    border-radius: 0;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
    text-shadow: 0 0 0 #fff;
}
.nav-tabs {
    border-bottom: none !important;
}

/*** 首页 ***/
.container {
    border: none;
    background-color: #f3f3f3 !important;
    max-width: 9999px;
}
#btn-post-answer-btm\20 {
    font-size: 18px;
    border-radius: 0;
    margin-right: 0;
    width: 100%;
    padding: 8px 10px;
    background: #fff;
    border: none;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
#btn-post-answer-btm\20 > a {
    color: #aaa !important;
}
#post-list .post-body-wrap-list {
    background: #fff;
    padding: 15px !important;
    margin: 15px 0 15px 0;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
#post-list .post-title.title > a:nth-child(1) {
    color: #000;
    font-size: 18px;
}
#post-list .post-title.title > a:hover {
    color: #4575b4;
}
#post-list .post-text-peek {
    font-size: 15px;
}
.btn {
    border-radius: 0;
}

/*** 问题页面 ***/
#post-details .post-list-content {
    font-size: 15px;
}
#post-details > span:nth-child(1) > div {
    background: #fff;
    padding: 15px !important;
    margin: 15px 0 15px 0;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.post-answer-wrap {
    background: #fff;
    padding: 15px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.post-body {
    border-bottom: none!important;
}
.vote {
    border-radius: 0 !important;
}
.view-comment {
    background-color: transparent;
    color: #999;
    cursor: pointer;
    font-size: 100%;
    font-weight: normal;
    border-radius: 0;
}
.view-comment:hover {
    background: #f3f4f5 !important;
    border: none;
}
.user-detail-action-title {
    background: #fff;
    padding: 15px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.subs_wrap {
    margin: 15px 0px !important;
    background: #fff;
    height: 80px !important;
    padding: 10px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.side_qpool_wrap {
    max-height: 9999px;
    background: #fff;
    padding: 15px 5px !important;
    margin: 0;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.more-like-this {
    background: #fff;
    padding: 15px !important;
    margin: 0;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.user-detail-action-cnt {
    padding-left: 0;
}
.user-detail-item {
    background: #fff;
    padding: 15px !important;
    margin: 0;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
a.tag {
    padding: 6px 15px;
    border-radius: 0;
    border: none;
    box-shadow: none;
    background: #e0f3f8 !important;
    color: #4575b4;
}

/*** 探索页面 ***/
.tags-unit {
    background: #fff;
    padding: 15px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
    border: none;
}
.user-details {
    width: 70%;
    background: #fff;
    padding: 15px !important;
    margin: 15px 15px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.user-prof-edit-btn a {
    border-radius: 0;
    padding: 8px 10px;
    background: #4575b4 !important;
    border: none;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.col-md-12.user-details > div.col-md-3 {
    border-bottom: 1px solid #e6e6e6;
}

/*全局搜索*/
#search-results {
    width: 70%;
    background: #fff;
    padding: 15px !important;
    margin: 15px 15px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}

/*** pin-cong.com/i/ ***/
.popuser-wrap {
    background: #fff;
    padding: 15px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.poptag-wrap {
    background: #fff;
    padding: 15px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}

/*** to-feed & to-top ***/
.to-feed {
    border-radius: 0;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
.to-top {
    border-radius: 0;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}

/*** 修改个人资料 ***/
#user-info-edit-form {
    background: #fff;
    padding: 15px !important;
    margin-bottom: 15px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}
#user-info-edit-btn {
    border-radius: 0;
    padding: 8px 10px;
    background: #4575b4 !important;
    border: none;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
}

/*** 问题池 ***/
.post-list {
    background: #fff;
    padding: 15px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24) !important;
}
.post-row {
    border-top: none;
    border-bottom: 1px solid #ECECEC;
    padding-bottom: 5px !important;
    margin-bottom: 5px;
}
}
