css:
@-moz-document domain("www.hydrogenmods.co.uk") {
.p-sectionLinks {
    background: #1f1f1f!important;
    border-bottom: 0px solid #000!important;
}
.p-body {
    background-color: #1f1f1f!important;
    color: white;
}
.block-header {
    color: #2279b7!important;
    border-bottom: 1px solid #1b1b1b!important;
    background: linear-gradient(0deg, #0e0e0e, #313131)!important;
}
.block-container {
    color: #ffffff!important;
    background: #191919!important;
    border-width: 3px!important;
    border-style: solid!important;
    border-color: #000000!important;
    border-radius: 4px!important;
}
.block-footer {
    border-top: 2px solid #000000;
}
.block-tabHeader {
    border-bottom: 2px solid #000000;
}
.contentRow-fader {
    background: linear-gradient(to bottom, rgba(254, 254, 254, 0) 0%, #191919 80%)!important;
}
.input:focus,
.input.is-focused {
    background: #000000!important;
}
.input {
    color: #ffffff!important;
    background: #232323!important;
    border-width: 2px!important;
    border-style: solid!important;
    border-color: #000000!important;
    border-radius: 4px!important;
    display: block!important;
}
::-webkit-input-placeholder {
    /* WebKit, Blink, Edge */
    
    color: #fff!important;
}
:-moz-placeholder {
    /* Mozilla Firefox 4 to 18 */
    
    color: #fff!important;
    opacity: 1;
}
::-moz-placeholder {
    /* Mozilla Firefox 19+ */
    
    color: #fff!important;
    opacity: 1;
}
:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    
    color: #fff!important;
}
::-ms-input-placeholder {
    /* Microsoft Edge */
    
    color: #fff!important;
}
::placeholder {
    /* Most modern browsers support this now. */
    
    color: #fff!important;
}
.block-footer {
    color: #969696!important;
    background: linear-gradient(180deg, #191919, #101010)!important;
}
.block-filterBar {
    border-bottom: 1px solid #000000!important;
    background: linear-gradient(0deg, #000000, #1f1f1f)!important;
}
.fr-toolbar {
    color: #ffffff!important;
    background: #1f1f1f!important;
    border: 2px solid #000000!important;
    border-top: 3px solid #1e72af!important;
}
.en-toolbar {
    color: #ffffff!important;
    background: #1f1f1f!important;
    border: 2px solid #000000!important;
    border-top: 3px solid #1e72af!important;
}
.fr-box.fr-basic .fr-element {
    color: #ffffff!important;
    background-color: #1f1f1f!important;
}
.fr-box.fr-basic .fr-wrapper {
    border: 2px solid #000000!important;
    margin-bottom: 10px!important;
}
.en-box.en-basic .en-element {
    color: #ffffff!important;
    background-color: #1f1f1f!important;
}
.en-box.en-basic .en-wrapper {
    border: 2px solid #000000!important;
    margin-bottom: 10px!important;
}
.siropuChatDateTime {
    background: #000000!important;
    color: #8c8c8c!important;
}
#siropuChatBarUserCount a {
    background: #191919!important;
}
#siropuChatBarUserCount {
    border-left: 2px solid #000000;
}
#siropuChatTabs a.siropuChatActiveTab {
    color: #ffffff;
}
.menu-linkRow {
    color: #ffffff!important;
    background-color: #1f1f1f!important;
}
.menu-row.menu-row--highlighted {
    color: #141414!important;
    background: #1f1f1f!important;
}
.menu-footer {
    color: #505050!important;
    border-top: 2px solid #000000;
    background: linear-gradient(180deg, #1f1f1f, #0c0c0c)!important;
}
.menu-content {
    background: #1f1f1f;
    color: #fff;
}
.menu-header {
    color: #47a7eb;
    border-bottom: 1px solid #1f1f1f;
    background: linear-gradient(0deg, #1f1f1f, #1f1f1f);
}
.menu-row.menu-row--alt {
    color: #ffffff;
    background: #151515;
}
.menu-separator {
    border-top: 2px solid #000000;
}
.userTitle {
    color: #fff;
}
.formRow>dt {
    background: #191919;
    border-right: 2px solid transparent;
    border-color: #000000;
}
.formSubmitRow-controls {
    background-color: #191919;
}
.formSubmitRow-bar {
    background: #191919;
    border-top: 0px solid #cbcbcb;
}
.memberHeader-main {
    background: #1f1f1f;
    border-bottom: 1px solid #ffffff;
}
.block--messages .message,
.block--messages .block-row {
    color: #fff;
    background: #191919;
    border-width: 0px;
    border-style: solid;
    border-color: #000000;
}
.message-cell.message-cell--user,
.message-cell.message-cell--action {
    background: #191919;
}
.blockMessage {
    color: #ffffff;
    background: #191919;
    border-width: 2px;
    border-style: solid;
    border-color: #000000;
}
.button.is-disabled,
a.button.is-disabled {
    color: #ffffff;
    background: #101010;
    border-color: #000000;
}
.blockStatus {
    color: #ffffff;
    background: #191919;
    border-width: 2px;
    border-style: solid;
    border-top-color: #000000;
    border-right-color: #000000;
    border-bottom-color: #000000;
    border-left-color: #000000;
    border-left: 3px solid #f2930d;
    border-radius: 4px;
}
.bbCodeBlock.bbCodeBlock--quote .bbCodeBlock-content {
    background-color: #191919;
}
.bbCodeBlock-title {
    color: #f2930d;
    background: #191919;
}
.bbCodeBlock-content {
    background-color: #191919;
}
.message-responseRow {
    background: #202020;
    border: 2px solid #000000;
}
.tooltip--member .tooltip-content {
    color: #fff;
    background: #191919;
    border: 3px solid #000000;
}
.memberTooltip-header {
    background: #101010;
    border-bottom: 1px solid #000000;
}
.button.button--link,
a.button.button--link {
    background: #121212;
    color: #2577b1;
    border: 2px solid #000000;
}
.tooltip--member.tooltip--top .tooltip-arrow:after {
    border-top-color: #000000;
}
.tooltip--member.tooltip--top .tooltip-arrow {
    border-top-color: #000000;
}
.overlay-title {
    color: #2577b1;
    border-bottom: 1px solid #000000;
    background: linear-gradient(0deg, #000000, #252525);
}
.overlay {
    background: #161616;
    color: #ffffff;
    border-width: 3px;
    border-style: solid;
    border-color: #000000;
}
.p-nav-list .p-navEl.is-selected .p-navEl-link {
    background-color: #151515;
}
.p-navEl.is-selected:hover {
    background-color: #1f1f1f;
}
.likesBar {
    background: #151515;
    border: 1px solid #000000;
}
.tagItem {
    color: #c7c7c7;
    background: #050505;
    border: 1px solid #292929;
}
.button.button--link,
a.button.button--link:hover {
    background-color: #000!important;
}
.tagItem:hover {
    background-color: #000;
}
.filterBar-menuTrigger:hover {
        background-color: #000!important;
}
.node+.node {
    border-top: 2px solid #000000;
}
.block-container>.tabPanes>li>.blocks>.block:last-child .block-outer.block-outer--after, .block-container>.block-body>.blocks>.block:last-child .block-outer.block-outer--after, .block-container>.block-body>.block-row>.blocks>.block:last-child .block-outer.block-outer--after, .overlay-content>.blocks>.block:last-child .block-outer.block-outer--after, .block-container>.tabPanes>li>.block:last-child .block-outer.block-outer--after, .block-container>.block-body>.block:last-child .block-outer.block-outer--after, .block-container>.block-body>.block-row>.block:last-child .block-outer.block-outer--after, .overlay-content>.block:last-child .block-outer.block-outer--after, .block-container>.tabPanes>li>.blockMessage:last-child .block-outer.block-outer--after, .block-container>.block-body>.blockMessage:last-child .block-outer.block-outer--after, .block-container>.block-body>.block-row>.blockMessage:last-child .block-outer.block-outer--after, .overlay-content>.blockMessage:last-child .block-outer.block-outer--after {
    border-top: 2px solid #000000;
}
.block-row.block-row--alt {
    color: #ffffff;
    background: #151515;
}
.block-row.block-row--separated+.block-row {
    border-top: 2px solid #000000;
}
.formRow.formRow--fullWidth+.formRow, .formRow+.formRow.formRow--fullWidth, .formRow.formRow--fullWidth+*>.formRow:first-of-type, .formRow+*>.formRow.formRow--fullWidth:first-of-type {
    border-top: 2px solid #000000;
}
.memberTooltip-separator {
    border-top: 2px solid #000000;
}
.blockLink:hover {
    background-color: #121212!important;
}
.blockLink.is-selected {
    background: #121212;
}
.inputGroup.inputGroup--joined .inputGroup-text {
    border-width: 2px;
    border-style: solid;
    border-color: #000000;
    background: #232323;
}
.inputGroup.inputGroup--joined .input+.inputGroup-text, .inputGroup.inputGroup--joined .input+.input, .inputGroup.inputGroup--joined .inputGroup-text+.input {
    border-left: 1px solid #000000;
}
}
