css:
@-moz-document domain("yts.ag"), domain("yts.am"), domain("yts.lt") {
.ibox-bordered,
[class*='v2'],
[class='hidden-xs hidden-sm'],
#movie-comments,
#screenshots,
#movie-related,
#background-overlay,
.header-slogan,
.nav-links,
.modal-title,
footer,
.addthis_sharing_toolbox,
.addthis-smartlayers {
	display: none;
}
.col-md-offset-1 {
	margin-left: auto;
}
.col-md-push-9 {
	left: auto;
}
.col-md-10 {
	width: auto;
}
#background-image,
.modal-container {
	background: #1d1d1d !important;
}
.modal-content .modal-torrent {
	border-right: 1px solid #000;
}
#movie-sub-info,
#movie-tech-specs,
#movie-bottom {
	margin-top: auto;
}
.main-nav-links {
	margin-right: auto;
}
#movie-sub-info p {
	margin: 0 0;
}
#crew {
	margin: -25% 0 0 45%;
	margin-top: -25%;
	margin-right: 0px;
	margin-bottom: 0px;
	margin-left: 45%;
	position: absolute;
	width: auto;
}
.modal-content {
	padding: 1em;
}
}
