css:
@-moz-document domain("torrentfreak.com") {
.entry-content,
.entry-lead,
.entry-summary,
.author-box,
.archive-header h1,
.stats-number {
	color: gray;
}
#main,
.entry-title,
.entry-kilo .entry-title-wrapper,
.entry-micro .entry-title-wrapper,
.entry-nano .entry-title-wrapper {
	background: #333333;
}
body,
html {
	border-top: 2px solid #ff4794;
	border-bottom: 2px solid #ff4794;
}
#main {
	margin: 20px auto;
}
#logo {
	padding: 21px 8px;
}
#wrap-page,
#wrap-main,
body,
html,
.entry-pico:nth-child(6n+4),
.entry-pico:nth-child(6n+5),
.entry-pico:nth-child(6n+6) {
	background: #222222;
}
.entry-title {
	color: #ff4794;
}
.entry-kilo,
.entry-micro,
.entry-nano {
	border-bottom: 2px solid #e0005e;
}
.pbx-alt {
	background: #424242;
}
#sidebar,
#topbar,
#feature {
	display: none;
}
.archive-header {
	border-bottom: 2px solid #222;
}
#stick-logo {
	position: absolute;
}
table.css > tbody tr:nth-child(2n) {
	background: #222;
}
table.css > tbody tr > td,
table.css > thead th,
table.css > tfoot {
	border: 1px solid #222;
}
table.css > thead th,
table.css > tfoot {
	background: #00000080;
}
#main {
	min-height: auto;
}
}
