css:
@-moz-document domain("hw.cti.p.lodz.pl") {

body {
	background-color: #3e3e3e;
	color: #f0f0f0;
}
.topic-entry {
	padding: .5rem 1rem;
}
.topic-title {
	font-size: 1.2rem;
}
.mb-2 {
	margin-bottom: 0.22rem!important;
}
.fa-exclamation-circle {
	color: #aaaaaa !important;
}
.btn-secondary {
	background-color: #404040;
	border-color: #505050;
}
.btn-secondary:hover {
	color: #fff;
	background-color: #707070;
	border-color: #707070;
}
.progress {
	background-color: #404040 !important;
	height: .3rem;
}
.progress-bar bg-danger {
	background-color: black !important;
}
.progress-bar {
	background-color: #1F4E9F !important;
}
.modal-header {
	color: rgba(0, 0, 0, 0);
	border: none;
	border-bottom: 1px solid #0099ff;
}
.modal-footer {
	position: absolute;
	border-top: none;
}
.pre {
	color: #0099ff !important;
}
.container {
	width: 100% !important;
}
.navbar {
	padding: .4rem .6rem;
}
.navbar-light .navbar-nav .nav-link {
	color: rgba(255, 255, 255, 0.5);
}
.navbar-light:hover .navbar-nav .nav-link {
	color: rgba(255, 255, 255, 1);
}
.modal-content {
	background-color: #333333;
}
.navbar-light .navbar-brand,
.navbar-light .navbar-brand:focus,
.navbar-light .navbar-brand:hover,
.navbar-light .navbar-toggler,
.navbar-light .navbar-toggler:focus,
.navbar-light .navbar-toggler:hover {
	color: white;
}
.breadcrumb,
.bg-faded {
	background-color: #232323;
}
.card-block {
	background-color: #171717;
}
.card-footer,
.card-header {
	background-color: #2b2b2b;
}
.card {
	border: 1px solid rgb(69, 130, 236);
	background-color: #232323;
}
.nav-tabs .nav-item.show .nav-link,
.nav-tabs .nav-link.active {
	color: #ffffff;
	background-color: #171717;
	border-color: #4582ec;
}
#sendReply {
	font-size: 3rem;
	font-weight: 100;
}
#sendReplyModal .modal-dialog {
	margin-top: 48vh;
}
.replaceModal {
	background-color: rgba(0, 0, 0, 0.5);
}
.replaceModal .modal-dialog {
	margin-top: 150px;
}
.card-header {
	border-bottom: 1px solid rgb(69, 130, 236);
}
.list-group-item-hover:hover {
	color: #ffffff;
	background-color: #575757;
}
.sourceCode {
	color: white;
}
code {
	background-color: #000000;
}
.list-group-item-action {
	color: #ffffff;
}
.list-group-item {
	background-color: #2b2b2b;
}
.list-group-item:hover {
	color: #fff;
	background-color: #1b1b1b;
}
.form-control {
	background-color: #2d2d2d;
}
.custom-task-file-control {
	background-color: #0c0c0c;
}
.custom-task-file-control::before {
	background-color: #000;
}
.topic-entry-active .list-group-item,
.topic-entry-active:hover .list-group-item {
	color: #ffffff;
}
.table-striped tbody tr:nth-of-type(odd) {
	background-color: rgba(255, 255, 255, 0.1);
}
.table-active,
.table-active>td,
.table-active>th,
.table-hover .table-active:hover,
.table-hover .table-active:hover>td,
.table-hover .table-active:hover>th,
.table-hover tbody tr:hover {
	background-color: #202020;
}
}
