body {
    background: #4f5469;
    color: #e6e6e6;
    font-family: 'Roboto Slab', serif;
    font-size: 14px;
    font-weight: 400;
    line-height: 1.4em;
    padding: 0;
    margin: 0;
    -webkit-font-smoothing: antialiased;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.004);
}

.board {
    background: transparent;
    border-top: 0px solid #ccc;
    margin-bottom: 50px;
    padding-bottom: 25px;
}

.is_index .board {
    padding-bottom: 50px;
}

a,
a:visited,
.quoteLink,
.quotelink,
.deadlink,
div#boardNavMobile .pageJump a,
.persistentNav .pageJump a,
.summary a.replylink,
div.post div.postInfo span.postNum a:visited,
div.post div.postInfo span.postNum a.replylink,
#totop span,
#tobottom span {
    color: #7678ED !important;
    text-decoration: none!important;
    font-family: "Roboto Mono";
}

a.summary{
    margin: -20px auto 20px auto;
    display: block;
    width: 1000px;
    text-align: center;
    background: #b3b4f147;
    padding: 8px;
    border-radius: 5px;
    color: #d4d4d4 !important;
    font-weight: 600;
    font-size: 14px;
}

a.summary:hover {
    color: #ED1C24;
}

a:hover,
.quoteLink:hover,
.quotelink:hover,
.deadlink:hover,
.summary a.replylink:hover,
.persistentNav .pageJump a:hover,
div#boardNavMobile .pageJump a:hover,
div.post div.postInfo span.postNum a:hover,
.posteruid .hand:hover,
#totop span:hover,
#tobottom span:hover {
    color: #ED1C24 !important;
    text-decoration: underline !important;
}

.postInfo a.postMenuBtn,
.postInfo a.postMenuBtn:hover {
    color: #c5c9d8!important;
    text-decoration: none!important;
}

div.postContainer {
    display: block;
    width: 1200px;
    margin: 2px auto 0 auto;
}

.thread {
    margin-top: 40px!important;
    padding-top: 40px;
    border-top: 6px solid #00000010;
}

.thread:first-child {
    padding-top: 0;
    border-top: none;
}

.md-plus-btn {
    border-radius: 50%;
    font-size: 24px;
    height: 56px;
    margin: auto;
    min-width: 56px;
    width: 56px;
    padding: 0;
    overflow: hidden;
    box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 0 1px 1px 0 rgba(0, 0, 0, .24);
    line-height: normal;
    position: fixed;
    right: 25px;
    bottom: 25px;
}

div.boardBanner>div.boardTitle {
    margin-top: 90px;
    font-size: 60px;
    color: #fff;
    margin-bottom: 100px;
    font-family: 'Oleo Script Swash Caps', cursive;
    font-weight: normal;
    letter-spacing: 0;
}

:root.tomorrow div.boardTitle{
    font-family: 'Oleo Script Swash Caps', cursive;
    color: #fff;
}

:-ms-input-placeholder {
    color: rgba(0, 0, 0, .35)
}

::-webkit-input-placeholder {
    color: rgba(0, 0, 0, .35)
}

:-moz-placeholder {
    color: rgba(0, 0, 0, .35)
}

::-moz-placeholder {
    color: rgba(0, 0, 0, .35)
}

#togglePostFormLink,
.ad-plea,
#blotter tfoot td,
#ctrl-top,
div.post div.postInfo span.postNum,
div.pagelist div.pages {
    color: transparent;
}

table.postForm>tbody>tr>td:first-child,
.thread-stats {
    color: #d06453;
    font-family: 'Oleo Script Swash Caps', cursive;
    font-size: 19px;
}

.postInfo .backlink a.quotelink,
.postInfo .backlink a.quotelink:hover {
    color: #7678ED !important;
}

blockquote>span.quote {
    color: #C4F1BE;
}

input[type="text"] {
    border: none!important;
    border-bottom: 1px solid rgba(0, 0, 0, .12)!important;
    font-size: 14px!important;
    font-family: "Helvetica", "Arial", sans-serif;
    padding: 4px!important;
    -webkit-transition: border-bottom-color 0.2s;
    transition: border-bottom-color 0.2s;
}

input[type="text"]:focus,
#quickReply input[type="submit"]:focus {
    border: 0!important;
    border-bottom: 1px solid #c5c9d8!important;
}

.tomorrow .extPanel,
.recaptcha_input_area #recaptcha_response_field {
    border: 0!important;
}

input[type="submit"],
button {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 30px;
    min-width: 75px;
    display: inline-block;
    text-transform: uppercase;
    outline: none;
    cursor: pointer;
    background: #D64933;
    color: #fff;
    font-size: 14px;
    font-family: 'Roboto',
    'Helvetica',
    'Arial',
    sans-serif;
    line-height: 1em;
    margin-left: 8px;
}

#qrCaptchaContainerAlt {
    margin-bottom: 8px;
}

table {
    border-spacing: 5px;
}

#postForm {
    width: 600px;
}

#postForm input[type="text"] {
    margin: 4px 0;
}

#postForm td:first-child {
    text-align: right;
    padding-right: 0 25px;
}

textarea {
    border: 0!important
}

textarea:focus {
    border: 0!important
}

table#postForm td {
    background: transparent;
    border: 0;
    font-weight: normal;
}

#toggleMsgBtn {
    margin-left: 5px!important;
}

.rules {
    text-align: center;
}

#qrHeader {
    background: #676767!important;
    color: #fff!important;
}

#qrHeader a {
    color: #fff!important;
}

#search-box {
    height: inherit;
    line-height: inherit;
    margin: 0;
    padding: inherit;
}

#ctrl-top {
    padding-bottom: 10px;
    border-top: 1px solid #ddd;
    padding-top: 10px;
    text-align: center;
}

.reply:target,
.reply.highlight {
    background: #eee!important;
    padding: 10px!important;
    border: 0!important;
}

.navLinks+hr,
.open-qr-wrap,
.board hr,
#bannerCnt,
#ctrl-top+hr,
#ctrl-top>hr {
    display: none;
}

.navLinks {
    padding: 10px 0px;
    color: transparent;
    width: 1200px;
    margin: auto;
}

.navLinks label {
    color: #c5c9d8;
}

.navLinks label+span {
    color: #000;
}

.navLinksBot {
    margin-top: 30px;
    text-align: left;
}

div#boardNavDesktop,
div#boardNavDesktopFoot,
div#boardNavMobile {
    background: #222;
    padding: 2px 5px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .08), 0 3px 1px -2px rgba(0, 0, 0, .06), 0 1px 5px 0 rgba(0, 0, 0, .04);
    margin-bottom: 25px;
    color: transparent;
    font-size: 14px;
}

div#boardNavMobile {
    color: #000;
    border: 0;
}

div#boardNavDesktopFoot {
   margin: 0px !important;
   padding: 10px 5px;
}

div#boardNavDesktop a,
div#boardNavDesktopFoot a {
    margin-left: -3px;
    margin-right: -3px;
    padding: 0;
}

.persistentNav .pageJump a {
    margin: 0;
    padding-right: 5px;
}

div.pagelist {
    background: #fff;
    margin: 70px auto;
    border: 0;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14),
    0 3px 1px -2px rgba(0, 0, 0, .2),
    0 1px 5px 0 rgba(0, 0, 0, .12);
    padding: 8px 10px;
    border-radius: 2px;
    display: block;
    max-width: 600px;
    text-align: center;
    float: none !important;
}

div.pagelist>div {
    float: none;
    display: inline;
}

div#absbot {
    background: #ddd;
    color: #787878;
}

div#absbot #footer-links a:hover {
    text-decoration: underline!important;
}

div#absbot #footer-links a {
    text-decoration: none !important;
    color: #4b40ae !important;
}

.bottomCtrl {
    display: none
}

.ad-plea-bottom+hr {
    display: none;
}

div#boardNavDesktopFoot {
    margin-bottom: 0;
}

div.post,
.extPanel,
div.reply,
.dd-menu ul {
    background: #434759;
    /* box-shadow: 1px 1px 0px 0 rgba(0, 0, 0, 0.23),
    5px 11px 12px 0px rgba(0, 0, 0, .2),
    2px 4px 5px 0 rgba(0, 0, 0, .12); */
    border-bottom: 1px solid #00000050;
    border: none;
    overflow: visible;
}

div.pagelist div.cataloglink {
    border: 0;
}

div.post {
    /* max-width: 75%; */
    margin: 0;
    padding: 10px 18px 50px;
    border-radius: 10px;
}

div.reply{
    border-radius: 1px;
    padding: 10px 18px 50px;
    width: 95%;
}

.dd-menu li {
    padding: 6px 10px!important;
    border: 0!important;
}

.dd-menu li:hover {
    background-color: #eee!important;
}

#quote-preview {
    border: 0!important;
    padding: 10px!important;
}

div.op {
    display: block;
    max-width: none;
}

.fileText {
    color: #999;
    font-size: 12px;
    margin-top: 2px;
}

.op .fileText {
    margin-top: 0px;
    margin-bottom: 10px;
}

.fileText a {
    color: #76c0ed !important;
    text-decoration: none;
}

.posteruid,
.dateTime {
    color: #999;
}

.postInfo input[type="checkbox"] {
    display: none;
}

div.sideArrows {
    display: none;
}

hr {
    border: 0;
    border-bottom: 1px solid #757575;
}

div.post:after {
    display: block;
    content: ' ';
    clear: both;
}

div.post div.file .fileThumb {
    float: left;
    margin-left: -30px;
    margin-right: 22px;
    margin-top: 10px;
    margin-bottom: 10px;
    z-index: 9;
}

div.post div.file .fileThumb img {
    object-fit: cover;
}

div.reply div.file .fileThumb img:not(.expanded-thumb) {
    border-radius: 100%;
    width: 100px!important;
    height: 100px!important;
}

div.op div.file .fileThumb img:not(.expanded-thumb) {
    border-radius: 150px;
    width: 150px!important;
    height: 150px!important;
}

.postMessage {
    margin-left: 25px;
    margin-top: 12px;
}

#boardNavDesktop::after {
    content: ' ';
    display: block;
    clear: both;
}

span.summary {
    border-radius: 7px;
    background-color: #f5f5f512;
    max-width: 800px;
    display: block;
    margin: -48px auto 8px auto;
    /* box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12); */
    border-top: 1px solid rgba(0, 0, 0, 0.1);
    text-indent: 10px;
    padding: 10px 0;
    text-align: center;
    color: #fff;
}


/* Fixes */

input:focus,
textarea:focus {
    border: 0!important;
}

#togglePostFormLink a::after,
.navLinksBot [href="#top"]::after,
#totop::after {
       background: #D64933;
       box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12),
       0 1px 1px 0 rgba(0, 0, 0, .24);
       position: fixed;
       right: 25px;
       bottom: 25px;
       display: inline-block;
       font-size: 32px;
       color: #fff;
       content: '+';
       width: 52px;
       height: 52px;
       line-height: 50px;
       vertical-align: middle;
       border-radius: 50%;
       font-weight: normal;
       text-align: center;
       cursor: pointer;
}

#togglePostFormLink a::after {
    z-index: 1;
    box-shadow: none;
}

.boardList {
    font-size: 0;
}

.boardList a {
    font-size: 12px;
    display: inline-block;
    padding: 0px 2px!important;
    margin: 0!important;
    border-right: 1px solid #434759b8;
}

.postMenuBtn:hover {
    text-decoration: none!important;
}

div.post div.postInfo span.nameBlock span.name {
    color: #d64933;
    /* font-weight: bold; */
}

div.post div.postInfo {
    display: block;
    width: 100%;
    font-family: "Roboto Mono" !important;
    font-weight: 500;
    font-size: 13px !important;
}

.fa::before{
    font-size: 16px;
}

.hide-thread-button,
.hide-reply-button {
    float: right;
    margin: 10px 10px 10px -10px;
    padding: 3px 2px 1px 2px;
    background: #fff;
    border-radius: 50%;
    width: 20px;
    text-align: center;
}

:root.tomorrow .qr-link {
    border-color: rgb(25, 27, 31) rgb(25, 27, 31) rgb(10, 12, 16);
    background: #434759;
    border-width: 2px;
    color: #fff !important;
}

select,
input, textarea {
    border-radius: 3px !important;
    padding: 6px 12px !important;
    margin: 0px 3px !important;
}

#ctrl-top {
    padding-bottom: 0;
    border-top: 0px solid #ddd;
    padding-top: 15px;
    text-align: center;
}

.globalMessage {
    color: #ED1C24;
    text-align: center;
    text-transform: uppercase;
    font-size: 13px;
}

div.post div.postInfo span.subject {
    color: #ED1C24;
    font-weight: bold;
    font-size: 1.2em;
}

.nameBlock.capcodeMod span.name,
span.capcodeMod a span.name,
span.capcodeMod span.postertrip,
span.capcodeMod strong.capcode {
    color: #e880e8!important;
}

.extButton.threadHideButton {
    float: right;
    margin-right: -5px;
    margin-top: 2px;
}

:root.yotsuba .dialog {
    background-color: #24262f;
    border-color: transparent;
}

#embedding {
    padding: 0;
    position: fixed;
}

.dialog {
    border: 1px solid #00000030;
    display: block;
}

#embedding .jump {
    margin: 0;
    text-decoration: none;
    float: left;
    position: absolute;
}

div#embedding a {
    background: #ffffff1a;
    display: inline-table;
    width: 20px;
    text-align: center;
    height: 20px;
    vertical-align: bottom;
    font-weight: 900;
    font-size: 16px;
}

:root.yotsuba .inline {
    border-color: #00000061;
    background-color: rgba(0, 0, 0, 0.22);
    padding: 8px;
    margin: 10px 0;
    max-width: 100%;
}

:root.yotsuba #header-bar.dialog {
    background-color: #222;
}

:root.yotsuba #header-bar,
 :root.yotsuba #notifications {
