css:
@-moz-document domain("e-rock.ru") {
body, .alt, .alt2, .alt2Active, .alt1, #wrap, .catbot, .page, .panelsurround, .panel, input, select, div#wrapright, div#wrapleft, div#topnav, div#nav, div#header, .alt1, .alt1Active,legend
{
	background: #1f1f1f url(data:image/png;base64,)!important;
	color: #ccc;
	border-color: #555 !important;
	border-width: 1px;
}

a:link
{
	color: #79d079;
}

.tborder, .tcat, .tcat2, .vbmenu_control
{
	background: #222;
	border: none;
}

.thead
{
	background: #3C667E none;
}

table.tborder thead tr td.tcat table tbody tr td img, div.page div div.catbot img, div#wrapright img.fright, div#wrapright img.fleft
{
	display: none !important;
}

div span.smallfont font b, a:visited, body_avisited
{
	color: #964 !important;
}

#nav
{
	filter: invert(90%);
}
}
