css:
@-moz-document domain("coinlib.io") {
/* 
 * Coinlib - Simple Dark v1.0.3
 *
 * Feel free to edit and release other versions!
 *
 */

/* Logo */
.page-header .page-header-top .page-logo .logo-default,
twitterwidget {
    filter: invert(1);
}

/*
 * Backgrounds
 */
.page-header,
.page-wrapper .page-wrapper-middle,
.dropdown-menu,
.page-header .page-header-top .top-menu .navbar-nav>li.dropdown.open .dropdown-toggle, .page-header .page-header-top .top-menu .navbar-nav>li.dropdown:active .dropdown-toggle, .page-header .page-header-top .top-menu 	.navbar-nav>li.dropdown:focus .dropdown-toggle,
.bg-grey-cararra,
.coin-header .coin-graph-controls,
.news-widget-readmore,
input {
    background: #333 !important;
}

.widget-bg-color-white,
.portlet.light,
.table-scrollable>.table {
    background: #444 !important
}

.btn.default:not(.btn-outline) {
    background: #aaa !important;
}

.btn.green:not(.btn-outline) {
    background: #1f585d !important;
}

.btn.btn-outline.green.active, .btn.btn-outline.green:active, .btn.btn-outline.green:active:focus, .btn.btn-outline.green:active:hover, .btn.btn-outline.green:focus, .btn.btn-outline.green:hover {
    background: #1f585d !important;
}
.bootstrap-switch .bootstrap-switch-handle-off.bootstrap-switch-warning, .bootstrap-switch .bootstrap-switch-handle-on.bootstrap-switch-warning {
    background: #bb9910 !important;
}

.page-prefooter,
.portlet.box>.portlet-body {
    background: #444 !important;
}

/* Account Hover */
.page-header .page-header-top .top-menu .navbar-nav>li.dropdown-dark .dropdown-menu {
    background: #444 !important;
}

/* Hover Backgrounds */
.dropdown-menu>li>a:hover {
    background: #373e47 !important;
}
.page-header .page-header-menu .hor-menu.hor-menu-light .navbar-nav>li.open>a, .page-header .page-header-menu .hor-menu.hor-menu-light .navbar-nav>li:hover>a {
    background: #55616f!important;
}

.table.table-light.table-hover.coinlist>tbody>tr>th:hover,
.table.table-light.table-hover.coinlist>tbody>tr:hover,
.dropdown-menu>li.active:hover>a, .dropdown-menu>li.active>a, .dropdown-menu>li:hover>a {
    background: #444 !important;
}
.dropdown-menu>li>a:focus, 
.table.table-light.table-hover>tbody>tr:hover>td, .table.table-light.table-hover>tbody>tr>td:hover, .table.table-light.table-hover>tbody>tr>th:hover,
.page-header .page-header-top .top-menu .navbar-nav>li.dropdown>.dropdown-toggle:active, .page-header .page-header-top .top-menu .navbar-nav>li.dropdown>.dropdown-toggle:focus, .page-header .page-header-top .top-menu .navbar-nav>li.dropdown>.dropdown-toggle:hover,
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover,
.nav>li>a:focus, .nav>li>a:hover,
.mt-comments .mt-comment:hover {
    background: #333 !important;
}

.table-hover>tbody>tr:hover, .table-hover>tbody>tr:hover>td, .table.table-light.table-hover.coinlist>tbody>tr:hover:not(:first-child) {
    background: #333 !important;
}

/*
 *Borders
 */
.btn.default:not(.btn-outline) {
    border-color: #aaa !important;
}

.nav-tabs,
.portlet>.portlet-title,
.table.table-light>thead>tr>th,
.table.table-light>tbody>tr>td,
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover,
.nav-tabs>li>a:hover,
.news-post { 
    border-color: #333 !important;
}
.table .byexchange-row,
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    border-color: #333 !important;
}


/* Account Hover */
.page-header .page-header-top .top-menu .navbar-nav>li.dropdown-dark .dropdown-menu:after {
    border-bottom-color: #444 !important;
}

/* Login */
.form .form-actions, .portlet-form .form-actions {
    border-top: 1px solid #333 !important;
}



/* Modal/Popup */
.modal-content {
    background: #333 !important;
    color: #ddd !important;
}
.btn-default {
    background: #444 !important;
    color: #ddd !important;
    border-color: #333 !important;
}
.form-control {
    background: #444 !important;
    border-color: #333 !important;
}
.form-control, output {
    color: #ddd !important;
}
.bootstrap-switch .bootstrap-switch-label {
    background: #333 !important;
}
.input-group-addon {
    background: #444 !important;
}
.modal .modal-header {
    border-color: #444 !important;
}
.bootstrap-select .btn,
.input-group-addon {
    border-color: #333 !important;
}
.bootstrap-switch,
.dropdown-menu {
    border-color: #444 !important;
}
.modal-body a,
.input-group-addon,
.close {
    color: #ddd !important;
}


/* Coin Page - Misc. */

.news-widget:not(.twitter-widget) {
    background: #333 !important;
    border-color: #333 !important;
}

/* Coin Page Charts */
.highcharts-root > rect,
.highcharts-button-disabled text {
    fill: #333!important;
}
.highcharts-label > rect {
    fill: #444 !important;
}

.highcharts-range-input text > tspan,
.highcharts-button-normal text,
.highcharts-button-pressed text {
    fill: #ccc !important;
}

.highcharts-button rect {
    fill: #444 !important;
}

.highcharts-series > path, 
.highcharts-series rect {
    fill-opacity: 0.4 !important;
    stroke-opacity: 0.25 !important;
}
.small-chart rect {
    fill: #444 !important;
}
.small-chart path {
    fill-opacity: 1 !important;
	stroke-opacity: 1 !important;
}


/*
 * Font Colors
 */
body,
.font-grey-mint,
.table.table-light>tbody>tr>td,
.btn.red:not(.btn-outline), 
.btn.blue-hoki:not(.btn-outline),
.news-widget-body,
.portlet.light>.portlet-title>.caption,
#coin-main-price,
.widget-thumb .widget-thumb-body .widget-thumb-body-stat {
    color: #ccc !important;
}
a,
.page-header .page-header-menu .hor-menu.hor-menu-light .navbar-nav>li.open>a, .page-header .page-header-menu .hor-menu.hor-menu-light .navbar-nav>li:hover>a {
    color: #32c5d2 !important;
}
.label, 
.btn.default:not(.btn-outline) {
    color: #000 !important;
}

table tr td a[id*="link"] {
    color: #ccc !important;
}
g.highcharts-legend-item text tspan,
g.highcharts-axis-labels text tspan {
    color: #ccc !important;
    fill: #ccc !important;
}
#more_news_button {
    background: #333 !important;
    color: #ccc !important;
}
.close {
    filter: invert(1);
}

/*
 * Default fixes 
 *
 * These apply default styles that I had to overwrite above for other reasons.
 */

.page-header .page-header-top .top-menu .navbar-nav>li.dropdown-dark .dropdown-menu .dropdown-menu-list>li a:hover, .page-header .page-header-top .top-menu .navbar-nav>li.dropdown-dark .dropdown-menu.dropdown-menu-default>li a:hover {
    background: #373e47 !important;
}
}
