css:
@-moz-document domain("forum.hkgolden.com") {
body {
    background: #151515;
    color: #151515;
}
div.PageMiddleBox {
    background: #151515;
}
p,
td,
th,
ul,
li,
div {
    color: #E1E1DE;
    background-color: #151515!important;
}
div#ctl00_ContentPlaceHolder1_view_form > div:nth-child(5) > div > div > div a,
.link,
a.hitsearch_link,
div.ContentGrid > a {
    color: #3498db!important;
}
a:link,
a:visited,
a:hover,
a:active {
    text-decoration: none;
}
table {
    border-color: #000000!important;
    border-width: 1px;
    border-radius: 6px;
}
blockquote>div {
    color: #aca9a9!important;
}
.btn {
    background-color: #151515!important;
}
div.ContentGrid {
    color: #ccc6c0;
}
div.Topic_FunctionPanel {
    margin-top: 0px;
}
input,
textarea {
    background-color: #151515;
    border: 1px solid #000000;
    color: white;
}
select {
    color: #E1E1DE;
    overflow-y: hidden;
    background-color: #151515;
    border: 1px solid #000000;
    cursor: pointer!important;
}
img#ctl00_TopBarHomeImage {
    padding: 150px 200px 0px 0px;
    width: 0px;
    height: 0px;
    background: /*[[logo]]*/;
}
form#aspnetForm table:nth-child(13) > tbody > tr:nth-child(2),
form#aspnetForm table:nth-child(12) > tbody > tr:nth-child(2),
div.DivResizableBoxContainer,
div.container,
div.DivBoxContainer,
div.forum_banner,
div.title img {
    display: none!important;
}
span.HitSearchText,
table#mainTopicTable tr:nth-child(2) th {
    color: #E1E1DE;
}
div.hslice div table {
    border-collapse: collapse;
}
div.hslice div table tr,
div.hslice div table td,
td.repliers_header,
table.repliers > tbody > tr > td:nth-child(2),
td.repliers_left,
.btn {
    border: 1px solid #000000!important;
    border-radius: 5px;
}
div.hslice div table tr:nth-child(2) th,
select.topicSorting,
select.topicSorting option {
    color: #fbc308!important;
    background-color: #151515!important;
}
table#mainTopicTable td {
    background-color: #1D1D1D!important;
}
table#mainTopicTable tr:nth-child(1) {
    display: none;
}
table#mainTopicTable tr td:nth-child(3)>a,
table#mainTopicTable tr td:nth-child(2)>a,
div.tab_icon {
    color: #E1E1DE!important;
}
div#mainTopicTable div:nth-child(2),
div#mainTopicTable div:nth-child(1) {
    background-color: #151515!important;
}
div#DivMarkThread {
    background-color: #151515;
}
div#ctl00_ContentPlaceHolder1_view_form div:nth-child(3) a,
div#ctl00_ContentPlaceHolder1_view_form > table > tbody > tr > td > div:nth-child(2) a,
table#repliers tr:nth-child(1),
div.ContentPanel > table > tbody > tr > td div:nth-child(2) a,
div.tab_icon_selected,
div.tab_icon:hover,
.btn {
    color: #fbc308!important;
}
td.repliers_header,
td.repliers_header div {
    color: #fbc308;
}
/*紅人榜*/
td.redhottitle > img{
    display: none;
}
td.redhottitle{
    background-color: #151515!important;
    color: red;
}
table#ctl00_ContentPlaceHolder1_HotPeoples,
table#ctl00_ContentPlaceHolder1_HotPeoples > tbody > tr:nth-child(3) > td > table > tbody > tr > td > table > tbody > tr > td{
    border-collapse: collapse !important;
    border: 1px solid #000000 !important;
}
/*Profile page stuff*/

td.main_table1,
td.title {
    border: 1px solid #000000;
}
td.title img {
    display: none;
}
td.title,
span#ctl00_ContentPlaceHolder1_dataLabel > table > tbody > tr:nth-child(1) > td {
    color: #fbc308!important;
}
div#ctl00_ContentPlaceHolder1_ProfileForm a {
    color: #E1E1DE;
}
span.ppClass,
.pp {
    background-color: transparent !important;
    border: 1px solid #000000;
    border-radius: 2px;
    margin-top: 5px;
}
td.main_table1 a {
    color: #fbc308!important;
}
span#ctl00_ContentPlaceHolder1_lb_reli,
span#ctl00_ContentPlaceHolder1_lb_website,
button#deleteBtn {
    background-color: #151515!important;
    border: 1px solid #000000;
}
span.mp,
span.bgClass {
    background-color: transparent;
    border: 1px solid #000000;
    margin-bottom: 2px;
    border-radius: 2px;
}
span.mp:hover,
span.mp:hover a {
    color: #fbc308!important;
    background-color: transparent!important;
}
span.bgClass a,
input#ctl00_ContentPlaceHolder1_previousBtn,
input#ctl00_ContentPlaceHolder1_nextBtn,
input#ctl00_ContentPlaceHolder1_pageGoBtn,
button#deleteBtn {
    color: #fbc308!important;
}
}
