css:
@-moz-document domain("nimingban.com"), domain("tnmb.org"), domain("adnmb1.com") {
/*仅支持有自定义CSS功能的浏览器了，比如chrome的stylish*/ 
/*全选本页内容，粘贴到自定义css的扩展中为tnmb.org和adnmb1.com保存即可*/ 
/*本CSS并未经过多浏览器测试，如果有BUG我不管啦！*/ 
/*仅支持分辨率大于1024的窗口*/ 
/*这前端框架太TM操蛋了，我尽力了！*/ 
/*最后更新：2017年12月31日*/ 
/*http://h.nimingban.com/t/7361937*/ 

.uk-link:hover,a:hover{text-decoration:none}
::-webkit-scrollbar{width:8px;height:8px;background-color:#e7f2fe}
::-webkit-scrollbar-thumb{background-color:#3091f2}
#h-menu-content,#h-menu-search,#h-menu-top{width:99pt}
#h-menu{border-right:none;box-shadow:-8px 0 0 #bfdaf4 inset}
#h-post-form .h-post-form-input,#h-post-form .h-post-form-option,#h-post-form .h-post-form-title{padding:0 5px}
.h-post-form-input.uk-width-3-5{padding:0!important}
form{margin-right:auto;margin-left:auto}
#h-menu-search-keyword{border:1px solid #bfdaf4;background:#e7f2fe!important;color:#3091f2}
input,select,textarea{min-height:28px;outline:0;border:none;border-right:1px solid #bfdaf4;border-left:1px solid #bfdaf4;background-color:#fff;color:#3091f2}
#h-menu-search-keyword:focus{border:1px solid #3091f2;background:#fff!important}
.h-post-form-option.uk-width-1-5{float:right;width:215px}
.h-post-form-option.uk-width-1-5:before{color:#f30000;content:"米米尔隆的红色按钮→"}
.h-post-form-input.uk-width-4-5{margin-bottom:-5px!important;padding:0!important}
.uk-width-1-5{padding-left:0!important;min-height:30px!important}
.h-post-form-title{height:30px!important;background:#e7f2fe!important;color:#3091f2;text-align:center!important;line-height:30px}
.uk-grid.uk-grid-small{margin-left:0}
.uk-grid.uk-grid-small.h-post-form-grid{margin-bottom:5px;min-height:28px;border:1px solid #3091f2;background:#e7f2fe!important;background-color:#fff;color:#3091f2}
#h-post-form .h-post-form-textarea-title{height:34px;line-height:34px}
#h-emot-select,.uk-width-1-5{width:75pt;height:30px}
#h-post-form{overflow:hidden;width:100%;border-bottom:1px solid #d2a101}
.h-forum-header{margin:10px 0 -3000px;padding:10px 10px 3000px;border:solid 1px #d2a101;background-color:#fde98e;color:#674424;font-size:9pt}
.h-forum-header img{padding-bottom:10px}
.h-threads-item.uk-clearfix{margin:10px 0;padding:9px 10px;border:1px solid #bfdaf4;background:#e7f2fe;color:#2a4c6d}
hr{border-top:none}
.h-threads-img{margin:0 9pt!important;padding:2px!important;border:solid 1px #d2a101;background:#fff!important;background-color:#fde98e;box-shadow:0 1px 5px #aaa}
.h-threads-item-replys img.h-threads-img{margin:0 10px 0 0!important}
.h-threads-item-reply-main{color:#2e526a}
.h-threads-item-reply-main,a.h-threads-info-id{padding:5px;border:1px solid #3091f2;background:#fff!important}
a.h-threads-info-id{float:right;margin:-6px;color:#3091f2;text-align:center}
.h-threads-item-main .h-threads-info a.h-threads-info-id:hover{background:#3091f2!important;color:#fff}
a.h-threads-info-id:hover{background:#e7f2fe!important}
.h-threads-info-reply-btn{float:right;margin:-10px 10px;padding:5px;border:1px solid #bfdaf4;background:#fff!important;color:#3091f2;text-align:center}
#h-ref-view .h-threads-item-ref{border:2px solid hsla(0,0%,98%,.02);background:rgba(48,145,242,.15)}
#h-ref-view .h-threads-content{padding-top:20px}
.uk-text-primary.uk-text-small{padding:9px 5px 7px 5px;border:1px solid #D2A101;background-color:#FDE98E;color:#954424!important}
#h-menu #h-menu-content .h-active{background:#3091f2}
.uk-nav ul{padding:0}
.uk-nav-sub{margin-left:0!important;padding:0}
.uk-nav-sub li{padding-left:15px}
.uk-nav-sub li.h-active{margin-left:0!important;padding-left:15px!important}
.uk-nav-sub li:hover{background:#e7f2fe}
.uk-nav-sub li.h-active:hover{background:#3091f2!important}
#h-bottom-nav{border-top:1px #d2a101 solid;background-color:#fde98e;color:#674424!important}
#h-ref-view{max-width:1090px}
.h-threads-item .h-threads-img-box{float:left}
.h-threads-item-reply-main .h-threads-info,.h-threads-item-replys .h-threads-info{float:left;padding-bottom:10px;width:100%}
.h-threads-item-main .h-threads-content{margin:9pt 20px 15px!important}
.h-threads-item-replys .h-threads-content{margin:2pc 20px 15px!important}
.h-admin-tool{float:right;margin:-6px -6px -6px 20px;padding:3px 2px 2px;width:3pc;border:1px solid #3091f2;background:#fff!important;color:#3091f2;text-align:center}
.h-admin-tool input{min-height:1pc}
label.h-admin-tool{margin:-1px -6px 0;width:66px;height:25px}
input[type=submit]{float:right;margin:-1px -6px 0!important;width:72px!important;height:25px;min-height:2pc;border:1px solid #3091f2;background:#fff!important;color:#3091f2}
.h-threads-item-reply{word-wrap:break-word;word-break:break-all}
.h-threads-item-main .h-threads-info .h-admin-tool,.h-threads-item-main .h-threads-info a.h-threads-info-id{margin:-10px -11px;border:1px solid #bfdaf4}
.h-threads-info-createdat,.h-threads-item .h-threads-tips{color:#75b8eb}
#h-content h2.h-title,.h-threads-item-main .h-threads-info span.h-threads-info-createdat{color:#3091f2;font-family:"Microsoft JhengHei"}
.h-acfun-preview-container{margin:0 9pt!important;padding:2px!important;border:solid 1px #3091f2!important;background:#fff!important;color:#2e526a}
.uk-dropdown.uk-dropdown-small{padding:0 5px 0 5px;border:1px solid #D2A101;border-radius:0;background-color:#FDE98E!important;color:#954424!important}
.uk-nav-dropdown .uk-nav-header,.uk-nav-dropdown>li>a{color:#954424}
.uk-nav-dropdown>li>a:focus,.uk-nav-dropdown>li>a:hover{background:#d2a102}
.uk-nav-dropdown .uk-nav-divider{border-top:1px solid #d2a102}
.uk-nav-divider{margin:0}
.h-tool-btn{border:1px solid #D2A101;background:#FDE98E!important;color:#954424!important}
.h-threads-info-reply-btn{color:#fff;font-size:14px!important}
.h-threads-content font{padding:2px 5px;border:1px solid #3090f2;background-color:#e7f2fe;color:#3090f2;border-left:4px solid #3090f2;cursor:help}
/*↓↓↓工具栏移到左边，照顾分辨率大的用户，可以删掉这行*/
#h-tool{left:150px;width:42px}
/*↓↓↓这条是管理员才需要的，反正显示出来也不会有什么影响(`ε´ )*/
.h-admin-tool{display:block}
}
