css:
@-moz-document domain("tv.uqam.ca") {
@media only screen and (min-width: 1200px) {
    #uqam_contenu {
        background: #151515;
        color: #fff;
    }
    #uqam_entete_recherche #uqam_recherche-champ {
        background-color: #151515;
        color: #fff;
    }
}
@media only screen and (min-width: 120px),
only screen and (min-width: 320px),
only screen and (min-width: 480px),
only screen and (min-width: 768px) {
    #uqam_contenu {
        background: #151515;
        color: #fff;
    }
    #uqam_mobile_navigation-menu {
        border-bottom: 3px solid #000;
        border-left: 3px solid #000;
    }
    #uqam_mobile_navigation-fermer {
        border-bottom: 3px #000 solid;
    }
    #details {
        background: #090909;
    }
}
.uqam h1,
.uqam h2 {
    color: #fff;
}
input[type=text]:active,
input[type=text]:focus,
input[type=text]:hover {
    color: #fff;
}
#uqam_conteneur_lien-rapides {
    border: 3px solid #000;
}
#uqam_conteneur_lien-rapides:before {
    border-color: #000 transparent;
}
#uqam_conteneur_recherche-choix {
    background: #202020;
    background: linear-gradient(#202020, #202020);
    border: 3px solid #000;
}
#contenu-haut {
    background: #151515;
    border-bottom: 11px solid #151515;
}
#uqam_entete {
    border-bottom: 3px solid #101010;
}
#uqam_pied-page {
    border-top: 3px solid #101010;
}
#uqam_retour-haut-page {
    background-color: #101010;
}
#menu-principal li {
    border-right: 1px solid #fff;
    border-color: rgba(0, 0, 0, 0.07);
}
#menu-principal li ul {
    border: 1px solid #000;
    border-color: rgb(0, 0, 0);
}
.field-collection-container {
    border-bottom: 1px solid #000000;
}
input {
    border: 1px solid rgb(0, 0, 0);
    background-color: #070707;
    color: white;
}
.view-display-id-liste .view-filters,
.view-display-id-tuiles .view-filters {
    border-top: 1px solid #ffffff;
    border-bottom: 1px solid #ffffff;
}
#uqam_mobile_navigation-fermer img {
    content: url("http://tv.uqam.ca/sites/all/themes/uqam2015/css/image/icon/icon_fleche-vertical_couleur-noir.svg");
}
/* jw player black */

.jw-skin-uqamtv .jw-controlbar {
    background: rgba(0, 0, 0, 0.8);
}
.jw-skin-uqamtv .jw-time-tip,
.jw-skin-uqamtv .jw-volume-tip,
.jw-skin-uqamtv .jw-menu {
    background: rgba(0, 0, 0, 0.73);
}
}
